Output:
/**
 * Unified Command Center - Canonical Implementation
 *
 * Consolidates 3 previous versions into a single, flexible component that supports:
 * - Lite mode (simple content display) - DEFAULT
 * - Full mode (draggable dashboard tiles with react-rnd)
 * - 56px header height (MythTech standard)
 * - Performance optimizations (memoized subcomponents)
 * - Customizable theming per agent persona
 *
 * @version 2.0.0 - Sprint 2 Consolidation
 */

import React, { useState, useEffect, useMemo, useCallback, memo, ReactNode } from 'react';
import { Rnd } from 'react-rnd';
import { Lock, Unlock, LogOut, Clock, Globe, X } from '@/components/ui/HearthlinkIcons';
import { RadialNavigationModal } from '../mythtech/navigation/RadialNavigationModal';
import { GlobalCommsStream } from '../mythtech/tasks/GlobalCommsStream';
import { PremiumFrame } from './premium/PremiumPanel';
import { useADHDUI } from '../ui/ADHDOptimizedInterface';
import { resolveAsset } from '@/utils/assets';

// ============================================================================
// Types & Interfaces
// ============================================================================

export interface AgentIdentity {
  name: string;
  displayName: string;
  subtitle: string;
  iconPath: string;
  primaryColor: string;    // e.g., "#d6b369" for Alden
  secondaryColor: string;  // e.g., "#00e6ff" for Alden
  accentColor?: string;    // optional third color
}

export interface ThemeConfig {
  overlay?: {
    pattern: 'grid' | 'hex' | 'circuit' | 'diagonal' | 'none';
    opacity: number;
  };
  glassmorphism?: boolean;
  borderGlow?: boolean;
}

export interface DashboardTile {
  id: string;
  x: number;
  y: number;
  width: number;
  height: number;
  locked?: boolean;
  content: ReactNode;
}

export type ViewMode = 'overview' | 'tasks' | 'calendar' | 'email' | 'chat' | string;

export interface TabItem {
  id: string;
  label: string;
}

export interface UnifiedCommandCenterProps {
  // Agent Identity (new API)
  identity?: AgentIdentity;

  // Legacy API (deprecated but supported for backward compatibility)
  title?: string;
  subtitle?: string;
  iconPath?: string;
  titleColor?: string;
  subtitleColor?: string;
  dateColor?: string;
  timeColor?: string;
  accentColor?: string;
  themeColor?: string;

  // User context
  userId?: string;

  // Mode selection
  mode?: 'lite' | 'full';  // 'lite' = simple content, 'full' = dashboard with tiles

  // Dashboard configuration (for 'full' mode)
  dashboardLayout?: DashboardTile[];
  onDashboardLayoutChange?: (layout: DashboardTile[]) => void;

  // Tabs (for 'lite' mode)
  tabs?: TabItem[];
  activeTab?: string;
  onTabChange?: (tabId: string) => void;

  // Views (for 'full' mode)
  activeView?: ViewMode;
  onViewChange?: (view: ViewMode) => void;
  availableViews?: Array<{ id: ViewMode; label: string }>;

  // View content renderers (for 'full' mode)
  renderOverview?: () => ReactNode;
  renderTasksView?: () => ReactNode;
  renderCalendarView?: () => ReactNode;
  renderEmailView?: () => ReactNode;
  renderChatView?: () => ReactNode;
  renderCustomView?: (viewId: string) => ReactNode;

  // Content (for 'lite' mode)
  children: ReactNode;

  // Sidebar/Chat
  sidebar?: ReactNode;
  sidebarTitle?: string;
  sidebarCollapsed?: boolean;
  expandedChatContent?: ReactNode;

  // Navigation
  onBackToLauncher?: () => void;
  onNavigateToModule?: (moduleName: string) => void;
  onNavigate?: (moduleName: string) => boolean | void;

  // Theme
  theme?: ThemeConfig;
  withFrame?: boolean;

  // Connection status
  isConnected?: boolean;
  statusLabel?: string;
  statusTone?: 'success' | 'warning' | 'error';

  // Header customization
  headerStyle?: React.CSSProperties;
  headerClassName?: string;
  headerActions?: ReactNode;
  onFocusAssist?: () => void;
  isFocusAssistActive?: boolean;

  // Content layout
  fullWidthContent?: boolean;
}

// ============================================================================
// Constants
// ============================================================================

const HEADER_HEIGHT = 56; // MythTech standard
const FRAME_PADDING = 16;

// Stable empty arrays to prevent re-renders
const EMPTY_DASHBOARD_LAYOUT: DashboardTile[] = [];
const EMPTY_TABS: TabItem[] = [];
const EMPTY_VIEWS: Array<{ id: ViewMode; label: string }> = [];

// ============================================================================
// Helper Components
// ============================================================================

interface NavButtonProps {
  label: string;
  active: boolean;
  onClick: () => void;
  primaryColor: string;
  secondaryColor: string;
}

const NavButton: React.FC<NavButtonProps> = memo(({ label, active, onClick, primaryColor, secondaryColor }) => {
  const activeStyle: React.CSSProperties = useMemo(() => ({
    border: `1px solid ${secondaryColor}`,
    boxShadow: `0 0 16px ${secondaryColor}40, inset 0 0 0 1px ${primaryColor}66`,
    background: `linear-gradient(135deg, ${secondaryColor}22, rgba(0,0,0,0.4))`,
    color: secondaryColor
  }), [primaryColor, secondaryColor]);

  const inactiveStyle: React.CSSProperties = useMemo(() => ({
    border: '1px solid rgba(255, 255, 255, 0.08)',
    boxShadow: '0 0 12px rgba(0, 0, 0, 0.45)',
    background: 'linear-gradient(135deg, rgba(255,255,255,0.03), rgba(0,0,0,0.5))',
    color: '#8fa7c7'
  }), []);

  return (
    <button
      onClick={onClick}
      data-testid={`nav-button-${label.toLowerCase()}`}
      className={`px-4 py-1.5 text-[10px] uppercase tracking-[0.2em] rounded transition-all font-semibold font-orbitron-force ${
        active ? '' : 'text-[#8fa7c7]'
      }`}
      style={active ? activeStyle : inactiveStyle}
    >
      {label}
    </button>
  );
});

NavButton.displayName = 'NavButton';

// ============================================================================
// Memoized Header Component
// ============================================================================

interface CommandCenterHeaderProps {
  identity: AgentIdentity;
  tabs: TabItem[];
  activeTab?: string;
  onTabChange?: (tabId: string) => void;
  availableViews?: Array<{ id: ViewMode; label: string }>;
  activeView?: ViewMode;
  onViewChange?: (view: ViewMode) => void;
  statusLabel: string;
  statusTone: 'success' | 'warning' | 'error';
  isConnected: boolean;
  onBackToLauncher?: () => void;
  onRadialOpen: () => void;
  headerStyle?: React.CSSProperties;
  headerClassName?: string;
  headerActions?: React.ReactNode;
  onFocusAssist?: () => void;
  isFocusAssistActive?: boolean;
}

const CommandCenterHeader = memo<CommandCenterHeaderProps>(({
  identity,
  tabs,
  activeTab,
  onTabChange,
  availableViews,
  activeView,
  onViewChange,
  statusLabel,
  statusTone,
  isConnected,
  onBackToLauncher,
  onRadialOpen,
  headerStyle,
  headerClassName,
  headerActions,
  onFocusAssist,
  isFocusAssistActive
}) => {
  const [now, setNow] = useState<Date>(new Date());
  const [tzOffset, setTzOffset] = useState<number>(0);

  useEffect(() => {
    const timer = setInterval(() => setNow(new Date()), 60000);
    return () => clearInterval(timer);
  }, []);

  /* Timezone Logic */
  const [showTzControl, setShowTzControl] = useState(false);
  const [localTzOffset, setLocalTzOffset] = useState<number>(0); // System offset

  useEffect(() => {
    const offset = -new Date().getTimezoneOffset() / 60;
    setLocalTzOffset(offset);
    setTzOffset(offset);
  }, []);

  const handleTzChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setTzOffset(parseFloat(e.target.value));
  };
    
  const resetTz = () => {
    setTzOffset(localTzOffset);
    setShowTzControl(false);
  };

  const defaultHeaderStyle = useMemo(() => ({
    background: 'linear-gradient(90deg, #06101f 0%, #0a1a30 50%, #06101f 100%)',
    borderBottom: `2px solid ${identity.primaryColor}`,
    boxShadow: `0 4px 12px rgba(0,0,0,0.5), inset 0 -1px 0 ${identity.secondaryColor}80, inset 0 1px 0 ${identity.primaryColor}66`
  }), [identity.primaryColor, identity.secondaryColor]);

  const statusColors = useMemo(() => {
    switch (statusTone) {
      case 'warning':
        return { dot: '#f59e0b', text: '#fbbf24' };
      case 'error':
        return { dot: '#ef4444', text: '#f87171' };
      default:
        return { dot: '#22c55e', text: '#a7f3d0' };
    }
  }, [statusTone]);

  // Determine which navigation to show (tabs vs views)
  const showViews = availableViews && availableViews.length > 0;
  const showTabs = !showViews && tabs && tabs.length > 0;

  return (
    <header
      className={`shrink-0 z-10 relative ${headerClassName || ''}`}
      style={{
        height: `${HEADER_HEIGHT}px`,
        ...defaultHeaderStyle,
        ...headerStyle
      }}
    >
      <div className={`absolute inset-x-6 bottom-1 h-0.5 bg-gradient-to-r from-transparent via-opacity-70 to-transparent rounded-full blur-[1px]`}
           style={{ backgroundColor: identity.secondaryColor }} />

      <div className="w-full max-w-[1600px] mx-auto h-full flex items-center justify-between px-4">
        <div className="flex items-center gap-3 md:gap-6 min-w-0 flex-shrink-0 flex-1">
          {/* Agent Icon */}
          <button
            type="button"
            onClick={onRadialOpen}
            className="group relative flex items-center justify-center w-10 h-10 rounded-full transition-all duration-300 z-[5000] cursor-pointer pointer-events-auto"
            style={{ backgroundColor: `${identity.primaryColor}1a` }}
            title="Open Navigation Hub"
            aria-label={`Open ${identity.displayName} Navigation`}
          >
            <img
              src={identity.iconPath}
              alt={identity.displayName}
              className="object-contain group-hover:scale-110 transition-transform pointer-events-none"
              style={{
                width: '48px',
                height: '48px',
                filter: `drop-shadow(0 0 8px ${identity.primaryColor}bf)`
              }}
            />
            <div className="absolute inset-0 rounded-full blur-md opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none"
                 style={{ backgroundColor: `${identity.primaryColor}33` }} />
          </button>

          {/* Title */}
          <div className="flex flex-col leading-none max-w-[320px]">
            <h1 className="text-2xl font-semibold uppercase font-orbitron-force leading-none tracking-[0.8em]"
                style={{ color: identity.primaryColor }}>
              {identity.displayName}
            </h1>
            <span className="text-sm uppercase tracking-[0.15em] font-semibold leading-none mt-0.5"
                  style={{
                    color: identity.secondaryColor,
                    textShadow: `0 0 4px ${identity.secondaryColor}99`
                  }}>
              {identity.subtitle}
            </span>
          </div>

          {/* Divider */}
          {(showViews || showTabs) && (
            <div className="h-6 w-[1px] bg-gradient-to-b from-transparent via-white/10 to-transparent hidden md:block" />
          )}

          {/* Navigation (Views or Tabs) */}
          {showViews && (
            <div className="flex space-x-2 md:space-x-3 overflow-x-auto ml-2">
              {availableViews!.map((view) => (
                <NavButton
                  key={view.id}
                  label={view.label}
                  active={activeView === view.id}
                  onClick={() => onViewChange?.(view.id)}
                  primaryColor={identity.primaryColor}
                  secondaryColor={identity.secondaryColor}
                />
              ))}
            </div>
          )}
          {showTabs && (
            <div className="flex space-x-2 md:space-x-3 overflow-x-auto ml-2">
              {tabs.map((tab) => (
                <NavButton
                  key={tab.id}
                  label={tab.label}
                  active={activeTab === tab.id}
                  onClick={() => onTabChange?.(tab.id)}
                  primaryColor={identity.primaryColor}
                  secondaryColor={identity.secondaryColor}
                />
              ))}
            </div>
          )}
        </div>

        {/* Right side: Clock, Status, Actions, Exit */}
        <div className="flex items-center gap-2 md:gap-4 flex-shrink-0">
          {/* Clock */}
          {/* Clock & Timezone */}
          <div className="relative group/clock">
            <button 
              onClick={() => setShowTzControl(!showTzControl)}
              className="flex flex-col text-right min-w-[140px] hover:bg-white/5 p-1 rounded transition-colors cursor-pointer"
              title="Click to adjust Timezone"
            >
              <span className="text-xs text-[#9fb4d6] uppercase tracking-[0.15em] font-semibold flex items-center justify-end gap-2">
                {tzOffset !== localTzOffset && <Globe size={10} className="text-alden-gold animate-pulse" />}
                {showTzControl ? 'Adjust Timezone' : (
                  <>
                    {new Date(now.getTime() + tzOffset * 3600000).toLocaleDateString('en-US', {
                      month: 'short',
                      day: 'numeric',
                      timeZone: 'UTC'
                    })}
                    <span className="text-[10px] ml-1 opacity-70">
                      {tzOffset === 0 ? 'UTC' : `UTC${tzOffset > 0 ? '+' : ''}${tzOffset}`}
                    </span>
                  </>
                )}
              </span>
              <span className="text-sm text-[#e5f7ff] font-bold tracking-[0.1em] font-orbitron-force">
                {new Date(now.getTime() + tzOffset * 3600000).toLocaleTimeString('en-US', {
                  hour: '2-digit',
                  minute: '2-digit',
                  hour12: true,
                  timeZone: 'UTC'
                })}
              </span>
            </button>
            
            {/* Timezone Slider Popover */}
            {(showTzControl || tzOffset !== localTzOffset) && (
               <div className={`absolute top-full right-0 mt-2 bg-[#0b1221] border border-[#2d3748] p-4 rounded-lg shadow-xl z-50 min-w-[240px] ${!showTzControl ? 'hidden group-hover/clock:block' : ''}`}>
                 <div className="flex items-center justify-between mb-4">
                   <span className="text-[10px] text-[#9fb4d6] uppercase tracking-wider">Time Travel</span>
                   <button onClick={resetTz} className="text-[10px] text-alden-gold hover:underline font-bold">RESET LOCAL</button>
                 </div>
                 
                 {/* Quick Presets */}
                 <div className="grid grid-cols-4 gap-2 mb-4">
                    {[-5, -6, -7, -8].map(offset => (
                      <button 
                        key={offset}
                        onClick={() => { setTzOffset(offset); }}
                        className={`text-xs p-1 rounded border ${tzOffset === offset ? 'bg-alden-cyan/20 border-alden-cyan text-alden-cyan' : 'border-white/10 hover:bg-white/5 text-slate-400'}`}
                      >
                        {offset === -5 ? 'EST' : offset === -6 ? 'CST' : offset === -7 ? 'MST' : 'PST'}
                      </button>
                    ))}
                 </div>

                 <input 
                   type="range" 
                   min="-12" 
                   max="14" 
                   step="1" 
                   value={tzOffset} 
                   onChange={handleTzChange}
                   className="w-full h-1 bg-[#2d3748] rounded-lg appearance-none cursor-pointer accent-alden-cyan mb-2"
                 />
                 <div className="flex justify-between text-[10px] text-slate-500 font-mono">
                    <span>-12</span>
                    <span>UTC</span>
                    <span>+14</span>
                 </div>
               </div>
            )}
          </div>

          {/* Connection status */}
          <div 
            className="flex items-center gap-2 px-3 py-1.5 bg-black/20 rounded-lg border border-white/10 backdrop-blur-md cursor-help"
            title={`System Status: ${statusLabel} | Tone: ${statusTone}`}
          >
            <div
              className={`w-1.5 h-1.5 rounded-full shadow-[0_0_8px_currentColor] animate-pulse`}
              style={{ backgroundColor: statusColors.dot }}
            />
            <span className="text-[10px] text-[#9fb4d6] uppercase tracking-wider font-bold hidden md:inline">
              {statusLabel}
            </span>
          </div>

          {/* Additional header actions */}
          {headerActions}

        {/* Exit button */}
          {onBackToLauncher && (
            <button
              onClick={onBackToLauncher}
              className="p-2 text-[#9fb4d6] hover:text-red-400 hover:bg-red-500/10 rounded-lg transition-all border border-transparent hover:border-red-500/30"
              title="Exit Command Center"
            >
              <LogOut size={18} />
            </button>
          )}
        </div>
      </div>
    </header>
  );
});

CommandCenterHeader.displayName = 'CommandCenterHeader';

// ============================================================================
// Memoized Sidebar Component
// ============================================================================

interface CommandCenterSidebarProps {
  sidebar?: ReactNode;
  collapsed?: boolean;
  sidebarTitle?: string;
  identity: AgentIdentity;
}

const CommandCenterSidebar = memo<CommandCenterSidebarProps>(({
  sidebar,
  collapsed,
  sidebarTitle = 'AI Intelligence',
  identity
}) => {
  const defaultSidebar = useMemo(() => (
    <>
      <div className="h-10 border-b flex items-center justify-between px-4 bg-black/20 shrink-0"
           style={{ borderColor: `${identity.primaryColor}33` }}>
        <div className="flex items-center gap-2">
          <div className="w-1.5 h-1.5 rounded-full animate-pulse"
               style={{ backgroundColor: identity.primaryColor }} />
          <span className="text-xs text-[#9fb4d6] uppercase tracking-widest font-bold font-orbitron-force">
            {sidebarTitle}
          </span>
        </div>
      </div>
      <div className="flex-1 overflow-hidden relative text-[#c5d2e8]">
        <div className="absolute inset-0 bg-gradient-to-br from-black/40 via-transparent to-black/20 pointer-events-none" />
        <GlobalCommsStream />
      </div>
    </>
  ), [sidebarTitle, identity.primaryColor]);

  const showSidebar = sidebar !== null;
  const sidebarContent = sidebar !== undefined ? sidebar : defaultSidebar;

  if (!showSidebar) return null;

  const sidebarStyle: React.CSSProperties = useMemo(() => ({
    border: `1px solid ${identity.secondaryColor}55`,
    boxShadow: `0 0 20px ${identity.secondaryColor}18, inset 0 0 12px ${identity.primaryColor}18`,
    background: `linear-gradient(135deg, rgba(11,17,36,0.92), ${identity.secondaryColor}06)`,
  }), [identity.primaryColor, identity.secondaryColor]);

  return (
    <aside
      className={`relative flex-shrink-0 border-l transition-all duration-300 ${
        collapsed ? 'w-16' : 'w-[360px]'
      }`}
      style={sidebarStyle}
    >
      {sidebarContent}
    </aside>
  );
});

CommandCenterSidebar.displayName = 'CommandCenterSidebar';

// ============================================================================
// Main Component
// ============================================================================

export const UnifiedCommandCenter: React.FC<UnifiedCommandCenterProps> = (props) => {
  const {
    identity: identityProp,
    title: legacyTitle,
    subtitle: legacySubtitle,
    iconPath: legacyIconPath,
    titleColor: legacyTitleColor,
    subtitleColor: legacySubtitleColor,
    accentColor: legacyAccentColor = '#00e6ff',
    themeColor: legacyThemeColor = '#c5d2e8',
    userId,
    mode = 'lite',
    dashboardLayout: initialDashboardLayout = EMPTY_DASHBOARD_LAYOUT,
    onDashboardLayoutChange,
    tabs = EMPTY_TABS,
    activeTab,
    onTabChange,
    activeView = 'overview',
    onViewChange,
    availableViews = EMPTY_VIEWS,
    renderOverview,
    renderTasksView,
    renderCalendarView,
    renderEmailView,
    renderChatView,
    renderCustomView,
    children,
    sidebar,
    sidebarTitle,
    sidebarCollapsed = false,
    expandedChatContent,
    onBackToLauncher,
    onNavigateToModule,
    onNavigate,
    theme: themeProp = {},
    withFrame = false,
    isConnected = true,
    statusLabel = 'System Online',
    statusTone = 'success',
    headerStyle,
    headerClassName,
    headerActions,
    fullWidthContent = false,
    onFocusAssist,
    isFocusAssistActive,
  } = props;

  // Build identity from legacy props if not provided directly
  const identity: AgentIdentity = identityProp || {
    name: legacyTitle?.toLowerCase().replace(/\s+/g, '-') || 'agent',
    displayName: legacyTitle || 'Agent',
    subtitle: legacySubtitle || 'Intelligence',
    iconPath: legacyIconPath || resolveAsset('assets/Hearthlink.png'), // Fallback to Hearthlink logo
    primaryColor: legacyTitleColor || '#d6b369',
    secondaryColor: legacySubtitleColor || legacyAccentColor || '#00e6ff',
    accentColor: legacyAccentColor
  };
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isRadialOpen, setIsRadialOpen] = useState(false);
  
  // Resolve theme
  const theme = useMemo(() => ({
    overlay: themeProp.overlay || { pattern: 'none', opacity: 0 },
    glassmorphism: themeProp.glassmorphism ?? false,
    borderGlow: themeProp.borderGlow ?? false,
  }), [themeProp]);

  // Use a ref to track previous layout for comparison
  const prevLayoutRef = React.useRef<string>(JSON.stringify(initialDashboardLayout));
  const [dashboardLayout, setDashboardLayout] = useState<DashboardTile[]>(initialDashboardLayout);

  // Sync external layout changes - only when actually different
  useEffect(() => {
    const currentLayoutStr = JSON.stringify(initialDashboardLayout);
    if (prevLayoutRef.current !== currentLayoutStr) {
      prevLayoutRef.current = currentLayoutStr;
      setDashboardLayout(initialDashboardLayout);
    }
  }, [initialDashboardLayout]);

  // Calculate dashboard canvas height dynamically
  const dashboardCanvasHeight = useMemo(() => {
    if (dashboardLayout.length === 0) return 720;
    const maxY = Math.max(...dashboardLayout.map(item => item.y + item.height));
    return Math.max(720, maxY + FRAME_PADDING * 2);
  }, [dashboardLayout]);

  // Handle tile updates
  const updateTile = useCallback((id: string, updates: Partial<DashboardTile>) => {
    const newLayout = dashboardLayout.map(item =>
      item.id === id ? { ...item, ...updates } : item
    );
    setDashboardLayout(newLayout);
    onDashboardLayoutChange?.(newLayout);
  }, [dashboardLayout, onDashboardLayoutChange]);

  const toggleTileLock = useCallback((id: string) => {
    const tile = dashboardLayout.find(t => t.id === id);
    if (tile) {
      updateTile(id, { locked: !tile.locked });
    }
  }, [dashboardLayout, updateTile]);

  // Navigation handler
  const handleNavigate = useCallback((moduleName: string) => {
    setIsRadialOpen(false);

    if (onNavigate) {
      const handled = onNavigate(moduleName);
      if (handled) return;
    }

    if (onNavigateToModule) {
      onNavigateToModule(moduleName);
    } else if (onBackToLauncher) {
      onBackToLauncher();
    }
  }, [onNavigate, onNavigateToModule, onBackToLauncher]);

  // Background overlay pattern generator
  const getOverlayPattern = useCallback(() => {
    const { overlay } = theme;
    if (!overlay || overlay.pattern === 'none') return {};

    const { primaryColor, secondaryColor } = identity;
    const opacity = overlay.opacity || 0.03;
    const opacityHex = Math.floor(opacity * 255).toString(16).padStart(2, '0');

    switch (overlay.pattern) {
      case 'grid':
        return {
          backgroundImage: `repeating-linear-gradient(0deg, transparent, transparent 35px, ${primaryColor}${opacityHex} 35px, ${primaryColor}${opacityHex} 36px),
                            repeating-linear-gradient(90deg, transparent, transparent 35px, ${primaryColor}${opacityHex} 35px, ${primaryColor}${opacityHex} 36px)`,
        };
      case 'hex':
        return {
          backgroundImage: `radial-gradient(circle at center, transparent 1px, ${secondaryColor}${opacityHex} 1px, ${secondaryColor}${opacityHex} 2px, transparent 2px)`,
          backgroundSize: '30px 30px',
        };
      case 'circuit':
        return {
          backgroundImage: `linear-gradient(90deg, ${primaryColor}${opacityHex} 1px, transparent 1px),
                            linear-gradient(0deg, ${primaryColor}${opacityHex} 1px, transparent 1px)`,
          backgroundSize: '20px 20px',
        };
      default:
        return {};
    }
  }, [theme, identity]);

  // Frame style with agent colors
  const frameStyle = useMemo<React.CSSProperties>(() => ({
    border: `2px solid ${identity.primaryColor}bf`,
    boxShadow: `0 0 22px ${identity.secondaryColor}2e, inset 0 0 18px rgba(0,0,0,0.55)`,
    background: 'linear-gradient(135deg, rgba(4,10,20,0.92), rgba(6,14,28,0.82))'
  }), [identity.primaryColor, identity.secondaryColor]);

  // Render view content for 'full' mode
  const renderViewContent = useCallback(() => {
    if (activeView === 'chat' && expandedChatContent) {
      return expandedChatContent;
    }

    switch (activeView) {
      case 'overview':
        if (renderOverview) return renderOverview();
        // Default overview with dashboard
        return (
          <div className="space-y-3 mt-1">
            <div className="relative w-full p-1 rounded-xl" style={frameStyle}>
              <div
                className={`absolute -top-2 left-1/2 -translate-x-1/2 w-32 h-1.5 rounded-full`}
                style={{
                  backgroundColor: identity.secondaryColor,
                  boxShadow: `0 0 12px ${identity.secondaryColor}b3`
                }}
              />
              <div
                className={`relative w-full rounded-lg border`}
                style={{
                  height: dashboardCanvasHeight,
                  minHeight: dashboardCanvasHeight,
                  overflow: 'visible',
                  borderColor: `${identity.secondaryColor}33`
                }}
              >
                {dashboardLayout.map(item => {
                  const snapGrid: [number, number] = [10, 10];
                  return (
                    <Rnd
                      key={item.id}
                      bounds="parent"
                      size={{ width: item.width, height: item.height }}
                      position={{ x: item.x, y: item.y }}
                      disableDragging={item.locked}
                      enableResizing={!item.locked}
                      dragGrid={snapGrid}
                      resizeGrid={snapGrid}
                      onDragStop={(_, d) => {
                        updateTile(item.id, { x: d.x, y: d.y });
                      }}
                      onResizeStop={(_, __, ref, ___, position) => {
                        updateTile(item.id, {
                          width: ref.offsetWidth,
                          height: ref.offsetHeight,
                          x: position.x,
                          y: position.y
                        });
                      }}
                      className="transition-shadow"
                    >
                      <div className="relative w-full h-full p-4 box-border overflow-hidden">
                        <div className="absolute inset-0 flex items-end justify-end pr-2 pb-2 z-30 pointer-events-auto">
                          <button
                            onClick={() => toggleTileLock(item.id)}
                            className="p-1.5 rounded-md bg-black/70 border border-white/20 text-[#9fb4d6] hover:border-opacity-50 transition-colors"
                            style={{
                              color: item.locked ? identity.primaryColor : undefined,
                              borderColor: item.locked ? identity.primaryColor : undefined
                            }}
                            title={item.locked ? 'Unlock tile' : 'Lock tile'}
                          >
                            {item.locked ? <Lock size={14} /> : <Unlock size={14} />}
                          </button>
                        </div>
                        <div className="w-full h-full">
                          {item.content}
                        </div>
                      </div>
                    </Rnd>
                  );
                })}
              </div>
            </div>
          </div>
        );
      case 'tasks':
        return renderTasksView ? renderTasksView() : null;
      case 'calendar':
        return renderCalendarView ? renderCalendarView() : null;
      case 'email':
        return renderEmailView ? renderEmailView() : null;
      default:
        return renderCustomView ? renderCustomView(activeView) : null;
    }
  }, [
    activeView,
    expandedChatContent,
    renderOverview,
    renderTasksView,
    renderCalendarView,
    renderEmailView,
    renderCustomView,
    frameStyle,
    identity,
    dashboardCanvasHeight,
    dashboardLayout,
    updateTile,
    toggleTileLock
  ]);

  // Wrap content in frame if requested
  const content = useMemo(() => {
    if (mode === 'full') {
      return renderViewContent();
    }

    if (withFrame) {
      return (
        <PremiumFrame>
          <div
            className="absolute -top-2 left-1/2 -translate-x-1/2 w-32 h-1.5 rounded-full"
            style={{
              backgroundColor: identity.secondaryColor,
              boxShadow: `0 0 12px ${identity.secondaryColor}70`
            }}
          />
          <div className="relative w-full h-full min-h-[720px] p-4">
            {children}
          </div>
        </PremiumFrame>
      );
    }

    return children;
  }, [mode, withFrame, children, renderViewContent, identity.secondaryColor]);

  return (
    <div
      className="fixed inset-0 bg-[#020617] text-[#c5d2e8] flex flex-col overflow-hidden font-sans z-50"
    >
      {/* Background overlay */}
      {theme.overlay && theme.overlay.pattern !== 'none' && (
        <div
          className="absolute inset-0 opacity-[0.02] pointer-events-none"
          style={getOverlayPattern()}
        />
      )}

      {/* Glassmorphism overlay */}
      {theme.glassmorphism && (
        <div className="absolute inset-0 bg-gradient-to-br from-[#0b1628]/10 via-transparent to-[#0b1628]/10 pointer-events-none" />
      )}

      {/* Header */}
      <CommandCenterHeader
        identity={identity}
        tabs={tabs}
        activeTab={activeTab}
        onTabChange={onTabChange}
        availableViews={availableViews}
        activeView={activeView}
        onViewChange={onViewChange}
        statusLabel={statusLabel}
        statusTone={statusTone}
        isConnected={isConnected}
        onBackToLauncher={onBackToLauncher}
        onRadialOpen={() => setIsRadialOpen(true)}
        headerStyle={headerStyle}
        headerClassName={headerClassName}
        headerActions={headerActions}
      />

      {/* Main content area */}
      <div className="flex-1 flex overflow-hidden relative">
        <main
          className={`flex-1 flex flex-col transition-all duration-300 text-[#c5d2e8] min-w-0 ${
            activeView === 'chat' ? 'w-0 opacity-0 overflow-hidden' : ''
          }`}
        >
          <div
            className="flex-1 overflow-y-auto scrollbar-thin scrollbar-track-transparent"
            style={{ scrollbarColor: `${identity.primaryColor}66 transparent` }}
          >
            <div className={`w-full ${
              fullWidthContent
                ? 'h-full'
                : 'max-w-[1600px] mx-auto pt-6 pb-16 px-6'
            }`}>
              {content}
            </div>
          </div>
        </main>

        {/* Sidebar */}
        {(sidebar !== null || expandedChatContent) && (
          activeView === 'chat' && expandedChatContent ? (
            <aside
              className="absolute top-0 left-0 right-0 bottom-0 z-20 w-full bg-[#020617] flex flex-col"
              style={{
                border: `1px solid ${identity.secondaryColor}8c`,
                boxShadow: `0 0 28px ${identity.secondaryColor}2e, inset 0 0 18px ${identity.primaryColor}28`,
                background: `linear-gradient(135deg, rgba(11,17,36,0.92), ${identity.secondaryColor}0f)`,
              }}
            >
              {expandedChatContent}
            </aside>
          ) : (
            <CommandCenterSidebar
              sidebar={sidebar}
              collapsed={sidebarCollapsed}
              sidebarTitle={sidebarTitle || `${identity.displayName} Intelligence`}
              identity={identity}
            />
          )
        )}
      </div>

      <RadialNavigationModal
        isOpen={isRadialOpen}
        onClose={() => setIsRadialOpen(false)}
        currentModule={identity.displayName}
        onNavigate={handleNavigate}
      />
    </div>
  );
};

export default UnifiedCommandCenter;

// Re-export types for convenience
export type { TabItem as UnifiedTabItem };
export type UnifiedCommandCenterOptimized = typeof UnifiedCommandCenter;

