  statusTone: 'success' | 'warning' | 'error';
  isConnected: boolean;
  onBackToLauncher?: () => void;
  onRadialOpen: () => void;
  headerStyle?: React.CSSProperties;
  headerClassName?: string;
  headerActions?: React.ReactNode;
  onFocusAssist?: () => void;
  isFocusAssistActive?: boolean;
}

const CommandCenterHeader = memo<CommandCenterHeaderProps>((props) => {
  const {
    identity,
    tabs,
    activeTab,
    onTabChange,
    availableViews,
    activeView,
    onViewChange,
    statusLabel,
    statusTone,
    onBackToLauncher,
    onRadialOpen,
    headerStyle,
    headerClassName,
    headerActions
  } = props;

  const [now, setNow] = useState<Date>(new Date());
  const [tzOffset, setTzOffset] = useState<number>(0);
  const [showTzControl, setShowTzControl] = useState(false);
  const [localTzOffset, setLocalTzOffset] = useState<number>(0);

  useEffect(() => {
    const timer = setInterval(() => setNow(new Date()), 60000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    const offset = -new Date().getTimezoneOffset() / 60;
    setLocalTzOffset(offset);
    setTzOffset(offset);
  }, []);

  const handleTzChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setTzOffset(parseFloat(e.target.value));
  };

  const resetTz = () => {
    setTzOffset(localTzOffset);
    setShowTzControl(false);
  };

  const defaultHeaderStyle = useMemo(
    () => ({
      background: 'linear-gradient(90deg, #06101f 0%, #0a1a30 50%, #06101f 100%)',
      borderBottom: `2px solid ${identity.primaryColor}`,
      boxShadow: `0 4px 12px rgba(0,0,0,0.5), inset 0 -1px 0 ${identity.secondaryColor}80, inset 0 1px 0 ${identity.primaryColor}66`
    }),
    [identity.primaryColor, identity.secondaryColor]
  );

  const statusColors = useMemo(() => {
    switch (statusTone) {
      case 'warning':
        return { dot: '#f59e0b', text: '#fbbf24' };
      case 'error':
        return { dot: '#ef4444', text: '#f87171' };
      default:
        return { dot: '#22c55e', text: '#a7f3d0' };
    }
  }, [statusTone]);

  const showViews = availableViews && availableViews.length > 0;
  const showTabs = !showViews && tabs && tabs.length > 0;

  return (
    <header
      className={`shrink-0 z-10 relative ${headerClassName || ''}`}
      style={{ height: `${HEADER_HEIGHT}px`, ...defaultHeaderStyle, ...headerStyle }}
    >
      <div
        className="absolute inset-x-6 bottom-1 h-0.5 bg-gradient-to-r from-transparent via-opacity-70 to-transparent rounded-full blur-[1px]"
        style={{ backgroundColor: identity.secondaryColor }}
      />
      <div className="w-full max-w-[1600px] mx-auto h-full flex items-center justify-between px-4">
        <div className="flex items-center gap-3 md:gap-6 min-w-0 flex-shrink-0 flex-1">
          <button
            type="button"
            onClick={onRadialOpen}
            className="group relative flex items-center justify-center w-10 h-10 rounded-full transition-all duration-300 z-[5000] cursor-pointer pointer-events-auto"
            style={{ backgroundColor: `${identity.primaryColor}1a` }}
