            <h1
              className="text-2xl font-semibold uppercase font-orbitron-force leading-none tracking-[0.8em]"
              style={{ color: identity.primaryColor }}
            >
              {identity.displayName}
            </h1>
            <span
              className="text-sm uppercase tracking-[0.15em] font-semibold leading-none mt-0.5"
              style={{ color: identity.secondaryColor, textShadow: `0 0 4px ${identity.secondaryColor}99` }}
            >
              {identity.subtitle}
            </span>
          </div>
          {(showViews || showTabs) && (
            <div className="h-6 w-[1px] bg-gradient-to-b from-transparent via-white/10 to-transparent hidden md:block" />
          )}
          {showViews && (
            <div className="flex space-x-2 md:space-x-3 overflow-x-auto ml-2">
              {availableViews!.map((view) => (
                <NavButton
                  key={view.id}
                  label={view.label}
                  active={activeView === view.id}
                  onClick={() => onViewChange?.(view.id)}
                  primaryColor={identity.primaryColor}
                  secondaryColor={identity.secondaryColor}
                />
              ))}
            </div>
          )}
          {showTabs && (
            <div className="flex space-x-2 md:space-x-3 overflow-x-auto ml-2">
              {tabs.map((tab) => (
                <NavButton
                  key={tab.id}
                  label={tab.label}
                  active={activeTab === tab.id}
                  onClick={() => onTabChange?.(tab.id)}
                  primaryColor={identity.primaryColor}
                  secondaryColor={identity.secondaryColor}
                />
              ))}
            </div>
          )}
        </div>
        <div className="flex items-center gap-2 md:gap-4 flex-shrink-0">
          <div className="relative group/clock">
            <button
              onClick={() => setShowTzControl(!showTzControl)}
              className="flex flex-col text-right min-w-[140px] hover:bg-white/5 p-1 rounded transition-colors cursor-pointer"
              title="Click to adjust Timezone"
            >
              <span className="text-xs text-[#9fb4d6] uppercase tracking-[0.15em] font-semibold flex items-center justify-end gap-2">
                {tzOffset !== localTzOffset && <Globe size={10} className="text-alden-gold animate-pulse" />}
                {showTzControl ? 'Adjust Timezone' : (
                  <>
                    {new Date(now.getTime() + tzOffset * 3600000).toLocaleDateString('en-US', {
                      month: 'short',
                      day: 'numeric',
                      timeZone: 'UTC'
                    })}
                    <span className="text-[10px] ml-1 opacity-70">
                      {tzOffset === 0 ? 'UTC' : `UTC${tzOffset > 0 ? '+' : ''}${tzOffset}`}
                    </span>
                  </>
                )}
              </span>
              <span className="text-sm text-[#e5f7ff] font-bold tracking-[0.1em] font-orbitron-force">
                {new Date(now.getTime() + tzOffset * 3600000).toLocaleTimeString('en-US', {
                  hour: '2-digit',
                  minute: '2-digit',
                  hour12: true,
                  timeZone: 'UTC'
                })}
              </span>
            </button>
            {(showTzControl || tzOffset !== localTzOffset) && (
              <div
                className={`absolute top-full right-0 mt-2 bg-[#0b1221] border border-[#2d3748] p-4 rounded-lg shadow-xl z-50 min-w-[240px] ${
                  !showTzControl ? 'hidden group-hover/clock:block' : ''
                }`}
              >
                <div className="flex items-center justify-between mb-4">
                  <span className="text-[10px] text-[#9fb4d6] uppercase tracking-wider">Time Travel</span>
                  <button onClick={resetTz} className="text-[10px] text-alden-gold hover:underline font-bold">RESET LOCAL</button>
                </div>
                <div className="grid grid-cols-4 gap-2 mb-4">
                  {[-5, -6, -7, -8].map(offset => (
                    <button
                      key={offset}
                      onClick={() => { setTzOffset(offset); }}
                      className={`text-xs p-1 rounded border ${
                        tzOffset === offset ? 'bg-alden-cyan/20 border-alden-cyan text-alden-cyan' : 'border-white/10 hover:bg-white/5 text-slate-400'
                      }`}
                    >
                      {offset === -5 ? 'EST' : offset === -6 ? 'CST' : offset === -7 ? 'MST' : 'PST'}
                    </button>
                  ))}
                </div>
                <input
                  type="range"
                  min="-12"
                  max="14"
                  step="1"
                  value={tzOffset}
                  onChange={handleTzChange}
                  className="w-full h-1 bg-[#2d3748] rounded-lg appearance-none cursor-pointer accent-alden-cyan mb-2"
                />
                <div className="flex justify-between text-[10px] text-slate-500 font-mono">
                  <span>-12</span>
                  <span>UTC</span>
                  <span>+14</span>
                </div>
              </div>
            )}
          </div>
          <div
            className="flex items-center gap-2 px-3 py-1.5 bg-black/20 rounded-lg border border-white/10 backdrop-blur-md cursor-help"
            title={`System Status: ${statusLabel} | Tone: ${statusTone}`}
          >
            <div
              className="w-1.5 h-1.5 rounded-full shadow-[0_0_8px_currentColor] animate-pulse"
              style={{ backgroundColor: statusColors.dot }}
            />
            <span className="text-[10px] text-[#9fb4d6] uppercase tracking-wider font-bold hidden md:inline">
              {statusLabel}
            </span>
          </div>
          {headerActions}
          {onBackToLauncher && (
