// Sentry Authentication Manager
// Provides centralized authentication and user context management
//
// Architecture:
// - Integrates with SecureAuthenticator for JWT token management
// - Maintains current user session in Tauri state
// - Provides user_id automatically to all commands
// - Handles login/logout/session management

use crate::{auth::SecureAuthenticator, db_manager::DatabaseManager};
use argon2::{
    password_hash::{PasswordHash, PasswordHasher, PasswordVerifier, SaltString},
    Argon2,
};
use chrono::{DateTime, Utc};
use rand::rngs::OsRng;
use serde::{Deserialize, Serialize};
use sqlx::{FromRow, SqlitePool};
use std::sync::Arc;
use tokio::sync::RwLock;

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct UserSession {
    pub user_id: String,
    pub auth_token: String,
    pub created_at: DateTime<Utc>,
    pub last_activity: DateTime<Utc>,
    pub is_authenticated: bool,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct LoginRequest {
    pub username: String,
    pub password: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct LoginResponse {
    pub success: bool,
    pub user_id: Option<String>,
    pub auth_token: Option<String>,
    pub error: Option<String>,
}

#[derive(Debug, Clone, FromRow)]
#[allow(dead_code)]
struct AuthUserRecord {
    pub user_id: String,
    pub username: String,
    pub password_hash: String,
    pub failed_attempts: i32,
    pub last_failed_attempt: Option<String>,
    pub created_at: String,
    pub updated_at: String,
}

/// Sentry Authentication Manager
/// Manages user sessions and provides authentication services
pub struct SentryAuthManager {
    authenticator: Arc<SecureAuthenticator>,
    db: Arc<RwLock<Option<DatabaseManager>>>,
    current_session: Arc<RwLock<Option<UserSession>>>,
}

impl SentryAuthManager {
    pub fn new(
        authenticator: Arc<SecureAuthenticator>,
        db: Arc<RwLock<Option<DatabaseManager>>>,
    ) -> Self {
        Self {
            authenticator,
            db,
            current_session: Arc::new(RwLock::new(None)),
        }
    }

    pub async fn initialize_schema(&self) -> Result<(), String> {
        let pool = self.get_write_pool().await?;

        sqlx::query(
            r#"
            CREATE TABLE IF NOT EXISTS auth_users (
                user_id TEXT PRIMARY KEY,
                username TEXT UNIQUE NOT NULL,
                password_hash TEXT NOT NULL,
                failed_attempts INTEGER NOT NULL DEFAULT 0,
                last_failed_attempt TEXT,
                created_at TEXT NOT NULL,
                updated_at TEXT NOT NULL
            )
            "#,
        )
        .execute(&pool)
        .await
        .map_err(|e| format!("Failed to initialize auth schema: {e}"))?;
        Ok(())
    }

    async fn get_read_pool(&self) -> Result<SqlitePool, String> {
        let db_guard = self.db.read().await;
        let manager = db_guard
            .as_ref()
            .ok_or("Database not initialized for Sentry Auth")?;
        Ok(manager.read_pool.clone())
    }

    async fn get_write_pool(&self) -> Result<SqlitePool, String> {
        let db_guard = self.db.read().await;
        let manager = db_guard
            .as_ref()
            .ok_or("Database not initialized for Sentry Auth")?;
        Ok(manager.write_pool.clone())
    }

    /// Get current user ID (if authenticated)
    pub async fn get_current_user_id(&self) -> Option<String> {
        let session = self.current_session.read().await;
        session
            .as_ref()
            .filter(|s| s.is_authenticated)
            .map(|s| s.user_id.clone())
    }

    /// Get current user ID or default
    pub async fn get_user_id_or_default(&self) -> String {
        self.get_current_user_id()
            .await
            .unwrap_or_else(|| "default-user".to_string())
    }

    /// Get current session
    pub async fn get_session(&self) -> Option<UserSession> {
        self.current_session.read().await.clone()
    }

    const MAX_FAILED_ATTEMPTS: i32 = 5;
    const RATE_LIMIT_WINDOW_SECS: i64 = 60;

    /// Login user with password verification and rate limiting
    pub async fn login(&self, username: &str, password: &str) -> Result<LoginResponse, String> {
        println!("[SentryAuth] Login attempt for user: {username}");

        if username.trim().is_empty() || password.trim().is_empty() {
            return Err("Username and password are required".to_string());
        }

        let read_pool = self.get_read_pool().await?;
        let write_pool = self.get_write_pool().await?;
        let now = Utc::now();
        let record = self
            .fetch_user(&read_pool, username)
            .await?
            .ok_or_else(|| "Invalid username or password".to_string())?;

        if Self::is_rate_limited(&record, now) {
            return Err(
                "Too many login attempts. Please wait a minute before retrying.".to_string(),
            );
        }

        let parsed_hash = PasswordHash::new(&record.password_hash)
            .map_err(|_| "Invalid username or password".to_string())?;
        if Argon2::default()
            .verify_password(password.as_bytes(), &parsed_hash)
            .is_err()
        {
            self.record_failed_attempt(&write_pool, username, record.failed_attempts, now)
                .await?;
            return Err("Invalid username or password".to_string());
        }

        self.reset_failed_attempts(&write_pool, username, now)
            .await?;

        let user = record;
        let auth_token = self.authenticator.generate_token(&user.user_id, 24).await?;

        let session = UserSession {
            user_id: user.user_id.clone(),
            auth_token: auth_token.clone(),
            created_at: now,
            last_activity: now,
            is_authenticated: true,
        };

        {
            let mut current = self.current_session.write().await;
            *current = Some(session);
        }

        println!("[SentryAuth] Login successful for user: {username}");

        Ok(LoginResponse {
            success: true,
            user_id: Some(user.user_id),
            auth_token: Some(auth_token),
            error: None,
        })
    }

    /// Auto-login with default user (development only)
    #[cfg(debug_assertions)]
    pub async fn auto_login_default(&self) -> Result<UserSession, String> {
        println!("[SentryAuth] Auto-login with default user");

        let auth_token = self
            .authenticator
            .generate_token("default-user", 24)
            .await?;

        let now = Utc::now();
        let session = UserSession {
            user_id: "default-user".to_string(),
            auth_token: auth_token.clone(),
            created_at: now,
            last_activity: now,
            is_authenticated: true,
        };

        // Store session
        {
            let mut current = self.current_session.write().await;
            *current = Some(session.clone());
        }

        println!("[SentryAuth] Auto-login successful");
        Ok(session)
    }

    #[cfg(not(debug_assertions))]
    #[allow(dead_code)]
    pub async fn auto_login_default(&self) -> Result<UserSession, String> {
        Err("Auto-login disabled in production".to_string())
    }

    async fn fetch_user(
        &self,
        pool: &SqlitePool,
        username: &str,
    ) -> Result<Option<AuthUserRecord>, String> {
        sqlx::query_as::<_, AuthUserRecord>(
            "SELECT user_id, username, password_hash, failed_attempts, last_failed_attempt, created_at, updated_at FROM auth_users WHERE username = ?",
        )
        .bind(username)
        .fetch_optional(pool)
        .await
        .map_err(|e| format!("Failed to query auth user: {e}"))
    }

    fn is_rate_limited(record: &AuthUserRecord, now: DateTime<Utc>) -> bool {
        if record.failed_attempts < Self::MAX_FAILED_ATTEMPTS {
            return false;
        }

        if let Some(ref last_failed) = record.last_failed_attempt {
            if let Ok(parsed) = DateTime::parse_from_rfc3339(last_failed) {
                let elapsed = now.signed_duration_since(parsed.with_timezone(&Utc));
                return elapsed.num_seconds() < Self::RATE_LIMIT_WINDOW_SECS;
            }
        }

        false
    }

    async fn record_failed_attempt(
        &self,
        pool: &SqlitePool,
        username: &str,
        current_attempts: i32,
        now: DateTime<Utc>,
    ) -> Result<(), String> {
        sqlx::query(
            "UPDATE auth_users SET failed_attempts = ?, last_failed_attempt = ?, updated_at = ? WHERE username = ?",
        )
        .bind(current_attempts + 1)
        .bind(now.to_rfc3339())
        .bind(now.to_rfc3339())
        .bind(username)
        .execute(pool)
        .await
        .map_err(|e| format!("Failed to update failed attempts: {e}"))?;
        Ok(())
    }

    async fn reset_failed_attempts(
        &self,
        pool: &SqlitePool,
        username: &str,
        now: DateTime<Utc>,
    ) -> Result<(), String> {
        sqlx::query(
            "UPDATE auth_users SET failed_attempts = 0, last_failed_attempt = NULL, updated_at = ? WHERE username = ?",
        )
        .bind(now.to_rfc3339())
        .bind(username)
        .execute(pool)
        .await
        .map_err(|e| format!("Failed to reset failed attempts: {e}"))?;
        Ok(())
    }

    #[allow(dead_code)]
    fn hash_password(&self, password: &str) -> Result<String, String> {
        let salt = SaltString::generate(&mut OsRng);
        Ok(Argon2::default()
            .hash_password(password.as_bytes(), &salt)
            .map_err(|e| format!("Password hashing failed: {e}"))?
            .to_string())
    }

    /// Logout current user
    pub async fn logout(&self) -> Result<(), String> {
        println!("[SentryAuth] Logging out current user");

        let session = {
            let mut current = self.current_session.write().await;
            current.take()
        };

        if let Some(session) = session {
            // Revoke the auth token
            self.authenticator.revoke_token(&session.auth_token).await?;
            println!(
                "[SentryAuth] Logout successful for user: {}",
                session.user_id
            );
        }

        Ok(())
    }

    /// Verify and refresh session
    pub async fn verify_session(&self, auth_token: &str) -> Result<UserSession, String> {
        println!("[SentryAuth] Verifying session");

        // Verify token with authenticator
        let user_id = self.authenticator.verify_token(auth_token).await?;

        let now = Utc::now();
        let session = UserSession {
            user_id: user_id.clone(),
            auth_token: auth_token.to_string(),
            created_at: now, // TODO: Get actual creation time
            last_activity: now,
            is_authenticated: true,
        };

        // Update session
        {
            let mut current = self.current_session.write().await;
            *current = Some(session.clone());
        }

        println!("[SentryAuth] Session verified for user: {user_id}");
        Ok(session)
    }

    /// Update last activity timestamp
    #[allow(dead_code)]
    pub async fn touch_session(&self) {
        let mut session = self.current_session.write().await;
        if let Some(ref mut s) = *session {
            s.last_activity = Utc::now();
        }
    }
}

// ============================================================================
// Tauri Command Handlers
// ============================================================================

use tauri::State;

pub struct SentryAuthState {
    pub auth_manager: Arc<SentryAuthManager>,
}

/// Get current session
#[tauri::command]
pub async fn sentry_auth_get_session(
    state: State<'_, SentryAuthState>,
) -> Result<Option<UserSession>, String> {
    Ok(state.auth_manager.get_session().await)
}

/// Login user
#[tauri::command]
pub async fn sentry_auth_login(
    request: LoginRequest,
    state: State<'_, SentryAuthState>,
) -> Result<LoginResponse, String> {
    state
        .auth_manager
        .login(&request.username, &request.password)
        .await
}

/// Logout current user
#[tauri::command]
pub async fn sentry_auth_logout(state: State<'_, SentryAuthState>) -> Result<(), String> {
    state.auth_manager.logout().await
}

/// Verify session token
#[tauri::command]
pub async fn sentry_auth_verify_session(
    auth_token: String,
    state: State<'_, SentryAuthState>,
) -> Result<UserSession, String> {
    state.auth_manager.verify_session(&auth_token).await
}

/// Register a new user (call this before login in production)
/// Auto-login with default user (development only)
#[cfg(debug_assertions)]
#[tauri::command]
pub async fn sentry_auth_auto_login(
    state: State<'_, SentryAuthState>,
) -> Result<UserSession, String> {
    state.auth_manager.auto_login_default().await
}

#[cfg(not(debug_assertions))]
#[tauri::command]
pub async fn sentry_auth_auto_login(_: State<'_, SentryAuthState>) -> Result<UserSession, String> {
    Err("Auto-login disabled in production".to_string())
}

/// Get current user ID
#[tauri::command]
pub async fn sentry_auth_get_user_id(state: State<'_, SentryAuthState>) -> Result<String, String> {
    state.auth_manager.get_user_id_or_default().await.pipe(Ok)
}

