#[tauri::command]
pub async fn synapse_get_plugin_metrics(
    plugin_id: String,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<PluginMetrics, String> {
    let state = state.read().await;

    // Get plugin to determine state
    let plugin = state.plugin_manager.get_plugin(&plugin_id).await?;

    // For MVP: Simulate metrics based on plugin state
    // Real metrics implementation would monitor actual plugin processes
    let memory_mb = match plugin.state {
        crate::synapse::models::PluginState::Enabled => 15.0 + (plugin_id.len() as f64) % 20.0,
        crate::synapse::models::PluginState::Disabled => 2.0,
        crate::synapse::models::PluginState::Installed => 1.0,
        crate::synapse::models::PluginState::Error(_) => 0.5,
        crate::synapse::models::PluginState::Updating => 8.0,
    };

    let error_rate_percent = match plugin.state {
        crate::synapse::models::PluginState::Enabled => 0.1,
        crate::synapse::models::PluginState::Disabled => 0.0,
        crate::synapse::models::PluginState::Installed => 0.0,
        crate::synapse::models::PluginState::Error(_) => 100.0,
        crate::synapse::models::PluginState::Updating => 0.0,
    };

    // Simulate uptime based on created_at timestamp
    let uptime_seconds = if plugin.enabled {
        // Parse created_at and calculate uptime
        match chrono::DateTime::parse_from_rfc3339(&plugin.created_at) {
            Ok(created) => {
                let now = chrono::Utc::now();
                let duration = now.signed_duration_since(created.with_timezone(&chrono::Utc));
                duration.num_seconds().max(0) as u64
            }
            Err(_) => 3600, // Default to 1 hour if parsing fails
        }
    } else {
        0
    };

    Ok(PluginMetrics {
        memory_mb,
        error_rate_percent,
        uptime_seconds,
    })
}

