    request: AuthenticatedRequest<ExecuteSkillRequest>,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<ExecutionResult, String> {
    let authenticated_user = auth
        .verify_token(&request.auth_token)
        .await
        .map_err(|e| format!("Authentication failed: {e}"))?;

    let state = state.read().await;
    state
        .skill_executor
        .execute_skill(
            &request.payload.skill_id,
            &request.payload.instance_id,
            &request.payload.agent_id,
            request.payload.inputs,
            &formatted_trigger(authenticated_user, request.payload.triggered_by),
        )
        .await
}

