/// Get all plugins
#[tauri::command]
pub async fn synapse_get_plugins(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
) -> Result<Vec<SynapsePlugin>, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    let query = r#"
        SELECT plugin_id, name, version, description, permissions, enabled, manifest, created_at
        FROM synapse_plugins
        ORDER BY created_at DESC
    "#;

    let rows = sqlx::query_as::<_, SynapsePluginRow>(query)
        .fetch_all(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to fetch Synapse plugins: {e}"))?;

    let plugins = rows.into_iter().map(|row| row.into()).collect();
    Ok(plugins)
}

/// Create new plugin
#[tauri::command]
pub async fn synapse_create_plugin(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    request: CreatePluginRequest,
) -> Result<SynapsePlugin, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    let plugin_id = format!("plugin_{}", chrono::Utc::now().timestamp_millis());
    let created_at = chrono::Utc::now().to_rfc3339();
    let permissions_json =
        serde_json::to_string(&request.permissions).map_err(|e| e.to_string())?;
    let manifest_json = serde_json::to_string(&request.manifest).map_err(|e| e.to_string())?;

    let query = r#"
        INSERT INTO synapse_plugins (
            plugin_id, name, version, description, permissions, enabled, manifest, created_at
        ) VALUES (?, ?, ?, ?, ?, TRUE, ?, ?)
    "#;

    sqlx::query(query)
        .bind(&plugin_id)
        .bind(&request.name)
        .bind(&request.version)
        .bind(&request.description)
        .bind(&permissions_json)
        .bind(&manifest_json)
        .bind(&created_at)
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to create Synapse plugin: {e}"))?;

    Ok(SynapsePlugin {
        plugin_id,
        name: request.name,
        version: request.version,
        description: request.description,
        permissions: request.permissions,
        enabled: true,
        manifest: request.manifest,
        created_at,
    })
}

