) -> Result<u32, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    let query = r#"DELETE FROM synapse_security_logs WHERE timestamp < ?"#;

    let result = sqlx::query(query)
        .bind(&older_than)
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to clear security logs: {e}"))?;

    Ok(result.rows_affected() as u32)
