/// Apply a conference template
#[tauri::command]
pub async fn core_template_apply(
    template_id: String,
    user_id: String,
    title: String,
    purpose: String,
    state: State<'_, ConferenceManager>,
) -> Result<Conference, String> {
    // Get template
    let template = match template_id.as_str() {
        "template_brainstorm" => ConferenceTemplate::brainstorm(),
        "template_problem_solving" => ConferenceTemplate::problem_solving(),
        _ => return Err(format!("Unknown template: {template_id}")),
    };

    // Create conference from template
    let participants = template
        .default_participants
        .into_iter()
        .map(|(agent_id, role)| super::ParticipantSpec { agent_id, role })
        .collect();

    let request = CreateConferenceRequest {
        user_id,
        title,
        purpose,
        conference_type: template.conference_type,
        participants: Some(participants),
    };

    state.create_conference(request).await
}

