    let offset_val = offset.unwrap_or(0);

    let query = r#"
        SELECT message_id, session_id, from_agent, to_agent, content, timestamp, priority
        FROM core_messages
        WHERE session_id = ?
        AND (
            from_agent = ? OR to_agent = ? OR from_agent IS NULL OR to_agent IS NULL
        )
        ORDER BY timestamp DESC
        LIMIT ? OFFSET ?
    "#;

    let rows = sqlx::query_as::<_, CoreMessageRow>(query)
        .bind(&session_id)
        .bind(&requesting_agent)
        .bind(&requesting_agent)
        .bind(limit_val)
        .bind(offset_val)
        .fetch_all(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to fetch chat history: {e}"))?;

    let mut messages = Vec::new();
    for row in rows {
        let mut content: serde_json::Value = serde_json::from_str(&row.content)
            .map_err(|e| format!("Failed to parse message content: {e}"))?;

        // Apply privacy filtering based on requesting agent
        content = apply_privacy_filter(content, &requesting_agent, &row.from_agent, &row.to_agent)?;

        messages.push(CoreMessage {
            message_id: row.message_id,
            session_id: row.session_id,
            from_agent: row.from_agent,
            to_agent: row.to_agent,
            content,
            timestamp: row.timestamp,
            priority: row.priority,
        });
    }

    Ok(messages)
}

/// Apply persona-specific context filtering to message content
fn apply_persona_context_filter(
    content: serde_json::Value,
    from_agent: &str,
    to_agent: &str,
) -> Result<serde_json::Value, String> {
    // Create a filtered copy of the content
    let mut filtered_content = content.clone();

    // Remove sensitive context based on agent boundaries
    if let Some(obj) = filtered_content.as_object_mut() {
        // Remove agent-internal context that shouldn't be shared
        obj.remove(format!("{from_agent}_internal").as_str());
        obj.remove(format!("{from_agent}_private").as_str());
        obj.remove(format!("{from_agent}_secrets").as_str());

        // Add routing metadata
        obj.insert(
            "routing_metadata".to_string(),
            serde_json::json!({
                "from_agent": from_agent,
                "to_agent": to_agent,
                "filtered_at": chrono::Utc::now().to_rfc3339(),
                "trust_level": "zero_trust"
            }),
        );
    }

    Ok(filtered_content)
}

/// Apply privacy filtering based on requesting agent and message participants
fn apply_privacy_filter(
    content: serde_json::Value,
    requesting_agent: &str,
    from_agent: &Option<String>,
    to_agent: &Option<String>,
) -> Result<serde_json::Value, String> {
    let mut filtered_content = content.clone();

    // If requesting agent is not a participant, apply strict filtering
    let is_participant = (from_agent.as_ref().is_some_and(|a| a == requesting_agent))
        || (to_agent.as_ref().is_some_and(|a| a == requesting_agent));

    if !is_participant && requesting_agent != "user" {
        // Non-participants get highly filtered content
        if let Some(obj) = filtered_content.as_object_mut() {
            // Only keep safe, public information
            let safe_keys = ["type", "timestamp", "priority", "routing_metadata"];
            let mut safe_content = serde_json::Map::new();

            for key in safe_keys.iter() {
                if let Some(value) = obj.get(*key) {
                    safe_content.insert(key.to_string(), value.clone());
                }
            }

            // Add privacy notice
            safe_content.insert(
                "privacy_notice".to_string(),
                serde_json::json!({
                    "filtered": true,
                    "reason": "requesting_agent_not_participant",
                    "access_level": "public_only"
                }),
            );

            filtered_content = serde_json::Value::Object(safe_content);
        }
    } else if is_participant {
        // Participants get content with agent-specific filtering
        if let Some(obj) = filtered_content.as_object_mut() {
            // Remove internal context from other agents
            if let Some(from) = from_agent {
                if from != requesting_agent {
                    obj.remove(format!("{from}_internal").as_str());
                    obj.remove(format!("{from}_private").as_str());
                }
            }

            if let Some(to) = to_agent {
                if to != requesting_agent {
                    obj.remove(format!("{to}_internal").as_str());
                    obj.remove(format!("{to}_private").as_str());
                }
            }
        }
    }

    Ok(filtered_content)
}

