use chrono::Utc;
use serde::{Deserialize, Serialize};
use serde_json::{json, Value};
use std::sync::Arc;
use tauri::State;
use tokio::sync::RwLock;
use uuid::Uuid;

use crate::db_manager::DatabaseManager;

/// Identifier for agents participating in Core routing.
#[derive(Debug, Clone, Copy, PartialEq, Eq, Serialize, Deserialize)]
#[serde(rename_all = "lowercase")]
pub enum AgentId {
    User,
    Alden,
    Core,
    Alice,
}

/// Priority classification used during routing decisions.
#[derive(Debug, Clone, Copy, PartialEq, Eq, Serialize, Deserialize)]
#[serde(rename_all = "lowercase")]
#[derive(Default)]
pub enum RoutingPriority {
    Low,
    #[default]
    Normal,
    High,
}

impl RoutingPriority {
    pub fn label(&self) -> &'static str {
        match self {
            RoutingPriority::Low => "low",
            RoutingPriority::Normal => "normal",
            RoutingPriority::High => "high",
        }
    }
}

/// Basic payload that flows between agents through Core.
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct OrchestratedMessage {
    pub message_id: String,
    pub session_id: String,
    pub from: AgentId,
    pub to: AgentId,
    pub payload: Value,
    pub priority: RoutingPriority,
    pub timestamp: String,
}

impl OrchestratedMessage {
    pub fn new(
        session_id: &str,
        from: AgentId,
        to: AgentId,
        payload: Value,
        priority: RoutingPriority,
    ) -> Self {
        Self {
            message_id: Uuid::new_v4().to_string(),
            session_id: session_id.to_string(),
            from,
            to,
            payload,
            priority,
            timestamp: Utc::now().to_rfc3339(),
        }
    }
}

/// Request payload received from the frontend to trigger the delegation loop.
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DelegationRequest {
    #[serde(default = "default_session_id")]
    pub session_id: String,
    #[serde(default = "default_user_id")]
    pub user_id: String,
    pub message: String,
    #[serde(default)]
    pub metadata: Option<Value>,
    #[serde(default)]
    pub priority: RoutingPriority,
}

/// Routing decision details returned to the caller.
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct RoutingDecision {
    pub path: Vec<AgentId>,
    pub reasoning: String,
    pub decision_time: String,
    pub priority: RoutingPriority,
    pub last_agent: AgentId,
}

/// Response shape returned by the orchestrator.
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DelegationResponse {
    pub routed_messages: Vec<OrchestratedMessage>,
    pub decision: RoutingDecision,
    pub alice_payload: Value,
    pub suggested_action: Option<SuggestedAction>,
}

/// Actions the UI can choose to trigger after reviewing the orchestration decision.
#[derive(Debug, Clone, Serialize, Deserialize)]
#[serde(rename_all = "snake_case")]
pub enum SuggestedAction {
    StartDeepWork {
        /// Human readable reason for the suggestion.
        reason: String,
        /// Suggested focus duration in minutes.
        suggested_minutes: u32,
    },
}

/// Lightweight orchestrator that enforces the Alden ƒ+' Core ƒ+' Alice loop.
pub struct CoreOrchestrator {
    db: Arc<RwLock<Option<DatabaseManager>>>,
}

impl CoreOrchestrator {
    pub fn new(db: Arc<RwLock<Option<DatabaseManager>>>) -> Self {
        Self { db }
    }

    pub async fn orchestrate(
        &self,
        request: DelegationRequest,
    ) -> Result<DelegationResponse, String> {
        let db_state = self.db.read().await;
        if db_state.is_none() {
            return Err(
                "Core orchestrator could not start because the database is not ready".into(),
            );
        }

        let DelegationRequest {
            session_id,
            user_id,
            message,
            metadata,
            priority,
        } = request;

        let session_id = if session_id.trim().is_empty() {
            default_session_id()
        } else {
            session_id
        };
        let user_id = if user_id.trim().is_empty() {
            default_user_id()
        } else {
            user_id
        };

        let sanitized_message = if message.trim().is_empty() {
            "No user message provided.".to_string()
        } else {
            message
        };

        let final_priority = compute_priority(&sanitized_message, priority);

        let summary = sanitized_message.chars().take(160).collect::<String>();

        let user_message_payload = json!({
            "userId": user_id,
            "message": sanitized_message,
            "metadata": metadata.clone(),
        });

        let alden_payload = json!({
            "summary": summary,
            "context": metadata.clone(),
            "priority": final_priority.label(),
        });

        let alice_payload = json!({
            "instructions": format!("Alice, please build on: {}", summary),
            "aldenSummary": summary,
            "priority": final_priority.label(),
            "metadata": metadata,
        });

        let suggested_action = infer_suggested_action(&sanitized_message, final_priority);

        let user_message = OrchestratedMessage::new(
            &session_id,
            AgentId::User,
            AgentId::Alden,
            user_message_payload,
            final_priority,
        );

        let alden_to_core = OrchestratedMessage::new(
            &session_id,
            AgentId::Alden,
            AgentId::Core,
            alden_payload,
            final_priority,
        );

        let core_to_alice = OrchestratedMessage::new(
            &session_id,
            AgentId::Core,
            AgentId::Alice,
            alice_payload.clone(),
            final_priority,
        );

        let decision = RoutingDecision {
            path: vec![AgentId::User, AgentId::Alden, AgentId::Core, AgentId::Alice],
            reasoning: "Alden recognized the request and asked Core to hand it to Alice for deeper analysis.".into(),
            decision_time: Utc::now().to_rfc3339(),
            priority: final_priority,
            last_agent: AgentId::Alice,
        };

        Ok(DelegationResponse {
            routed_messages: vec![user_message, alden_to_core, core_to_alice],
            decision,
            alice_payload,
            suggested_action,
        })
    }
}

fn default_user_id() -> String {
    "default-user".to_string()
}

fn default_session_id() -> String {
    "session-default".to_string()
}

fn compute_priority(message: &str, requested: RoutingPriority) -> RoutingPriority {
    if requested != RoutingPriority::High && message.to_lowercase().contains("urgent") {
        RoutingPriority::High
    } else {
        requested
    }
}

fn infer_suggested_action(message: &str, priority: RoutingPriority) -> Option<SuggestedAction> {
    // Very lightweight heuristic: if user asks to focus, mentions deep work, or priority is high, suggest deep work.
    let lower = message.to_lowercase();
    let focus_terms = [
        "deep work",
        "focus",
        "study",
        "concentrate",
        "heads down",
        "blocked out",
    ];
    let matches_focus = focus_terms.iter().any(|term| lower.contains(term));

    if matches_focus || priority == RoutingPriority::High {
        let reason = if matches_focus {
            "User requested focused or deep work time.".to_string()
        } else {
            "High priority request routed to Alice; deep work is recommended to resolve it."
                .to_string()
        };

        return Some(SuggestedAction::StartDeepWork {
            reason,
            suggested_minutes: 50,
        });
    }

    None
}

#[tauri::command]
pub async fn core_delegate_message(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    request: DelegationRequest,
) -> Result<DelegationResponse, String> {
    let orchestrator = CoreOrchestrator::new(db.inner().clone());
    orchestrator.orchestrate(request).await
}

