pub struct SentryManager {
    db: Arc<RwLock<Option<DatabaseManager>>>,
}

impl SentryManager {
    pub fn new(db: Arc<RwLock<Option<DatabaseManager>>>) -> Self {
        Self { db }
    }

    pub async fn initialize_schema(&self) -> Result<(), String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        // Create Sentry tables
        let queries = vec![
            // Security events table
            r#"
                CREATE TABLE IF NOT EXISTS sentry_security_events (
                    event_id TEXT PRIMARY KEY,
                    user_id TEXT NOT NULL,
                    agent_id TEXT,
                    event_type TEXT NOT NULL,
                    severity TEXT NOT NULL,
                    description TEXT NOT NULL,
                    details TEXT NOT NULL, -- JSON
                    timestamp TEXT NOT NULL,
                    resolved BOOLEAN DEFAULT FALSE,
                    resolution TEXT
                )
            "#,
            // Compliance rules table
            r#"
                CREATE TABLE IF NOT EXISTS sentry_compliance_rules (
                    rule_id TEXT PRIMARY KEY,
                    name TEXT NOT NULL,
                    description TEXT NOT NULL,
                    severity TEXT NOT NULL,
                    conditions TEXT NOT NULL, -- JSON
                    enabled BOOLEAN DEFAULT TRUE,
                    created_at TEXT NOT NULL
                )
            "#,
            // Audit reports table
            r#"
                CREATE TABLE IF NOT EXISTS sentry_audit_reports (
                    report_id TEXT PRIMARY KEY,
                    user_id TEXT NOT NULL,
                    type TEXT NOT NULL,
                    generated_at TEXT NOT NULL,
                    start_date TEXT NOT NULL,
                    end_date TEXT NOT NULL,
                    summary TEXT NOT NULL, -- JSON
                    details TEXT NOT NULL -- JSON
                )
            "#,
        ];

        for query in queries {
            sqlx::query(query)
                .execute(&db.write_pool)
                .await
                .map_err(|e| format!("Failed to create Sentry table: {e}"))?;
        }

        Ok(())
    }
}

