/// Get security events
#[tauri::command]
pub async fn sentry_get_events(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    user_id: String,
    event_type: Option<String>,
    severity: Option<String>,
    agent_id: Option<String>,
    resolved: Option<bool>,
    limit: Option<i64>,
) -> Result<Vec<SentrySecurityEvent>, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    // For simplicity with dynamic filters, we'll fetch all events for user and filter in memory
    // In production, consider using a query builder library for more complex dynamic queries
    let query = r#"
        SELECT event_id, user_id, agent_id, event_type, severity, description,
               details, timestamp, resolved, resolution
        FROM sentry_security_events
        WHERE user_id = ?
        ORDER BY timestamp DESC
    "#;

    let rows = sqlx::query_as::<_, SentrySecurityEventRow>(query)
        .bind(&user_id)
        .fetch_all(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to fetch Sentry events: {e}"))?;

    let mut events = Vec::new();
    for row in rows {
        // Apply filters
        if let Some(ref et) = event_type {
            if &row.event_type != et {
                continue;
            }
        }
        if let Some(ref s) = severity {
            if &row.severity != s {
                continue;
            }
        }
        if let Some(ref a) = agent_id {
            if row.agent_id.as_ref() != Some(a) {
                continue;
            }
        }
        if let Some(r) = resolved {
            if row.resolved != r {
                continue;
            }
        }

        events.push(map_security_event_row(row)?);

        if let Some(l) = limit {
            if events.len() >= l as usize {
                break;
            }
        }
    }

    Ok(events)
}

