/// Create security event
#[tauri::command]
pub async fn sentry_create_event(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    request: CreateEventRequest,
) -> Result<SentrySecurityEvent, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    let event_id = format!("event_{}", chrono::Utc::now().timestamp_millis());
    let timestamp = chrono::Utc::now().to_rfc3339();

    let query = r#"
        INSERT INTO sentry_security_events (
            event_id, user_id, agent_id, event_type, severity, description, details, timestamp, resolved
        ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)
    "#;

    let details_json = serde_json::to_string(&request.details).map_err(|e| e.to_string())?;

    sqlx::query(query)
        .bind(&event_id)
        .bind(&request.user_id)
        .bind(&request.agent_id)
        .bind(&request.event_type)
        .bind(&request.severity)
        .bind(&request.description)
        .bind(&details_json)
        .bind(&timestamp)
        .bind(false) // resolved
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to create Sentry event: {e}"))?;

    Ok(SentrySecurityEvent {
        event_id,
        user_id: request.user_id,
        agent_id: request.agent_id,
        event_type: request.event_type,
        severity: request.severity,
        description: request.description,
        details: request.details,
        timestamp,
        resolved: false,
        resolution: None,
    })
}

