/// Get audit reports
#[tauri::command]
pub async fn sentry_get_reports(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    user_id: String,
    limit: Option<i64>,
) -> Result<Vec<SentryAuditReport>, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    let query = if limit.is_some() {
        r#"
        SELECT report_id, user_id, type, generated_at, start_date, end_date, summary, details
        FROM sentry_audit_reports
        WHERE user_id = ?
        ORDER BY generated_at DESC
        LIMIT ?
        "#
    } else {
        r#"
        SELECT report_id, user_id, type, generated_at, start_date, end_date, summary, details
        FROM sentry_audit_reports
        WHERE user_id = ?
        ORDER BY generated_at DESC
        "#
    };

    let rows = if let Some(l) = limit {
        sqlx::query_as::<_, SentryAuditReportRow>(query)
            .bind(&user_id)
            .bind(l)
            .fetch_all(&db.read_pool)
            .await
            .map_err(|e| format!("Failed to fetch Sentry reports: {e}"))?
    } else {
        sqlx::query_as::<_, SentryAuditReportRow>(query)
            .bind(&user_id)
            .fetch_all(&db.read_pool)
            .await
            .map_err(|e| format!("Failed to fetch Sentry reports: {e}"))?
    };

    let mut reports = Vec::new();
    for row in rows {
        let summary = serde_json::from_str(&row.summary)
            .map_err(|e| format!("Failed to parse report summary: {e}"))?;
        let details = serde_json::from_str(&row.details)
            .map_err(|e| format!("Failed to parse report details: {e}"))?;

        reports.push(SentryAuditReport {
