impl From<SynapsePluginRow> for SynapsePlugin {
    fn from(row: SynapsePluginRow) -> Self {
        let permissions = serde_json::from_str(&row.permissions).unwrap_or_default();
        let state = serde_json::from_str(&row.state).unwrap_or(PluginState::Installed);
        let manifest = serde_json::from_str(&row.manifest).unwrap_or_else(|_| {
            // Fallback manifest if parsing fails
            PluginManifest {
                plugin_id: row.plugin_id.clone(),
                name: row.name.clone(),
                version: row.version.clone(),
                author: None,
                description: row.description.clone(),
                authentication: AuthenticationConfig {
                    auth_type: AuthType::None,
                    storage: "none".to_string(),
                },
                permissions: PermissionSet {
                    read: vec![],
                    write: vec![],
                    network: vec![],
                },
                capabilities: vec![],
                webhook_config: None,
                api_endpoints: None,
            }
        });

        Self {
            plugin_id: row.plugin_id,
            name: row.name,
            version: row.version,
            description: row.description,
            permissions,
            enabled: row.enabled,
            state,
            manifest,
            created_at: row.created_at,
        }
    }
}

impl From<SynapseConnectionRow> for SynapseConnection {
    fn from(row: SynapseConnectionRow) -> Self {
        let SynapseConnectionRow {
            connection_id,
            plugin_id,
            service_name,
            endpoint,
            protocol,
            credentials_encrypted: _,
            status,
            last_activity,
            created_at,
        } = row;

        Self {
            connection_id,
            plugin_id,
            service_name,
            endpoint,
            protocol: serde_json::from_str(&protocol).unwrap_or(Protocol::RestApi {
                auth: AuthType::None,
            }),
            status: serde_json::from_str(&status).unwrap_or(ConnectionStatus::Disconnected),
            last_activity,
            created_at,
        }
    }
}

impl From<SynapseSecurityLogRow> for SynapseSecurityLog {
    fn from(row: SynapseSecurityLogRow) -> Self {
        Self {
            log_id: row.log_id,
            plugin_id: row.plugin_id,
            connection_id: row.connection_id,
            event_type: serde_json::from_str(&row.event_type)
                .unwrap_or(SecurityEventType::ApiCallMade),
            severity: serde_json::from_str(&row.severity).unwrap_or(SecuritySeverity::Info),
            timestamp: row.timestamp,
            details: serde_json::from_str(&row.details).unwrap_or_default(),
        }
    }
}

impl std::fmt::Display for PluginState {
    fn fmt(&self, f: &mut std::fmt::Formatter<'_>) -> std::fmt::Result {
        match self {
            PluginState::Installed => write!(f, "installed"),
            PluginState::Enabled => write!(f, "enabled"),
            PluginState::Disabled => write!(f, "disabled"),
            PluginState::Error(msg) => write!(f, "error: {msg}"),
            PluginState::Updating => write!(f, "updating"),
        }
    }
}

impl std::fmt::Display for ConnectionStatus {
    fn fmt(&self, f: &mut std::fmt::Formatter<'_>) -> std::fmt::Result {
        match self {
            ConnectionStatus::Connected => write!(f, "connected"),
            ConnectionStatus::Disconnected => write!(f, "disconnected"),
            ConnectionStatus::Failed => write!(f, "failed"),
            ConnectionStatus::Testing => write!(f, "testing"),
        }
    }
}

