/// Get all personas for user
#[tauri::command]
#[allow(dead_code)]
pub async fn mimic_get_personas(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    request: GetPersonasRequest,
) -> Result<Vec<MimicPersona>, String> {
    println!(
        "[Mimic] mimic_get_personas invoked for user_id={}",
        request.user_id
    );
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    let query = r#"
        SELECT persona_id, user_id, name, description, traits, expertise_areas, active, created_at
        FROM mimic_personas
        WHERE user_id = ?
        ORDER BY created_at DESC
    "#;

    let rows = sqlx::query_as::<_, MimicPersonaRow>(query)
        .bind(&request.user_id)
        .fetch_all(&db.read_pool)
        .await
        .map_err(|e| {
            eprintln!(
                "[Mimic] mimic_get_personas failed for user_id={}: {}",
                request.user_id, e
            );
            format!("Failed to fetch Mimic personas: {e}")
        })?;

    let mut personas = Vec::new();
    for row in rows {
        let traits: HashMap<String, f64> = serde_json::from_str(&row.traits)
            .map_err(|e| format!("Failed to parse traits: {e}"))?;
        let expertise_areas: Vec<String> = serde_json::from_str(&row.expertise_areas)
            .map_err(|e| format!("Failed to parse expertise areas: {e}"))?;

        personas.push(MimicPersona {
            persona_id: row.persona_id,
            user_id: row.user_id,
            name: row.name,
            description: row.description,
            traits,
            expertise_areas,
            active: row.active,
            created_at: row.created_at,
        });
    }

    println!(
        "[Mimic] mimic_get_personas returning {} personas for user_id={}",
        personas.len(),
        request.user_id
    );

    Ok(personas)
}

/// Get active persona for user
#[tauri::command]
#[allow(dead_code)]
pub async fn mimic_get_active_persona(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    request: GetActivePersonaRequest,
) -> Result<Option<MimicPersona>, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    let query = r#"
        SELECT p.persona_id, p.user_id, p.name, p.description, p.traits, 
               p.expertise_areas, p.active, p.created_at
        FROM mimic_personas p
        INNER JOIN mimic_active_persona a ON p.persona_id = a.persona_id
        WHERE a.user_id = ?
    "#;

    let row = sqlx::query_as::<_, MimicPersonaRow>(query)
        .bind(&request.user_id)
        .fetch_optional(&db.read_pool)
        .await
        .map_err(|e| format!("Failed to fetch active Mimic persona: {e}"))?;

    if let Some(row) = row {
        let traits: HashMap<String, f64> = serde_json::from_str(&row.traits)
            .map_err(|e| format!("Failed to parse traits: {e}"))?;
        let expertise_areas: Vec<String> = serde_json::from_str(&row.expertise_areas)
            .map_err(|e| format!("Failed to parse expertise areas: {e}"))?;

        Ok(Some(MimicPersona {
            persona_id: row.persona_id,
            user_id: row.user_id,
            name: row.name,
            description: row.description,
            traits,
            expertise_areas,
            active: row.active,
            created_at: row.created_at,
        }))
    } else {
        Ok(None)
    }
}

/// Create new persona
#[tauri::command]
pub async fn mimic_create_persona(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    request: CreatePersonaRequest,
) -> Result<MimicPersona, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    let persona_id = format!("persona_{}", chrono::Utc::now().timestamp_millis());
    let created_at = chrono::Utc::now().to_rfc3339();

    let query = r#"
        INSERT INTO mimic_personas (
            persona_id, user_id, name, description, traits, expertise_areas, created_at
        ) VALUES (?, ?, ?, ?, ?, ?, ?)
    "#;

    sqlx::query(query)
        .bind(&persona_id)
        .bind(&request.user_id)
        .bind(&request.name)
        .bind(&request.description)
        .bind(&serde_json::to_string(&request.traits).map_err(|e| e.to_string())?)
        .bind(&serde_json::to_string(&request.expertise_areas).map_err(|e| e.to_string())?)
        .bind(&created_at)
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to create Mimic persona: {e}"))?;

    Ok(MimicPersona {
        persona_id,
        user_id: request.user_id,
        name: request.name,
        description: request.description,
        traits: request.traits,
        expertise_areas: request.expertise_areas,
        active: false,
        created_at,
    })
}

