      // Update growth stats
      let stats_query = r#"
          INSERT INTO mimic_growth_stats (persona_id, sessions_completed, last_used)
          VALUES (?, 1, ?)
          ON CONFLICT(persona_id) DO UPDATE SET
              sessions_completed = sessions_completed + 1,
              total_usage_time = total_usage_time + ?,
              last_used = ?
