/// Authentication Guards and Request Wrappers
///
/// Provides centralized authentication enforcement for Tauri IPC commands.
use serde::{Deserialize, Serialize};

/// Generic authenticated request wrapper
/// Wraps any payload type with an auth_token field
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AuthenticatedRequest<T> {
    pub auth_token: String,
    #[serde(flatten)]
    pub payload: T,
}

impl<T> AuthenticatedRequest<T> {
    pub fn new(auth_token: String, payload: T) -> Self {
        Self {
            auth_token,
            payload,
        }
    }
}

/// Macro to enforce authentication and extract user_id
///
/// Usage: `let user_id = require_auth!(auth_state, &token);`
///
/// Returns the user_id on success, or returns early with an error
#[macro_export]
macro_rules! require_auth {
    ($auth:expr, $token:expr) => {{
        match $auth.verify_token($token).await {
            Ok(user_id) => user_id,
            Err(e) => {
                eprintln!("[SECURITY] Authentication failed: {}", e);
                return Err(format!("Authentication failed: {}", e));
            }
        }
    }};
}

/// Macro to verify user owns the resource
///
/// Usage: `require_ownership!(authenticated_user, payload.user_id);`
#[macro_export]
macro_rules! require_ownership {
    ($auth_user:expr, $resource_user:expr) => {{
        if $auth_user != $resource_user {
            eprintln!(
                "[SECURITY] User {} attempted to access resource owned by {}",
                $auth_user, $resource_user
            );
            return Err(format!(
                "Unauthorized: Cannot access resources belonging to other users"
            ));
        }
    }};
}

