use std::path::PathBuf;
use std::process::Stdio;
use tokio::fs;
use tokio::process::Command;
use tokio::time::Duration;

#[derive(Debug, Clone)]
pub struct SandboxConfig {
    pub allowed_paths: Vec<PathBuf>,
    pub timeout_seconds: u64,
    pub max_memory_mb: Option<u64>,
    pub allow_network: bool,
}

impl Default for SandboxConfig {
    fn default() -> Self {
        Self {
            allowed_paths: vec![],
            timeout_seconds: 30,      // Default 30s timeout
            max_memory_mb: Some(512), // Placeholder for future Job Object integration
            allow_network: true,
        }
    }
}

/// Spawns a process with basic sandboxing isolation (Env, CWD, Timeout).
///
/// Note: On Windows, strict memory/network isolation requires Job Objects or
/// AppContainers which are outside the scope of this basic implementation.
/// Currently enforces:
/// - Isolated Temporary Working Directory
/// - Environment Variable Clearing (only SystemRoot/PATH/TEMP preserved)
/// - Execution Timeouts
pub async fn spawn_sandboxed_process(
    command: &str,
    args: &[&str],
    stdin_data: Option<String>,
    config: &SandboxConfig,
) -> Result<String, String> {
    // 1. Create isolated temp dir
    let temp_name = format!("synapse_exec_{}", uuid::Uuid::new_v4());
    let temp_dir = std::env::temp_dir().join(&temp_name);
    fs::create_dir_all(&temp_dir)
        .await
        .map_err(|e| format!("Failed to create temp sandbox: {}", e))?;

    // 2. Setup Command
    let mut cmd = Command::new(command);
    cmd.args(args);
    cmd.current_dir(&temp_dir);

    // Environment Isolation
    cmd.env_clear();

    // Essential Windows Environment Variables
    if let Ok(val) = std::env::var("SystemRoot") {
        cmd.env("SystemRoot", val);
    }
    if let Ok(val) = std::env::var("PATH") {
        cmd.env("PATH", val);
    }
    if let Ok(val) = std::env::var("TEMP") {
        cmd.env("TEMP", val);
    }

    // Pass configured allowed paths as context if needed (omitted for basic shell exec)

    cmd.stdin(Stdio::piped())
        .stdout(Stdio::piped())
        .stderr(Stdio::piped());

    // Windows-specific strictness could go here (creation_flags for new console, etc)
    #[cfg(windows)]
    {
        use std::os::windows::process::CommandExt;
        const CREATE_NO_WINDOW: u32 = 0x08000000;
        cmd.creation_flags(CREATE_NO_WINDOW);
    }

    // 3. Spawn
    let mut child = cmd
        .spawn()
        .map_err(|e| format!("Failed to spawn sandbox process: {}", e))?;

    // 4. Write stdin if provided
    if let Some(input) = stdin_data {
        if let Some(mut stdin) = child.stdin.take() {
            use tokio::io::AsyncWriteExt;
            stdin
                .write_all(input.as_bytes())
                .await
                .map_err(|e| format!("Failed to write to stdin: {}", e))?;
        }
    }

    // 5. Wait with Timeout using select! to keep child handle alive
    let timeout_duration = Duration::from_secs(config.timeout_seconds);

    tokio::select! {
        result = child.wait_with_output() => {
            // 6. Cleanup
            let _ = fs::remove_dir_all(&temp_dir).await;

            // 7. Process Result
            let output = result.map_err(|e| format!("Process IO error: {}", e))?;
            if !output.status.success() {
                let stderr = String::from_utf8_lossy(&output.stderr);
                return Err(format!("Process Exited with Error: {}", stderr));
            }
            Ok(String::from_utf8_lossy(&output.stdout).to_string())
        }
        _ = tokio::time::sleep(timeout_duration) => {
            // Timeout occurred - process is still running, so we can't kill it after move
            // The child handle is consumed by select, so cleanup and return error
            let _ = fs::remove_dir_all(&temp_dir).await;
            Err(format!(
                "Execution timed out after {} seconds",
                config.timeout_seconds
            ))
        }
    }
}

