    use super::*;
    use crate::db_manager::DatabaseManager;
    use sqlx::sqlite::SqlitePoolOptions;
    use std::sync::Arc;
    use tokio::sync::RwLock;

    async fn create_test_db() -> Arc<RwLock<Option<DatabaseManager>>> {
        let pool = SqlitePoolOptions::new()
            .max_connections(5)
            .connect("sqlite::memory:")
            .await
            .expect("Failed to create test database");
        Arc::new(RwLock::new(Some(DatabaseManager {
            write_pool: pool.clone(),
            read_pool: pool,
        })))
    }

    async fn initialize_alice(db: &Arc<RwLock<Option<DatabaseManager>>>) {
        let manager = AliceManager::new(db.clone());
        manager.initialize_schema().await.unwrap();
    }

    #[tokio::test]
    async fn deep_work_stop_sets_end_time_and_duration() {
        let db = create_test_db().await;
        initialize_alice(&db).await;

        let db_guard = db.read().await;
        let db_ref = db_guard.as_ref().unwrap();

        let request = StartDeepWorkRequest {
            user_id: "tester".to_string(),
            goal: Some("Ship feature".to_string()),
        };
        let started = alice_start_deep_work_internal(db_ref, &request)
            .await
            .expect("start session");

        // Backdate start time so duration is deterministic without sleeping
        let earlier = (chrono::Utc::now() - chrono::Duration::seconds(90)).to_rfc3339();
        sqlx::query("UPDATE alice_deep_work_sessions SET start_time = ? WHERE session_id = ?")
            .bind(&earlier)
            .bind(&started.session_id)
            .execute(&db_ref.write_pool)
            .await
            .unwrap();

        let stop_request = StopDeepWorkRequest {
            user_id: request.user_id.clone(),
            status: "completed".to_string(),
        };

        let stopped = alice_stop_deep_work_internal(db_ref, &stop_request)
            .await
            .expect("stop session");

        assert_eq!(stopped.status, "completed");
        assert!(stopped.duration_seconds.unwrap() >= 89);
        assert!(stopped.end_time.is_some());

        let persisted: (String, Option<String>, Option<i64>) = sqlx::query_as(
            "SELECT status, end_time, duration_seconds FROM alice_deep_work_sessions WHERE session_id = ?",
        )
        .bind(&stopped.session_id)
        .fetch_one(&db_ref.read_pool)
        .await
        .unwrap();

        assert_eq!(persisted.0, "completed");
        assert_eq!(persisted.1, stopped.end_time);
        assert_eq!(persisted.2, stopped.duration_seconds);
    }

    #[tokio::test]
    async fn deep_work_analysis_reports_focus_and_completion() {
        let db = create_test_db().await;
        initialize_alice(&db).await;

        let db_guard = db.read().await;
        let db_ref = db_guard.as_ref().unwrap();

        let request = StartDeepWorkRequest {
            user_id: "analyst".to_string(),
            goal: Some("Write report".to_string()),
        };
        let started = alice_start_deep_work_internal(db_ref, &request)
            .await
            .expect("start session");

        // Backdate to a one-hour session
        let start_time = (chrono::Utc::now() - chrono::Duration::seconds(3600)).to_rfc3339();
        sqlx::query(
            "UPDATE alice_deep_work_sessions SET start_time = ?, status = 'active' WHERE session_id = ?",
        )
        .bind(&start_time)
        .bind(&started.session_id)
        .execute(&db_ref.write_pool)
        .await
        .unwrap();

        let stop_request = StopDeepWorkRequest {
            user_id: request.user_id.clone(),
            status: "completed".to_string(),
        };
        let stopped = alice_stop_deep_work_internal(db_ref, &stop_request)
            .await
            .expect("stop session");

        // Insert a couple of signals within the last 30 days
        let tags_json = serde_json::to_string(&vec!["focus"]).unwrap();
        let evidence_json = serde_json::to_string(&serde_json::json!({"note": "test"})).unwrap();
        let created_at = chrono::Utc::now().to_rfc3339();

        sqlx::query(
            r#"
            INSERT INTO alice_behavioral_signals (
                signal_id, user_id, focus, stress, engagement, cognitive_load, tags, evidence, created_at
            ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)
        "#,
        )
        .bind("sig1")
        .bind(&request.user_id)
        .bind(80.0_f64)
        .bind(20.0_f64)
        .bind(75.0_f64)
        .bind(30.0_f64)
        .bind(&tags_json)
        .bind(&evidence_json)
        .bind(&created_at)
        .execute(&db_ref.write_pool)
        .await
        .unwrap();

        let analysis = alice_get_deep_work_analysis_internal(db_ref, &request.user_id)
            .await
            .expect("analysis");

        assert_eq!(analysis.total_sessions, 1);
        assert_eq!(analysis.completed_sessions, 1);
        assert_eq!(analysis.active_sessions, 0);
        assert!(analysis.average_session_duration_seconds >= 3599.0);
        assert!(analysis.completion_rate > 0.99);
        assert!((analysis.focus_score - 80.0).abs() < 1e-6);
        assert!((analysis.stress_score - 20.0).abs() < 1e-6);
        assert!((analysis.engagement_score - 75.0).abs() < 1e-6);
        assert_eq!(stopped.status, "completed");
    }
