pub async fn alden_email_test_connection(
    auth: State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<EmailTestConnectionRequest>,
    _state: State<'_, EmailState>,
) -> Result<(), String> {
    let _authenticated_user = auth
        .verify_token(&request.auth_token)
        .await
        .map_err(|e| format!("Authentication failed: {e}"))?;

    let (host, port, use_ssl) = match request.payload.provider.as_str() {
        "gmail" => ("imap.gmail.com".to_string(), 993, true),
        "outlook" => ("outlook.office365.com".to_string(), 993, true),
        "imap" => {
            let host = request
                .payload
                .server_host
                .clone()
                .ok_or("Server host required for custom IMAP")?;
            let port = request
                .payload
                .server_port
                .ok_or("Server port required for custom IMAP")?;
            (host, port, request.payload.use_ssl.unwrap_or(true))
        }
        _ => return Err(format!("Unsupported provider: {}", request.payload.provider)),
    };

    let imap_config = ImapConfig::custom(
        host,
        port as u16,
        request.payload.email_address,
        request.payload.password,
        use_ssl,
    );
    let imap_client = ImapClient::new(imap_config);
    imap_client
        .test_connection()
        .await
        .map_err(|e| format!("IMAP connection test failed: {e}"))?;

    Ok(())
}

