}

impl CalendarManager {
    pub fn new(db: Arc<RwLock<Option<DatabaseManager>>>) -> Self {
        Self { db }
    }

    /// Initialize calendar tables in database
    pub async fn initialize_schema(&self) -> Result<(), String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        // Create calendar events table
        sqlx::query(
            r#"
            CREATE TABLE IF NOT EXISTS alden_calendar_events (
                id TEXT PRIMARY KEY NOT NULL,
                user_id TEXT NOT NULL,
                title TEXT NOT NULL,
                description TEXT,
                start_time INTEGER NOT NULL,
                end_time INTEGER NOT NULL,
                location TEXT,
                attendees TEXT,
                calendar_id TEXT NOT NULL,
                event_type TEXT NOT NULL DEFAULT 'meeting',
                recurrence_rule TEXT,
                reminder_minutes INTEGER,
                is_all_day INTEGER NOT NULL DEFAULT 0,
                external_id TEXT,
                created_at INTEGER NOT NULL,
                updated_at INTEGER NOT NULL
            )
        "#,
        )
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to create alden_calendar_events table: {e}"))?;

        // Create indexes
        let _ = sqlx::query("CREATE INDEX IF NOT EXISTS idx_calendar_user_time ON alden_calendar_events(user_id, start_time)")
            .execute(&db.write_pool)
            .await;

        let _ = sqlx::query("CREATE INDEX IF NOT EXISTS idx_calendar_external ON alden_calendar_events(external_id)")
            .execute(&db.write_pool)
            .await;

        println!("[Alden] Calendar management schema initialized");
        Ok(())
    }

    /// Create a new calendar event
    pub async fn create_event(&self, request: CreateEventRequest) -> Result<CalendarEvent, String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        let event_id = Uuid::new_v4().to_string();
        let now = chrono::Utc::now().timestamp();

        let attendees_json = request
            .attendees
            .as_ref()
            .map(|a| serde_json::to_string(a).unwrap_or_else(|_| "[]".to_string()));

        let event = sqlx::query_as::<_, CalendarEvent>(
            r#"
            INSERT INTO alden_calendar_events (
                id, user_id, title, description, start_time, end_time,
                location, attendees, calendar_id, event_type, recurrence_rule,
                reminder_minutes, is_all_day, external_id, created_at, updated_at
            )
            VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, NULL, ?, ?)
            RETURNING *
        "#,
        )
        .bind(&event_id)
        .bind(&request.user_id)
        .bind(&request.title)
        .bind(&request.description)
        .bind(request.start_time)
        .bind(request.end_time)
        .bind(&request.location)
        .bind(attendees_json)
        .bind(&request.calendar_id)
        .bind(request.event_type.unwrap_or_else(|| "meeting".to_string()))
        .bind(&request.recurrence_rule)
        .bind(request.reminder_minutes)
        .bind(request.is_all_day.unwrap_or(false))
        .bind(now)
        .bind(now)
        .fetch_one(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to create calendar event: {e}"))?;

        Ok(event)
    }

    /// Get calendar events within a time range
    pub async fn get_events(
        &self,
        user_id: String,
        start_time: Option<i64>,
        end_time: Option<i64>,
        calendar_ids: Option<Vec<String>>,
    ) -> Result<Vec<CalendarEvent>, String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        let mut query = String::from("SELECT * FROM alden_calendar_events WHERE user_id = ?");
        let mut params: Vec<String> = vec![user_id];

        if let Some(start) = start_time {
            query.push_str(" AND end_time >= ?");
            params.push(start.to_string());
        }

        if let Some(end) = end_time {
            query.push_str(" AND start_time <= ?");
            params.push(end.to_string());
        }

        if let Some(cal_ids) = calendar_ids {
            let placeholders = cal_ids.iter().map(|_| "?").collect::<Vec<_>>().join(",");
            query.push_str(&format!(" AND calendar_id IN ({placeholders})"));
            params.extend(cal_ids);
        }

        query.push_str(" ORDER BY start_time ASC");

        let mut sql_query = sqlx::query_as::<_, CalendarEvent>(&query);
        for param in params {
            sql_query = sql_query.bind(param);
        }

        let events = sql_query
            .fetch_all(&db.read_pool)
            .await
            .map_err(|e| format!("Failed to fetch calendar events: {e}"))?;

        Ok(events)
    }

    /// Update a calendar event
    pub async fn update_event(&self, request: UpdateEventRequest) -> Result<CalendarEvent, String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        let now = chrono::Utc::now().timestamp();
        let mut updates = vec!["updated_at = ?"];
        let mut params: Vec<String> = vec![now.to_string()];

        if let Some(t) = request.title {
            updates.push("title = ?");
            params.push(t);
        }

        if let Some(d) = request.description {
            updates.push("description = ?");
            params.push(d);
        }

        if let Some(st) = request.start_time {
            updates.push("start_time = ?");
            params.push(st.to_string());
        }

        if let Some(et) = request.end_time {
            updates.push("end_time = ?");
            params.push(et.to_string());
        }

        if let Some(l) = request.location {
            updates.push("location = ?");
            params.push(l);
        }

        let query = format!(
            "UPDATE alden_calendar_events SET {} WHERE id = ? AND user_id = ? RETURNING *",
            updates.join(", ")
        );

        params.push(request.event_id);
        params.push(request.user_id);

        let mut sql_query = sqlx::query_as::<_, CalendarEvent>(&query);
        for param in params {
            sql_query = sql_query.bind(param);
        }

        let event = sql_query
            .fetch_one(&db.write_pool)
            .await
            .map_err(|e| format!("Failed to update calendar event: {e}"))?;

        Ok(event)
    }

