    pub async fn delete_event(&self, event_id: String, user_id: String) -> Result<(), String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        sqlx::query("DELETE FROM alden_calendar_events WHERE id = ? AND user_id = ?")
