use serde::{Deserialize, Serialize};
use std::process::Command;
use sysinfo::System;

#[derive(Debug, Serialize, Deserialize)]
pub struct MemoryInfo {
    pub total: u64,
    pub available: u64,
}

#[derive(Debug, Serialize, Deserialize)]
pub struct CpuInfo {
    pub cores: usize,
}

/// Get system memory information
#[tauri::command]
pub fn get_memory_info() -> Result<MemoryInfo, String> {
    let mut sys = System::new_all();
    sys.refresh_all();

    Ok(MemoryInfo {
        total: sys.total_memory(),
        available: sys.available_memory(),
    })
}

/// Get CPU information
#[tauri::command]
pub fn get_cpu_info() -> Result<CpuInfo, String> {
    let mut sys = System::new_all();
    sys.refresh_cpu();

    Ok(CpuInfo {
        cores: sys.cpus().len(),
    })
}

#[derive(Debug, Serialize, Deserialize)]
pub struct GpuInfo {
    pub name: String,
    pub vram_total_mb: u64,
}

/// Get GPU information (Best effort)
#[tauri::command]
pub fn get_gpu_info() -> Result<Vec<GpuInfo>, String> {
    let mut gpus = Vec::new();

    #[cfg(target_os = "windows")]
    {
        // Try nvidia-smi first
        if let Ok(output) = Command::new("nvidia-smi")
            .args([
                "--query-gpu=name,memory.total",
                "--format=csv,noheader,nounits",
            ])
            .output()
        {
            if output.status.success() {
                let stdout = String::from_utf8_lossy(&output.stdout);
                for line in stdout.lines() {
                    let parts: Vec<&str> = line.split(',').collect();
                    if parts.len() >= 2 {
                        let name = parts[0].trim().to_string();
                        let vram = parts[1].trim().parse::<u64>().unwrap_or(0);
                        gpus.push(GpuInfo {
                            name,
                            vram_total_mb: vram,
                        });
                    }
                }
            }
        }
    }

    // If no GPUs found (or not Windows/Nvidia), return empty or partial
    // Ideally we would optionally use something like `wgpu` or `vulkano` here but that adds dependencies.
    // This is sufficient for the "System Read-only panel" draft requirement.
    Ok(gpus)
}

/// Get platform information
#[tauri::command]
pub fn get_platform() -> String {
    std::env::consts::OS.to_string()
}

/// Check if Ollama is installed
#[tauri::command]
pub fn check_ollama_installed() -> Result<bool, String> {
    #[cfg(target_os = "windows")]
    {
        // Check if ollama.exe exists in PATH or common installation paths
        let check_path = Command::new("where").arg("ollama").output();

        match check_path {
            Ok(output) => Ok(output.status.success()),
            Err(_) => {
                // Check common installation paths
                let userprofile_path = format!(
                    "{}\\AppData\\Local\\Programs\\Ollama\\ollama.exe",
                    std::env::var("USERPROFILE").unwrap_or_default()
                );
                let common_paths = vec![
                    "C:\\Program Files\\Ollama\\ollama.exe",
                    "C:\\Program Files (x86)\\Ollama\\ollama.exe",
                    userprofile_path.as_str(),
                ];

                for path in common_paths {
                    if std::path::Path::new(&path).exists() {
                        return Ok(true);
                    }
                }

                Ok(false)
            }
        }
    }

    #[cfg(target_os = "macos")]
    {
        let check_path = Command::new("which").arg("ollama").output();

        match check_path {
            Ok(output) => Ok(output.status.success()),
            Err(_) => Ok(false),
        }
    }

    #[cfg(target_os = "linux")]
    {
        let check_path = Command::new("which").arg("ollama").output();

        match check_path {
            Ok(output) => Ok(output.status.success()),
            Err(_) => Ok(false),
        }
    }
}

/// Get Ollama installation path
#[tauri::command]
pub fn get_ollama_path() -> Result<String, String> {
    #[cfg(target_os = "windows")]
    {
        let output = Command::new("where")
            .arg("ollama")
            .output()
            .map_err(|e| format!("Failed to locate ollama: {e}"))?;

        if output.status.success() {
            let path = String::from_utf8_lossy(&output.stdout)
                .lines()
                .next()
                .unwrap_or("")
                .to_string();
            Ok(path)
        } else {
            Err("Ollama not found in PATH".to_string())
        }
    }

    #[cfg(not(target_os = "windows"))]
    {
        let output = Command::new("which")
            .arg("ollama")
            .output()
            .map_err(|e| format!("Failed to locate ollama: {}", e))?;

        if output.status.success() {
            let path = String::from_utf8_lossy(&output.stdout).trim().to_string();
            Ok(path)
        } else {
            Err("Ollama not found in PATH".to_string())
        }
    }
}

/// Download and install Ollama
#[tauri::command]
pub async fn download_ollama() -> Result<(), String> {
    #[cfg(target_os = "windows")]
    {
        // Download Ollama installer for Windows
        let download_url = "https://ollama.com/download/OllamaSetup.exe";
        let temp_dir = std::env::temp_dir();
        let installer_path = temp_dir.join("OllamaSetup.exe");

        // Download installer
        let response = reqwest::get(download_url)
            .await
            .map_err(|e| format!("Failed to download Ollama: {e}"))?;

        let bytes = response
            .bytes()
            .await
            .map_err(|e| format!("Failed to read download: {e}"))?;

        std::fs::write(&installer_path, bytes)
            .map_err(|e| format!("Failed to save installer: {e}"))?;

        // Run installer silently
        let install_result = Command::new(&installer_path)
            .arg("/S") // Silent install
            .spawn()
            .map_err(|e| format!("Failed to run installer: {e}"))?
            .wait()
            .map_err(|e| format!("Failed to wait for installer: {e}"))?;

        if !install_result.success() {
            return Err("Installation failed".to_string());
        }

        // Clean up installer
        let _ = std::fs::remove_file(&installer_path);

        Ok(())
    }

    #[cfg(target_os = "macos")]
    {
        // On macOS, open browser to download page
        let _ = Command::new("open")
            .arg("https://ollama.com/download")
            .spawn();

        Err("Please download and install Ollama from the browser".to_string())
    }

    #[cfg(target_os = "linux")]
    {
        // Use curl to install Ollama on Linux
        let install_result = Command::new("curl")
            .arg("-fsSL")
            .arg("https://ollama.com/install.sh")
            .stdout(std::process::Stdio::piped())
            .spawn()
            .and_then(|child| {
                let output = child.stdout.expect("Failed to get stdout");
                Command::new("sh")
                    .stdin(output)
                    .spawn()
                    .and_then(|mut sh_child| sh_child.wait())
            });

        match install_result {
            Ok(status) if status.success() => Ok(()),
            Ok(_) => Err("Installation script failed".to_string()),
            Err(e) => Err(format!("Failed to install Ollama: {}", e)),
        }
    }
}

/// Download a specific Ollama model
#[tauri::command]
pub async fn download_ollama_model(model_name: String) -> Result<(), String> {
    let output = Command::new("ollama")
        .arg("pull")
        .arg(&model_name)
        .output()
        .map_err(|e| format!("Failed to download model: {e}"))?;

    if output.status.success() {
        Ok(())
    } else {
        let error = String::from_utf8_lossy(&output.stderr);
        Err(format!("Failed to download model {model_name}: {error}"))
    }
}

/// List installed Ollama models
#[tauri::command]
pub fn list_ollama_models() -> Result<Vec<String>, String> {
    let output = Command::new("ollama")
        .arg("list")
        .output()
        .map_err(|e| format!("Failed to list models: {e}"))?;

    if !output.status.success() {
        return Err("Failed to list models".to_string());
    }

    let stdout = String::from_utf8_lossy(&output.stdout);
    let models: Vec<String> = stdout
        .lines()
        .skip(1) // Skip header
        .filter_map(|line| line.split_whitespace().next().map(|s| s.to_string()))
        .collect();

    Ok(models)
}

/// Check Ollama service status
#[tauri::command]
pub fn check_ollama_running() -> Result<bool, String> {
    let output = Command::new("ollama").arg("list").output();

    match output {
        Ok(result) => Ok(result.status.success()),
        Err(_) => Ok(false),
    }
}

/// Start Ollama service
#[tauri::command]
pub fn start_ollama_service() -> Result<(), String> {
    #[cfg(target_os = "windows")]
    {
        Command::new("ollama")
            .arg("serve")
            .spawn()
            .map_err(|e| format!("Failed to start Ollama service: {e}"))?;
        Ok(())
    }

    #[cfg(target_os = "macos")]
    {
        Command::new("ollama")
            .arg("serve")
            .spawn()
            .map_err(|e| format!("Failed to start Ollama service: {}", e))?;
        Ok(())
    }

    #[cfg(target_os = "linux")]
    {
        // On Linux, try to start via systemd first
        let systemd_result = Command::new("systemctl")
            .arg("--user")
            .arg("start")
            .arg("ollama")
            .output();

        match systemd_result {
            Ok(output) if output.status.success() => return Ok(()),
            _ => {
                // Fallback to direct execution
                Command::new("ollama")
                    .arg("serve")
                    .spawn()
                    .map_err(|e| format!("Failed to start Ollama service: {}", e))?;
                Ok(())
            }
        }
    }
}

