use tauri::State;

pub struct SentryAuthState {
    pub auth_manager: Arc<SentryAuthManager>,
}

/// Get current session
#[tauri::command]
pub async fn sentry_auth_get_session(
    state: State<'_, SentryAuthState>,
) -> Result<Option<UserSession>, String> {
    Ok(state.auth_manager.get_session().await)
}

/// Login user
#[tauri::command]
pub async fn sentry_auth_login(
    request: LoginRequest,
    state: State<'_, SentryAuthState>,
) -> Result<LoginResponse, String> {
    state
        .auth_manager
        .login(&request.username, &request.password)
        .await
}

/// Logout current user
#[tauri::command]
pub async fn sentry_auth_logout(state: State<'_, SentryAuthState>) -> Result<(), String> {
    state.auth_manager.logout().await
}

/// Verify session token
#[tauri::command]
pub async fn sentry_auth_verify_session(
    auth_token: String,
    state: State<'_, SentryAuthState>,
) -> Result<UserSession, String> {
    state.auth_manager.verify_session(&auth_token).await
}

/// Register a new user (call this before login in production)
/// Auto-login with default user (development only)
#[cfg(debug_assertions)]
#[tauri::command]
pub async fn sentry_auth_auto_login(
    state: State<'_, SentryAuthState>,
) -> Result<UserSession, String> {
    state.auth_manager.auto_login_default().await
}

#[cfg(not(debug_assertions))]
#[tauri::command]
pub async fn sentry_auth_auto_login(_: State<'_, SentryAuthState>) -> Result<UserSession, String> {
    Err("Auto-login disabled in production".to_string())
}

/// Get current user ID
