/// Download and install Python
#[tauri::command]
pub async fn download_and_install_python() -> Result<(), String> {
    #[cfg(target_os = "windows")]
    {
        let download_url = "https://www.python.org/ftp/python/3.11.9/python-3.11.9-amd64.exe";
        let installer_path = std::env::temp_dir().join("python_installer.exe");

        // Download Python installer
        let response = reqwest::get(download_url)
            .await
            .map_err(|e| format!("Failed to download Python: {e}"))?;

        let bytes = response
            .bytes()
            .await
            .map_err(|e| format!("Failed to read Python installer: {e}"))?;

        fs::write(&installer_path, bytes)
            .map_err(|e| format!("Failed to save Python installer: {e}"))?;

        // Run silent installation
        let output = Command::new(&installer_path)
            .args([
                "/quiet",
                "InstallAllUsers=0",
                "PrependPath=1",
                "Include_test=0",
            ])
            .output()
            .map_err(|e| format!("Failed to run Python installer: {e}"))?;

        if !output.status.success() {
            return Err("Python installation failed".to_string());
        }

        // Clean up installer
        let _ = fs::remove_file(&installer_path);

        Ok(())
    }

    #[cfg(target_os = "macos")]
    {
        // On macOS, open Python download page for user to install
        let download_url = "https://www.python.org/downloads/macos/";
        Command::new("open")
            .arg(download_url)
            .spawn()
            .map_err(|e| format!("Failed to open Python download page: {}", e))?;

        Err("Please download and install Python from the opened page".to_string())
    }

    #[cfg(target_os = "linux")]
    {
        // On Linux, try to install via package manager
        if Command::new("which").arg("apt-get").output().is_ok() {
            let output = Command::new("pkexec")
                .args(&["apt-get", "install", "-y", "python3", "python3-pip"])
                .output()
                .map_err(|e| format!("Failed to install Python: {}", e))?;

            if output.status.success() {
                return Ok(());
            }
        }

        Err("Please install Python 3 using your system's package manager".to_string())
    }
}

