/// Create the Hearthlink data directory
#[tauri::command]
pub fn create_data_directory(path: String) -> Result<(), String> {
    let data_dir = PathBuf::from(&path);

    if !data_dir.exists() {
        fs::create_dir_all(&data_dir)
            .map_err(|e| format!("Failed to create data directory: {e}"))?;
    }

    // Create subdirectories
    let subdirs = vec!["database", "logs", "cache", "config"];
    for subdir in subdirs {
        let subdir_path = data_dir.join(subdir);
        if !subdir_path.exists() {
            fs::create_dir_all(&subdir_path)
                .map_err(|e| format!("Failed to create {subdir} directory: {e}"))?;
        }
    }

    Ok(())
}

/// Initialize the SQLite database
#[tauri::command]
pub async fn initialize_database(data_dir: String) -> Result<String, String> {
    let db_path = PathBuf::from(&data_dir)
        .join("database")
        .join("hearthlink.db");

    let db_url = format!("sqlite:{}", db_path.to_string_lossy());

    // Create database file if it doesn't exist
    if !db_path.exists() {
        fs::File::create(&db_path).map_err(|e| format!("Failed to create database file: {e}"))?;
    }

    Ok(db_url)
}

/// Create database schema
#[tauri::command]
pub async fn create_database_schema(db_url: String) -> Result<(), String> {
    use sqlx::sqlite::SqlitePoolOptions;

    let pool = SqlitePoolOptions::new()
        .max_connections(5)
        .connect(&db_url)
        .await
        .map_err(|e| format!("Failed to connect to database: {e}"))?;

    // Create tables
    let schema = r#"
        CREATE TABLE IF NOT EXISTS users (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            display_name TEXT NOT NULL,
            primary_persona TEXT NOT NULL,
            created_at TEXT NOT NULL,
            updated_at TEXT NOT NULL
        );

        CREATE TABLE IF NOT EXISTS conversations (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            user_id INTEGER NOT NULL,
            agent_id TEXT NOT NULL,
            title TEXT,
            created_at TEXT NOT NULL,
            updated_at TEXT NOT NULL,
            FOREIGN KEY (user_id) REFERENCES users(id)
        );

        CREATE TABLE IF NOT EXISTS messages (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            conversation_id INTEGER NOT NULL,
            role TEXT NOT NULL,
            content TEXT NOT NULL,
            created_at TEXT NOT NULL,
            FOREIGN KEY (conversation_id) REFERENCES conversations(id)
        );

        CREATE TABLE IF NOT EXISTS tasks (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            user_id INTEGER NOT NULL,
            title TEXT NOT NULL,
            description TEXT,
            status TEXT NOT NULL DEFAULT 'pending',
            priority INTEGER NOT NULL DEFAULT 0,
            due_date TEXT,
            created_at TEXT NOT NULL,
            updated_at TEXT NOT NULL,
            FOREIGN KEY (user_id) REFERENCES users(id)
        );

        CREATE TABLE IF NOT EXISTS agent_configs (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            agent_id TEXT NOT NULL UNIQUE,
            config_json TEXT NOT NULL,
            created_at TEXT NOT NULL,
            updated_at TEXT NOT NULL
        );

        CREATE TABLE IF NOT EXISTS llm_configs (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            provider TEXT NOT NULL,
            model TEXT,
            api_key TEXT,
            api_key_ciphertext BLOB,
            api_key_nonce BLOB,
            api_key_key_version INTEGER,
            base_url TEXT,
            is_default INTEGER NOT NULL DEFAULT 0,
            created_at TEXT NOT NULL,
            updated_at TEXT NOT NULL
        );

        CREATE TABLE IF NOT EXISTS system_settings (
            key TEXT PRIMARY KEY,
            value TEXT NOT NULL,
            updated_at TEXT NOT NULL
        );
    "#;

    sqlx::query(schema)
        .execute(&pool)
        .await
        .map_err(|e| format!("Failed to create database schema: {e}"))?;

    pool.close().await;

    Ok(())
}

/// Check database health
#[tauri::command]
pub async fn check_database_health(db_url: String) -> Result<DatabaseHealth, String> {
    use sqlx::sqlite::SqlitePoolOptions;

    let pool = SqlitePoolOptions::new()
        .max_connections(1)
        .connect(&db_url)
        .await
        .map_err(|e| format!("Failed to connect to database: {e}"))?;

    // Check if tables exist
    let table_count: i64 = sqlx::query_scalar(
        "SELECT COUNT(*) FROM sqlite_master WHERE type='table' AND name NOT LIKE 'sqlite_%'",
    )
    .fetch_one(&pool)
    .await
    .map_err(|e| format!("Failed to query database: {e}"))?;

    let db_path = db_url.replace("sqlite:", "");
    let metadata =
        fs::metadata(&db_path).map_err(|e| format!("Failed to get database file info: {e}"))?;

    pool.close().await;

    Ok(DatabaseHealth {
        healthy: table_count >= 7, // We expect 7 tables
        path: db_path,
        size_bytes: metadata.len(),
        tables_created: table_count >= 7,
    })
}

/// Save first-run configuration
#[tauri::command]
pub async fn save_first_run_config(db_url: String, config: FirstRunConfig) -> Result<(), String> {
    use sqlx::sqlite::SqlitePoolOptions;

    let pool = SqlitePoolOptions::new()
        .max_connections(1)
        .connect(&db_url)
        .await
        .map_err(|e| format!("Failed to connect to database: {e}"))?;

    let now = chrono::Utc::now().to_rfc3339();

    let (api_key_ciphertext, api_key_nonce, api_key_version) =
        if let Some(key_value) = config.api_key.as_deref() {
            let (ciphertext, nonce, version) = encrypt_api_key_value(key_value).await?;
            (Some(ciphertext), Some(nonce), Some(version))
        } else {
            (None, None, None)
        };

    // Create user record
    sqlx::query(
        "INSERT INTO users (display_name, primary_persona, created_at, updated_at) VALUES (?, ?, ?, ?)"
    )
    .bind(&config.display_name)
    .bind(&config.primary_persona)
    .bind(&now)
    .bind(&now)
    .execute(&pool)
    .await
    .map_err(|e| format!("Failed to create user record: {e}"))?;

    // Save LLM config (save regardless of whether api_key exists, as user might use local-only)
    sqlx::query(
        "INSERT INTO llm_configs \
         (provider, model, api_key, api_key_ciphertext, api_key_nonce, api_key_key_version, base_url, is_default, created_at, updated_at) \
         VALUES (?, ?, ?, ?, ?, ?, ?, 1, ?, ?)"
    )
    .bind(&config.llm_provider)
    .bind(&config.llm_model)
    .bind::<Option<String>>(None)
    .bind(api_key_ciphertext)
    .bind(api_key_nonce)
    .bind(api_key_version)
    .bind(&config.api_endpoint)
    .bind(&now)
    .bind(&now)
    .execute(&pool)
    .await
    .map_err(|e| format!("Failed to save LLM config: {e}"))?;

    // Mark first run as complete
    sqlx::query(
        "INSERT OR REPLACE INTO system_settings (key, value, updated_at) VALUES ('first_run_complete', 'true', ?)"
    )
    .bind(&now)
    .execute(&pool)
    .await
    .map_err(|e| format!("Failed to mark first run complete: {e}"))?;

    pool.close().await;

    Ok(())
}

async fn encrypt_api_key_value(api_key: &str) -> Result<(Vec<u8>, Vec<u8>, i64), String> {
    let key_manager = SecureKeyManager::new();
    let key = key_manager.get_vault_key().await?;

    if key.len() != 32 {
        return Err(format!("Invalid vault key length: {}", key.len()));
    }

    let cipher = Aes256Gcm::new_from_slice(&key).map_err(|e| format!("Invalid key length: {e}"))?;
    let mut nonce_bytes = [0u8; 12];
    OsRng.fill_bytes(&mut nonce_bytes);
    let nonce = Nonce::from_slice(&nonce_bytes);
    let ciphertext = cipher
        .encrypt(nonce, api_key.as_bytes())
        .map_err(|e| format!("Failed to encrypt API key: {e}"))?;

    Ok((ciphertext, nonce_bytes.to_vec(), 1))
}

/// Check if first run is complete
#[tauri::command]
pub async fn is_first_run_complete(db_url: String) -> Result<bool, String> {
    use sqlx::sqlite::SqlitePoolOptions;

    // First check if database exists
    let db_path = db_url.replace("sqlite:", "");
    if !PathBuf::from(&db_path).exists() {
        return Ok(false);
    }

    let pool = SqlitePoolOptions::new()
        .max_connections(1)
        .connect(&db_url)
        .await
        .map_err(|e| format!("Failed to connect to database: {e}"))?;

    let result: Option<String> =
        sqlx::query_scalar("SELECT value FROM system_settings WHERE key = 'first_run_complete'")
            .fetch_optional(&pool)
            .await
            .map_err(|e| format!("Failed to check first run status: {e}"))?;

    pool.close().await;

    Ok(result == Some("true".to_string()))
}

/// Perform complete system check
#[tauri::command]
pub async fn perform_system_check(app_handle: tauri::AppHandle) -> Result<SystemCheck, String> {
    let python_installed = check_python_installed()?;
    let python_version = if python_installed {
        get_python_version().ok()
    } else {
        None
    };

    let app_data = get_app_data_directory(app_handle)?;
    let db_path = PathBuf::from(&app_data)
        .join("database")
        .join("hearthlink.db");
    let config_path = PathBuf::from(&app_data)
        .join("config")
        .join("settings.json");

    Ok(SystemCheck {
        python_installed,
        python_version,
        database_exists: db_path.exists(),
        config_exists: config_path.exists(),
    })
}

