use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::sync::Arc;
use tauri::State;
use tokio::sync::RwLock;

/// Stream metadata for tracking stream information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct StreamMetadata {
    pub stream_id: String,
    pub stream_type: StreamType,
    pub created_at: String,
    pub bytes_transferred: u64,
    pub active: bool,
    pub chunk_size: u32,
    pub total_chunks: u32,
}

/// Types of streams supported
#[derive(Debug, Clone, Serialize, Deserialize)]
#[serde(rename_all = "lowercase")]
pub enum StreamType {
    Avatar,
    Audio,
    Data,
    Event,
}

/// Stream chunk for data transfer
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct StreamChunk {
    pub stream_id: String,
    pub chunk_index: u32,
    pub data: Vec<u8>,
    pub metadata: Option<HashMap<String, String>>,
    pub timestamp: String,
}

/// Stream status information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct StreamStatus {
    pub active: bool,
    pub bytes_transferred: u64,
    pub chunks_completed: u32,
    pub total_chunks: u32,
    pub error: Option<String>,
}

/// Streaming manager for handling active streams
pub struct StreamingManager {
    streams: Arc<RwLock<HashMap<String, StreamMetadata>>>,
}

impl StreamingManager {
    pub fn new() -> Self {
        Self {
            streams: Arc::new(RwLock::new(HashMap::new())),
        }
    }

    /// Create a new stream with metadata
    pub async fn create_stream(
        &self,
        stream_id: String,
        stream_type: StreamType,
        chunk_size: u32,
        total_chunks: u32,
    ) -> Result<StreamMetadata, String> {
        let metadata = StreamMetadata {
            stream_id: stream_id.clone(),
            stream_type,
            created_at: chrono::Utc::now().to_rfc3339(),
            bytes_transferred: 0,
            active: true,
            chunk_size,
            total_chunks,
        };

        let mut streams = self.streams.write().await;
        streams.insert(stream_id, metadata.clone());

        Ok(metadata)
    }

    /// Close a stream and clean up resources
    pub async fn close_stream(&self, stream_id: &str) -> Result<StreamStatus, String> {
        let mut streams = self.streams.write().await;

        if let Some(mut metadata) = streams.remove(stream_id) {
            metadata.active = false;

            let status = StreamStatus {
                active: false,
                bytes_transferred: metadata.bytes_transferred,
                chunks_completed: metadata.total_chunks, // Assume all chunks completed
                total_chunks: metadata.total_chunks,
                error: None,
            };

            Ok(status)
        } else {
            Err(format!("Stream {stream_id} not found"))
        }
    }

    /// Get stream status
    pub async fn get_stream_status(&self, stream_id: &str) -> Result<StreamStatus, String> {
        let streams = self.streams.read().await;

        if let Some(metadata) = streams.get(stream_id) {
            Ok(StreamStatus {
                active: metadata.active,
                bytes_transferred: metadata.bytes_transferred,
                chunks_completed: 0, // TODO: Track completed chunks
                total_chunks: metadata.total_chunks,
                error: None,
            })
        } else {
            Err(format!("Stream {stream_id} not found"))
        }
    }

    /// Process incoming stream chunk
    pub async fn process_chunk(&self, chunk: StreamChunk) -> Result<(), String> {
        let mut streams = self.streams.write().await;

        if let Some(metadata) = streams.get_mut(&chunk.stream_id) {
            metadata.bytes_transferred += chunk.data.len() as u64;
            Ok(())
        } else {
            Err(format!("Stream {} not found", chunk.stream_id))
        }
    }
}

/// State for streaming manager
pub struct StreamingState {
    pub manager: Arc<StreamingManager>,
}

impl StreamingState {
    pub fn new() -> Self {
        Self {
            manager: Arc::new(StreamingManager::new()),
        }
    }
}

/// Create a new stream
#[tauri::command]
pub async fn streaming_create_stream(
    stream_type: StreamType,
    chunk_size: u32,
    total_chunks: u32,
    state: State<'_, StreamingState>,
) -> Result<StreamMetadata, String> {
    let stream_id = format!(
        "stream_{}_{}",
        stream_type,
        chrono::Utc::now().timestamp_millis()
    );

    state
        .manager
        .create_stream(stream_id.clone(), stream_type, chunk_size, total_chunks)
        .await
}

/// Close an existing stream
#[tauri::command]
pub async fn streaming_close_stream(
    stream_id: String,
    state: State<'_, StreamingState>,
) -> Result<StreamStatus, String> {
    state.manager.close_stream(&stream_id).await
}

/// Get status of a stream
#[tauri::command]
pub async fn streaming_get_status(
    stream_id: String,
    state: State<'_, StreamingState>,
) -> Result<StreamStatus, String> {
    state.manager.get_stream_status(&stream_id).await
}

/// Stream avatar data
#[tauri::command]
pub async fn streaming_stream_avatar(
    audio_data: Vec<u8>,
    metadata: Option<HashMap<String, String>>,
    state: State<'_, StreamingState>,
) -> Result<String, String> {
    // Create avatar stream
    let stream_id = format!("avatar_{}", chrono::Utc::now().timestamp_millis());

    let stream_metadata = state
        .manager
        .create_stream(
            stream_id.clone(),
            StreamType::Avatar,
            1024, // 1KB chunks for avatar data
            (audio_data.len() / 1024 + 1) as u32,
        )
        .await?;

    // Process audio data in chunks
    let chunk_size = stream_metadata.chunk_size as usize;
    for (i, chunk) in audio_data.chunks(chunk_size).enumerate() {
        let stream_chunk = StreamChunk {
            stream_id: stream_id.clone(),
            chunk_index: i as u32,
            data: chunk.to_vec(),
            metadata: metadata.clone(),
            timestamp: chrono::Utc::now().to_rfc3339(),
        };

        state.manager.process_chunk(stream_chunk).await?;
    }

    // Close the stream
    state.manager.close_stream(&stream_id).await?;

    Ok(stream_id)
}

/// Stream generic data in chunks
#[tauri::command]
pub async fn streaming_stream_data(
    data: Vec<u8>,
    chunk_size: u32,
    stream_id: Option<String>,
    state: State<'_, StreamingState>,
) -> Result<String, String> {
    let stream_id =
        stream_id.unwrap_or_else(|| format!("data_{}", chrono::Utc::now().timestamp_millis()));

    state
        .manager
        .create_stream(
            stream_id.clone(),
            StreamType::Data,
            chunk_size,
            (data.len() / chunk_size as usize + 1) as u32,
        )
        .await?;

    // Process data in chunks
    let chunk_size = chunk_size as usize;

    for (i, chunk) in data.chunks(chunk_size).enumerate() {
        let stream_chunk = StreamChunk {
            stream_id: stream_id.clone(),
            chunk_index: i as u32,
            data: chunk.to_vec(),
            metadata: None,
            timestamp: chrono::Utc::now().to_rfc3339(),
        };

        state.manager.process_chunk(stream_chunk).await?;
    }

    // Close the stream
    state.manager.close_stream(&stream_id).await?;

    Ok(stream_id)
}

/// Get list of active streams
#[tauri::command]
pub async fn streaming_list_active(
    state: State<'_, StreamingState>,
) -> Result<Vec<StreamMetadata>, String> {
    let streams = state.manager.streams.read().await;
    Ok(streams.values().cloned().collect())
}

/// Clean up inactive streams
#[tauri::command]
pub async fn streaming_cleanup(
    older_than_ms: u64,
    state: State<'_, StreamingState>,
) -> Result<usize, String> {
    let mut streams = state.manager.streams.write().await;
    let cutoff_time = chrono::Utc::now().timestamp_millis() - older_than_ms as i64;

    let initial_count = streams.len();
    streams.retain(|_, metadata| {
        metadata
            .created_at
            .parse::<chrono::DateTime<chrono::Utc>>()
            .map(|dt| dt.timestamp_millis() > cutoff_time)
            .unwrap_or(true)
    });

    let cleaned_count = initial_count - streams.len();
    Ok(cleaned_count)
}

impl std::fmt::Display for StreamType {
    fn fmt(&self, f: &mut std::fmt::Formatter<'_>) -> std::fmt::Result {
        match self {
            StreamType::Avatar => write!(f, "avatar"),
            StreamType::Audio => write!(f, "audio"),
            StreamType::Data => write!(f, "data"),
            StreamType::Event => write!(f, "event"),
        }
    }
}

