   * Delay helper for retry logic
   */
  protected delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * Validate response data against expected schema
   */
  protected validateResponse<T>(data: unknown, requiredFields: string[]): T {
    if (typeof data !== 'object' || data === null) {
      throw new Error('Invalid response type');
    }
    for (const field of requiredFields) {
      if (!(field in data)) {
        throw new Error(`Missing required field: ${field}`);
      }
    }
    return data as T;
  }

  /**
   * Create a standardized error response
   */
  protected createErrorResponse<T>(error: unknown, fallback?: string, code?: string): BridgeResponse<T> {
    let message: string;
    if (error instanceof Error) {
      message = error.message;
    } else if (typeof error === 'string') {
      message = error;
    } else if (typeof error === 'object' && error !== null && 'message' in error) {
      const maybeMessage = (error as { message?: unknown }).message;
      message = typeof maybeMessage === 'string' ? maybeMessage : (fallback || 'Unknown error');
    } else {
      message = fallback || 'Unknown error';
    }

    return {
      success: false,
      error: message,
      code: code || 'BRIDGE_ERROR',
    };
  }
}

/**
 * Bridge Factory
 * Creates and initializes agent bridges
 */
export class BridgeFactory {
  private static bridges: Map<AgentId, AgentBridge> = new Map();
  private static bridgeRefs: Map<AgentId, number> = new Map();

  static async createBridge<TProps>(
    Bridge: new (config: BridgeConfig) => AgentBridge<TProps>,
    agentId: AgentId,
    userId = 'default_user' // TODO: Replace with actual user ID from auth system
  ): Promise<AgentBridge<TProps>> {
    // Return existing bridge if already created
    if (this.bridges.has(agentId)) {
      const refCount = this.bridgeRefs.get(agentId) ?? 0;
      this.bridgeRefs.set(agentId, refCount + 1);
      return this.bridges.get(agentId) as AgentBridge<TProps>;
    }

    // Create and initialize new bridge
    const bridge = new Bridge({ agentId, userId });
    await bridge.initialize();
    this.bridges.set(agentId, bridge);
    this.bridgeRefs.set(agentId, 1);

    return bridge;
  }

  static async destroyBridge(agentId: AgentId): Promise<void> {
    const refCount = this.bridgeRefs.get(agentId) ?? 0;

    if (refCount > 1) {
      this.bridgeRefs.set(agentId, refCount - 1);
      return;
    }

    this.bridgeRefs.delete(agentId);

    const bridge = this.bridges.get(agentId);
    if (bridge) {
      this.bridges.delete(agentId);
      await bridge.destroy();
    }
  }

  static async destroyAll(): Promise<void> {
    const promises = Array.from(this.bridges.keys()).map(agentId => this.destroyBridge(agentId));
    await Promise.all(promises);
    this.bridgeRefs.clear();
  }

  static getBridge(agentId: AgentId): AgentBridge | undefined {
    return this.bridges.get(agentId);
  }
}

