        user_id: String,
        persona_id: String,
        auth_token: String,
    ) -> Result<VaultResponse, String> {
        // Authenticate user
        let authenticated_user_id = self.authenticator.verify_token(&auth_token).await?;

        // Use constant-time comparison
        if !self
            .authenticator
            .verify_user_match(&authenticated_user_id, &user_id)
        {
            return Ok(VaultResponse {
                success: false,
                message: "Authentication failed: user mismatch".to_string(),
                data: None,
                entry_id: None,
            });
        }

        let db_guard = self.db_manager.read().await;
        let db_manager = db_guard.as_ref().ok_or("Database not initialized")?;

        // Delete the entry
        let result = sqlx::query(
            r#"
            DELETE FROM vault_entries
            WHERE user_id = ?1 AND persona_id = ?2
        "#,
        )
        .bind(&user_id)
        .bind(&persona_id)
        .execute(&db_manager.write_pool)
        .await
        .map_err(|e| format!("Failed to delete memory: {e}"))?;

        if result.rows_affected() > 0 {
            // Log successful deletion
            self.log_audit_event(
                &authenticated_user_id,
                "delete",
                None,
                "success",
                None,
                Some(serde_json::json!({
                    "persona_id": persona_id,
                })),
            )
            .await?;

            Ok(VaultResponse {
                success: true,
                message: "Memory deleted successfully".to_string(),
                data: None,
                entry_id: None,
            })
        } else {
            Ok(VaultResponse {
                success: true,
                message: "No memory found to delete".to_string(),
                data: None,
                entry_id: None,
            })
        }
    }

    /// Get audit log for a user
    pub async fn get_audit_log(
        &self,
        user_id: String,
        auth_token: String,
        limit: Option<i64>,
    ) -> Result<Vec<AuditLogEntry>, String> {
        // Authenticate user
        let authenticated_user_id = self.authenticator.verify_token(&auth_token).await?;

        // Use constant-time comparison
        if !self
            .authenticator
            .verify_user_match(&authenticated_user_id, &user_id)
        {
            return Err("Authentication failed: user mismatch".to_string());
        }

        let db_guard = self.db_manager.read().await;
        let db_manager = db_guard.as_ref().ok_or("Database not initialized")?;

        let limit_value = limit.unwrap_or(100);

        let rows = sqlx::query(r#"
            SELECT id, user_id, action, resource_id, timestamp, result, error_message, metadata, schema_version
            FROM audit_log
            WHERE user_id = ?1
            ORDER BY timestamp DESC
            LIMIT ?2
        "#)
        .bind(&user_id)
        .bind(limit_value)
        .fetch_all(&db_manager.read_pool)
        .await
        .map_err(|e| format!("Failed to retrieve audit log: {e}"))?;

        let entries: Vec<AuditLogEntry> = rows
            .iter()
            .map(|row| AuditLogEntry {
                id: row.get("id"),
                user_id: row.get("user_id"),
                action: row.get("action"),
                resource_id: row.get("resource_id"),
                timestamp: row.get("timestamp"),
                result: row.get("result"),
                error_message: row.get("error_message"),
                metadata: row
                    .get::<Option<String>, _>("metadata")
                    .and_then(|s| serde_json::from_str::<serde_json::Value>(&s).ok()),
                schema_version: row.get("schema_version"),
            })
            .collect();

        Ok(entries)
    }

    /// Log audit event
    async fn log_audit_event(
        &self,
        user_id: &str,
        action: &str,
        resource_id: Option<&str>,
        result: &str,
        error_message: Option<&str>,
        metadata: Option<serde_json::Value>,
    ) -> Result<(), String> {
        let db_guard = self.db_manager.read().await;
        let db_manager = db_guard.as_ref().ok_or("Database not initialized")?;

        let event_id = Uuid::new_v4().to_string();
        let timestamp = Utc::now().timestamp();
        let metadata_json = metadata
            .map(|m| serde_json::to_string(&m).unwrap_or_default())
            .unwrap_or_default();

        sqlx::query(r#"
            INSERT INTO audit_log
            (id, user_id, action, resource_id, timestamp, result, error_message, metadata, schema_version)
            VALUES (?1, ?2, ?3, ?4, ?5, ?6, ?7, ?8, ?9)
        "#)
        .bind(&event_id)
        .bind(user_id)
        .bind(action)
        .bind(resource_id)
        .bind(timestamp)
        .bind(result)
        .bind(error_message)
        .bind(&metadata_json)
        .bind("1.0")
        .execute(&db_manager.write_pool)
        .await
        .map_err(|e| format!("Failed to log audit event: {e}"))?;

        Ok(())
    }
}

