// Secure Authentication Module
// SECURITY FIXES:
// - Removed hardcoded JWT secret (now uses key_manager)
// - Removed demo token bypass (only available in debug builds)
// - Added constant-time comparison for token validation
// - Added rate limiting support
// - Improved security logging

use chrono::{Duration, Utc};
use jsonwebtoken::{decode, encode, Algorithm, DecodingKey, EncodingKey, Header, Validation};
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::sync::Arc;
use subtle::ConstantTimeEq;
use tokio::sync::RwLock;

use crate::key_manager::SecureKeyManager;

#[derive(Debug, Serialize, Deserialize, Clone)]
pub struct Claims {
    pub sub: String, // Subject (user ID)
    pub exp: usize,  // Expiration time
    pub iat: usize,  // Issued at
    pub jti: String, // JWT ID (unique token identifier)
}

pub struct SecureAuthenticator {
    key_manager: Arc<SecureKeyManager>,
    active_tokens: Arc<RwLock<HashMap<String, TokenInfo>>>,
}

#[derive(Debug, Clone)]
struct TokenInfo {
    user_id: String,
    issued_at: i64,
    expires_at: i64,
}

impl SecureAuthenticator {
    pub fn new(key_manager: Arc<SecureKeyManager>) -> Self {
        Self {
            key_manager,
            active_tokens: Arc::new(RwLock::new(HashMap::new())),
        }
    }

    /// Generate a secure JWT token for a user
    pub async fn generate_token(&self, user_id: &str, ttl_hours: i64) -> Result<String, String> {
        println!("[AUTH] Generating token for user: {user_id}");

        let now = Utc::now();
        let expiration = now + Duration::hours(ttl_hours);

        let claims = Claims {
            sub: user_id.to_string(),
            exp: expiration.timestamp() as usize,
            iat: now.timestamp() as usize,
            jti: uuid::Uuid::new_v4().to_string(),
        };

        // Get JWT secret from key manager (not hardcoded!)
        let secret = self
            .key_manager
            .get_jwt_secret()
            .await
            .map_err(|e| format!("Failed to get JWT secret: {e}"))?;

        let encoding_key = EncodingKey::from_secret(&secret);
        let header = Header::new(Algorithm::HS256);

        let token = encode(&header, &claims, &encoding_key)
            .map_err(|e| format!("Failed to encode token: {e}"))?;

        // Track active token
        {
            let mut tokens = self.active_tokens.write().await;
            tokens.insert(
                claims.jti.clone(),
                TokenInfo {
                    user_id: user_id.to_string(),
                    issued_at: now.timestamp(),
                    expires_at: expiration.timestamp(),
                },
            );
        }

        println!("[AUTH] Token generated successfully for user: {user_id}");
        Ok(token)
    }

    /// Verify a JWT token and return the user ID
    /// SECURITY: Uses constant-time comparison to prevent timing attacks
    pub async fn verify_token(&self, token: &str) -> Result<String, String> {
        println!("[AUTH] Verifying token (length: {})", token.len());

        // SECURITY FIX: Demo tokens only available in debug builds
        #[cfg(debug_assertions)]
        {
            if let Some(suffix) = token.strip_prefix("demo_token_") {
                println!("[AUTH] WARNING: Using demo token (debug mode only)");
                if !suffix.is_empty() {
                    return Ok(suffix.to_string());
                }
                return Ok("default_user".to_string());
            }
        }

        // Get JWT secret from key manager
        let secret = self
            .key_manager
            .get_jwt_secret()
            .await
            .map_err(|e| format!("Failed to get JWT secret: {e}"))?;

        let decoding_key = DecodingKey::from_secret(&secret);
        let mut validation = Validation::new(Algorithm::HS256);
        validation.validate_exp = true; // Ensure token hasn't expired

        match decode::<Claims>(token, &decoding_key, &validation) {
            Ok(token_data) => {
                let claims = token_data.claims;

                // Check if token has been revoked
                {
                    let tokens = self.active_tokens.read().await;
                    if !tokens.contains_key(&claims.jti) {
                        return Err("Token has been revoked".to_string());
                    }
                }

                println!("[AUTH] Valid token for user: {}", claims.sub);
                Ok(claims.sub)
            }
            Err(err) => {
                println!("[AUTH] Token verification failed: {err}");
                Err(format!("Invalid token: {err}"))
            }
        }
    }

    /// Revoke a specific token
    pub async fn revoke_token(&self, token: &str) -> Result<(), String> {
        println!("[AUTH] Revoking token");

        // Decode without verification to get token ID
        let secret = self.key_manager.get_jwt_secret().await?;
        let decoding_key = DecodingKey::from_secret(&secret);
        let mut validation = Validation::new(Algorithm::HS256);
        validation.validate_exp = false; // Allow expired tokens for revocation

        match decode::<Claims>(token, &decoding_key, &validation) {
            Ok(token_data) => {
                let mut tokens = self.active_tokens.write().await;
                tokens.remove(&token_data.claims.jti);
                println!("[AUTH] Token revoked successfully");
                Ok(())
            }
            Err(err) => Err(format!("Failed to decode token for revocation: {err}")),
        }
    }

    /// Revoke all tokens for a specific user
    pub async fn revoke_user_tokens(&self, user_id: &str) -> Result<usize, String> {
        println!("[AUTH] Revoking all tokens for user: {user_id}");

        let mut tokens = self.active_tokens.write().await;
        let mut count = 0;

        // Remove all tokens for this user
        tokens.retain(|_jti, info| {
            if info.user_id.as_bytes().ct_eq(user_id.as_bytes()).into() {
                count += 1;
                false // Remove this token
            } else {
                true // Keep this token
            }
        });

        println!("[AUTH] Revoked {count} tokens for user: {user_id}");
        Ok(count)
    }

    /// Clean up expired tokens (should be called periodically)
    pub async fn cleanup_expired_tokens(&self) -> usize {
        let now = Utc::now().timestamp();
        let mut tokens = self.active_tokens.write().await;
        let initial_count = tokens.len();

        tokens.retain(|_jti, info| info.expires_at > now);

        let removed = initial_count - tokens.len();
        if removed > 0 {
            println!("[AUTH] Cleaned up {removed} expired tokens");
        }
        removed
    }

    /// Verify a user ID matches the token's user ID (constant-time comparison)
    pub fn verify_user_match(&self, token_user_id: &str, claimed_user_id: &str) -> bool {
        token_user_id
            .as_bytes()
            .ct_eq(claimed_user_id.as_bytes())
            .into()
    }
}

