// Database Manager with SQLx for secure vault operations
// Optimized with Connection Pooling (Read/Write Separation)

use sqlx::{
    sqlite::{SqliteConnectOptions, SqliteJournalMode, SqlitePoolOptions},
    SqlitePool,
};
use std::future::Future;
use std::pin::Pin;
use std::str::FromStr;

pub struct DatabaseManager {
    pub write_pool: SqlitePool,
    pub read_pool: SqlitePool,
}

impl DatabaseManager {
    pub async fn new(db_path: &str) -> Result<Self, String> {
        println!("[DB] Initializing database at: {db_path}");

        let connect_options = SqliteConnectOptions::from_str(db_path)
            .map_err(|e| format!("Failed to parse database path: {e}"))?
            .create_if_missing(true)
            .journal_mode(SqliteJournalMode::Wal);

        // Write Pool: Limited connections to reduce locking contention
        let write_pool = SqlitePoolOptions::new()
            .max_connections(5)
            .min_connections(1)
            .connect_with(connect_options.clone())
            .await
            .map_err(|e| format!("Failed to create write pool: {e}"))?;

        // Read Pool: Higher connection limit for parallel queries
        let read_pool = SqlitePoolOptions::new()
            .max_connections(10)
            .min_connections(2)
            .connect_with(connect_options)
            .await
            .map_err(|e| format!("Failed to create read pool: {e}"))?;

        let db_manager = Self {
            write_pool,
            read_pool,
        };

        db_manager.run_migrations().await?;

        Ok(db_manager)
    }

    pub async fn initialize(app_handle: &tauri::AppHandle) -> Result<Self, String> {
        let app_dir = app_handle
            .path_resolver()
            .app_data_dir()
            .ok_or("Failed to get app data directory")?;

        std::fs::create_dir_all(&app_dir)
            .map_err(|e| format!("Failed to create app directory: {e}"))?;

        let db_path = app_dir.join("vault.db");
        let db_url = format!("sqlite:{}", db_path.display());

        Self::new(&db_url).await
    }

    // Helper for executing read operations
    pub async fn execute_read<F, R>(&self, operation: F) -> Result<R, String>
    where
        F: FnOnce(&SqlitePool) -> Pin<Box<dyn Future<Output = Result<R, String>> + Send>>,
    {
        operation(&self.read_pool).await
    }

    // Helper for executing write operations
    pub async fn execute_write<F, R>(&self, operation: F) -> Result<R, String>
    where
        F: FnOnce(&SqlitePool) -> Pin<Box<dyn Future<Output = Result<R, String>> + Send>>,
    {
        operation(&self.write_pool).await
    }

    // Direct pool access if needed (prefer specific helpers)
    pub fn get_pool(&self) -> &SqlitePool {
        &self.read_pool
    }

    async fn run_migrations(&self) -> Result<(), String> {
        println!("[DB] Running database migrations...");

        // Using write_pool for migrations
        sqlx::query(
            r#"
            CREATE TABLE IF NOT EXISTS vault_entries (
                id TEXT PRIMARY KEY NOT NULL,
                user_id TEXT NOT NULL,
                persona_id TEXT NOT NULL,
                encrypted_data BLOB NOT NULL,
                nonce BLOB NOT NULL,
                key_version INTEGER NOT NULL DEFAULT 1,
                created_at INTEGER NOT NULL,
                updated_at INTEGER NOT NULL,
                schema_version TEXT NOT NULL
            )
        "#,
        )
        .execute(&self.write_pool)
        .await
        .map_err(|e| format!("Failed to create vault_entries table: {e}"))?;

        // Create indexes for better query performance
        let _ = sqlx::query("CREATE INDEX IF NOT EXISTS idx_vault_user_persona ON vault_entries(user_id, persona_id)")
            .execute(&self.write_pool)
            .await;

        let _ = sqlx::query(
            "CREATE INDEX IF NOT EXISTS idx_vault_updated ON vault_entries(updated_at DESC)",
        )
        .execute(&self.write_pool)
        .await;

        // Enhanced audit_log table with security fields
        sqlx::query(
            r#"
            CREATE TABLE IF NOT EXISTS audit_log (
                id TEXT PRIMARY KEY NOT NULL,
                user_id TEXT NOT NULL,
                action TEXT NOT NULL,
                resource_id TEXT,
                timestamp INTEGER NOT NULL,
                result TEXT NOT NULL,
                error_message TEXT,
                metadata TEXT,
                schema_version TEXT NOT NULL
            )
        "#,
        )
        .execute(&self.write_pool)
        .await
        .map_err(|e| format!("Failed to create audit_log table: {e}"))?;

        // Create indexes for audit_log
        let _ = sqlx::query(
            "CREATE INDEX IF NOT EXISTS idx_audit_user ON audit_log(user_id, timestamp DESC)",
        )
        .execute(&self.write_pool)
        .await;

        let _ = sqlx::query(
            "CREATE INDEX IF NOT EXISTS idx_audit_action ON audit_log(action, timestamp DESC)",
        )
        .execute(&self.write_pool)
        .await;

        println!("[DB] Database migrations completed successfully (with encryption schema)");
        Ok(())
    }

    pub async fn backup_database(&self, backup_dir: std::path::PathBuf) -> Result<String, String> {
        let timestamp = chrono::Utc::now().format("%Y%m%d_%H%M%S").to_string();
        let backup_filename = format!("hearthlink_backup_{}.db", timestamp);
        let backup_path = backup_dir.join(&backup_filename);

        let path_str = backup_path.to_string_lossy().into_owned();

        // Use VACUUM INTO for online backup
        // Note: We avoid SQL injection by using the backup_filename constructed above from timestamp which is safe.
        // However, path_str contains the directory part which could technically contain special chars.
        // VACUUM INTO expects a string literal.
        // Since we can't easily bind parameters to VACUUM INTO in all drivers, and we control the path:
        // We will sanitize/escape single quotes if needed, though app_data_dir usually is safe.

        let safe_path = path_str.replace("'", "''");

        let query = format!("VACUUM INTO '{}'", safe_path);

        sqlx::query(&query)
            .execute(&self.write_pool)
            .await
            .map_err(|e| format!("Backup failed: {}", e))?;

        Ok(path_str)
    }
}
