use std::sync::Arc;
use tauri::{AppHandle, State};
use tokio::sync::RwLock;

#[tauri::command]
pub async fn system_create_backup(
    app_handle: AppHandle,
    db_state: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
) -> Result<String, String> {
    let db_guard = db_state.read().await;
    let db_manager = db_guard.as_ref().ok_or("Database not initialized")?;

    let backup_dir = app_handle
        .path_resolver()
        .app_data_dir()
        .ok_or("Failed to get app data dir")?
        .join("backups");

    if !backup_dir.exists() {
        std::fs::create_dir_all(&backup_dir).map_err(|e| e.to_string())?;
    }

    let path = db_manager.backup_database(backup_dir).await?;
    Ok(path)
}

