import { test, expect, Helpers, Mocks, Selectors } from './setup';

// ==============================================================================
// Keyboard Navigation Tests
// ==============================================================================

test.describe('Keyboard Navigation - Global', () => {
  test.beforeEach(async ({ page }) => {
    await Mocks.install(page);
    await page.goto('/');
    await Helpers.waitForAppReady(page);
  });

  test.afterEach(async () => {
    Mocks.reset();
  });

  test('should allow Tab navigation through main navigation', async ({ page }) => {
    // Focus first interactive element
    await page.keyboard.press('Tab');

    const focusedElements: string[] = [];
    let iterations = 0;
    const maxIterations = 10;

    while (iterations < maxIterations) {
      await page.keyboard.press('Tab');
      const tagName = await page.evaluate(() => document.activeElement?.tagName);
      const role = await page.evaluate(() => document.activeElement?.getAttribute('role'));

      if (tagName) {
        focusedElements.push(tagName);
      }

      iterations++;

      // Stop if we've cycled back to body
      if (tagName === 'BODY') break;
    }

    // Should focus on interactive elements
    const interactiveElements = focusedElements.filter((el) =>
      ['BUTTON', 'A', 'INPUT', 'SELECT', 'TEXTAREA'].includes(el)
    );
    expect(interactiveElements.length).toBeGreaterThan(0);
  });

  test('should support Shift+Tab for reverse navigation', async ({ page }) => {
    // Tab forward several times
    for (let i = 0; i < 3; i++) {
      await page.keyboard.press('Tab');
    }

    const elementAfterForward = await page.evaluate(
      () => document.activeElement?.tagName
    );

    // Tab backward
    await page.keyboard.press('Shift+Tab');

    const elementAfterBackward = await page.evaluate(
      () => document.activeElement?.tagName
    );

    // Should be able to navigate backwards (elements should differ)
    expect(elementAfterForward).toBeTruthy();
    expect(elementAfterBackward).toBeTruthy();
  });

  test('should open command palette with Cmd+K / Ctrl+K', async ({ page }) => {
    const isMac = process.platform === 'darwin';
    await page.keyboard.press(isMac ? 'Meta+KeyK' : 'Control+KeyK');

    await page.waitForTimeout(500);

    const commandPalette = page.locator(Selectors.commandPalette);
    const exists = await commandPalette.count();

    if (exists > 0) {
      await expect(commandPalette).toBeVisible();
    }
  });

  test('should escape to close modals/dialogs', async ({ page }) => {
    // Try opening command palette first
    const isMac = process.platform === 'darwin';
    await page.keyboard.press(isMac ? 'Meta+KeyK' : 'Control+KeyK');
    await page.waitForTimeout(300);

    const commandPalette = page.locator(Selectors.commandPalette);
    const exists = await commandPalette.count();

    if (exists > 0) {
      // Press Escape
      await page.keyboard.press('Escape');
      await page.waitForTimeout(300);

      // Command palette should be hidden
      const stillVisible = await commandPalette.isVisible().catch(() => false);
      expect(stillVisible).toBe(false);
    }
  });

  test('should maintain visible focus indicators', async ({ page }) => {
    await page.keyboard.press('Tab');
    await page.waitForTimeout(200);

    const hasFocusVisible = await page.evaluate(() => {
      const activeEl = document.activeElement;
      if (!activeEl) return false;

      const styles = window.getComputedStyle(activeEl);
      const pseudo = window.getComputedStyle(activeEl, ':focus-visible');

      // Check if there's a visible focus indicator
      return (
        styles.outline !== 'none' ||
        styles.outlineWidth !== '0px' ||
        styles.boxShadow !== 'none' ||
        pseudo.outline !== 'none'
      );
    });

    expect(hasFocusVisible).toBe(true);
  });
});

