/**
 * Comprehensive Accessibility E2E Tests
 *
 * Tests WCAG 2.1 AA compliance across all Hearthlink modules.
 * Validates keyboard navigation, ARIA attributes, screen reader compatibility,
 * focus management, and color contrast.
 *
 * This suite complements Gemini's accessibility implementation work on:
 * - Kanban keyboard navigation
 * - ARIA labels for Risk Matrix and Gantt Chart
 * - Icon alternatives for screen readers
 *
 * @see docs/accessibility/WCAG_AUDIT_REPORT.md
 * @see docs/accessibility/ACCESSIBILITY_FIXES_NEEDED.md
 */

import { test, expect, Helpers, Mocks, Selectors } from './setup';

// ==============================================================================
// Keyboard Navigation Tests
// ==============================================================================

test.describe('Keyboard Navigation - Global', () => {
  test.beforeEach(async ({ page }) => {
    await Mocks.install(page);
    await page.goto('/');
    await Helpers.waitForAppReady(page);
  });

  test.afterEach(async () => {
    Mocks.reset();
  });

  test('should allow Tab navigation through main navigation', async ({ page }) => {
    // Focus first interactive element
    await page.keyboard.press('Tab');

    const focusedElements: string[] = [];
    let iterations = 0;
    const maxIterations = 10;

    while (iterations < maxIterations) {
      await page.keyboard.press('Tab');
      const tagName = await page.evaluate(() => document.activeElement?.tagName);
      const role = await page.evaluate(() => document.activeElement?.getAttribute('role'));

      if (tagName) {
        focusedElements.push(tagName);
      }

      iterations++;

      // Stop if we've cycled back to body
      if (tagName === 'BODY') break;
    }

    // Should focus on interactive elements
    const interactiveElements = focusedElements.filter((el) =>
      ['BUTTON', 'A', 'INPUT', 'SELECT', 'TEXTAREA'].includes(el)
    );
    expect(interactiveElements.length).toBeGreaterThan(0);
  });

  test('should support Shift+Tab for reverse navigation', async ({ page }) => {
    // Tab forward several times
    for (let i = 0; i < 3; i++) {
      await page.keyboard.press('Tab');
    }

    const elementAfterForward = await page.evaluate(
      () => document.activeElement?.tagName
    );

    // Tab backward
    await page.keyboard.press('Shift+Tab');

    const elementAfterBackward = await page.evaluate(
      () => document.activeElement?.tagName
    );

    // Should be able to navigate backwards (elements should differ)
    expect(elementAfterForward).toBeTruthy();
    expect(elementAfterBackward).toBeTruthy();
  });

  test('should open command palette with Cmd+K / Ctrl+K', async ({ page }) => {
    const isMac = process.platform === 'darwin';
    await page.keyboard.press(isMac ? 'Meta+KeyK' : 'Control+KeyK');

    await page.waitForTimeout(500);

    const commandPalette = page.locator(Selectors.commandPalette);
    const exists = await commandPalette.count();

    if (exists > 0) {
      await expect(commandPalette).toBeVisible();
    }
  });

  test('should escape to close modals/dialogs', async ({ page }) => {
    // Try opening command palette first
    const isMac = process.platform === 'darwin';
    await page.keyboard.press(isMac ? 'Meta+KeyK' : 'Control+KeyK');
    await page.waitForTimeout(300);

    const commandPalette = page.locator(Selectors.commandPalette);
    const exists = await commandPalette.count();

    if (exists > 0) {
      // Press Escape
      await page.keyboard.press('Escape');
      await page.waitForTimeout(300);

      // Command palette should be hidden
      const stillVisible = await commandPalette.isVisible().catch(() => false);
      expect(stillVisible).toBe(false);
    }
  });

  test('should maintain visible focus indicators', async ({ page }) => {
    await page.keyboard.press('Tab');
    await page.waitForTimeout(200);

    const hasFocusVisible = await page.evaluate(() => {
      const activeEl = document.activeElement;
      if (!activeEl) return false;

      const styles = window.getComputedStyle(activeEl);
      const pseudo = window.getComputedStyle(activeEl, ':focus-visible');

      // Check if there's a visible focus indicator
      return (
        styles.outline !== 'none' ||
        styles.outlineWidth !== '0px' ||
        styles.boxShadow !== 'none' ||
        pseudo.outline !== 'none'
      );
    });

    expect(hasFocusVisible).toBe(true);
  });
});

// ==============================================================================
// Kanban Board Keyboard Navigation (Gemini's Implementation)
// ==============================================================================

test.describe('Keyboard Navigation - Kanban Board', () => {
  test.beforeEach(async ({ page }) => {
    await Mocks.install(page);
    Mocks.scenarios.sampleProject();

    await page.goto('/');
    await Helpers.waitForAppReady(page);

    // Navigate to Core module
    const coreButton = page.locator(Selectors.moduleCore).or(
      page.getByRole('button', { name: /core/i })
    );
    const exists = await coreButton.count();
    if (exists > 0) {
      await coreButton.click();
      await page.waitForURL(/.*core.*/);
    }
  });

  test.afterEach(async () => {
    Mocks.reset();
  });

  test('should allow keyboard navigation between Kanban columns', async ({ page }) => {
    const kanban = page.locator(Selectors.kanbanBoard);
    const exists = await kanban.count();

    if (exists > 0) {
      // Focus the Kanban board
      await kanban.focus();
      await page.waitForTimeout(200);

      // Arrow keys should navigate between columns
      await page.keyboard.press('ArrowRight');
      await page.waitForTimeout(200);

      const focusedElement = await page.evaluate(
        () => document.activeElement?.getAttribute('data-column') || ''
      );

      // Should have moved to a column
      expect(focusedElement).toBeTruthy();
    }
  });

  test('should navigate between cards with arrow keys', async ({ page }) => {
    const kanban = page.locator(Selectors.kanbanBoard);
    const exists = await kanban.count();

    if (exists > 0) {
      await kanban.focus();
      await page.waitForTimeout(200);

      // Navigate to first card
      await page.keyboard.press('ArrowDown');
      await page.waitForTimeout(200);

      const focusedCard = await page.evaluate(
        () => document.activeElement?.getAttribute('data-card-id')
      );

      // Should have focused a card
      expect(focusedCard).toBeTruthy();
    }
  });

  test('should support Enter to select/activate cards', async ({ page }) => {
    const kanban = page.locator(Selectors.kanbanBoard);
    const exists = await kanban.count();

    if (exists > 0) {
      await kanban.focus();

      // Navigate to first card
      await page.keyboard.press('ArrowDown');
      await page.waitForTimeout(200);

      // Press Enter to activate
      await page.keyboard.press('Enter');
      await page.waitForTimeout(300);

      // A modal or detail view may appear
      const modal = page.locator(Selectors.modal);
      const modalExists = await modal.count();

      // Either modal appears or card becomes editable
      if (modalExists > 0) {
        await expect(modal).toBeVisible();
      }
    }
  });

  test('should support Space to move cards between columns', async ({ page }) => {
    const kanban = page.locator(Selectors.kanbanBoard);
    const exists = await kanban.count();

    if (exists > 0) {
      await kanban.focus();

      // Navigate to a card
      await page.keyboard.press('ArrowDown');
      await page.waitForTimeout(200);

      const initialColumn = await page.evaluate(
        () => document.activeElement?.closest('[data-column]')?.getAttribute('data-column')
      );

      // Press Space to grab/drop
      await page.keyboard.press('Space');
      await page.waitForTimeout(200);

      // Move right
      await page.keyboard.press('ArrowRight');
      await page.waitForTimeout(200);

      // Drop
      await page.keyboard.press('Space');
      await page.waitForTimeout(300);

      const finalColumn = await page.evaluate(
        () => document.activeElement?.closest('[data-column]')?.getAttribute('data-column')
      );

      // Columns should differ (card moved)
      if (initialColumn && finalColumn) {
        expect(initialColumn).not.toBe(finalColumn);
      }
    }
  });

  test('should have ARIA live region for announcements', async ({ page }) => {
    const liveRegion = page.locator('[aria-live="polite"]').or(
      page.locator('[aria-live="assertive"]')
    );

    const exists = await liveRegion.count();
    // Live region should exist for screen reader announcements
    expect(exists).toBeGreaterThan(0);
  });
});

