test.describe('Form Accessibility', () => {
  test.beforeEach(async ({ page }) => {
    await Mocks.install(page);
    Mocks.scenarios.sampleProject();

    await page.goto('/');
    await Helpers.waitForAppReady(page);
  });

  test.afterEach(async () => {
    Mocks.reset();
  });

  test('should have label elements for all inputs', async ({ page }) => {
    const inputs = await page.locator('input[type="text"], input[type="email"], textarea').all();

    for (const input of inputs) {
      const id = await input.getAttribute('id');
      const ariaLabel = await input.getAttribute('aria-label');
      const ariaLabelledBy = await input.getAttribute('aria-labelledby');

      // Should have either associated label or aria-label
      const hasLabel = id
        ? (await page.locator(`label[for="${id}"]`).count()) > 0
        : false;

      expect(hasLabel || ariaLabel || ariaLabelledBy).toBeTruthy();
    }
  });

  test('should show error messages with aria-describedby', async ({ page }) => {
    // Navigate to a form (e.g., create task)
    const aldenButton = page.locator(Selectors.moduleAlden).or(
      page.getByRole('button', { name: /alden/i })
    );
    const exists = await aldenButton.count();

    if (exists > 0) {
      await aldenButton.click();
      await page.waitForURL(/.*alden.*/);

      // Look for a create button
      const createButton = page.getByRole('button', { name: /create|new/i }).first();
      const buttonExists = await createButton.count();

      if (buttonExists > 0) {
        await createButton.click();
        await page.waitForTimeout(500);

        // Try submitting empty form
        const submitButton = page.getByRole('button', { name: /submit|save|create/i }).first();
        const submitExists = await submitButton.count();

        if (submitExists > 0) {
          await submitButton.click();
          await page.waitForTimeout(500);

          // Look for error messages
          const errorMessages = page.locator('[role="alert"], [aria-live="polite"]');
          const errorCount = await errorMessages.count();

          if (errorCount > 0) {
            // Error messages should be announced
            const firstError = errorMessages.first();
            const role = await firstError.getAttribute('role');
            const ariaLive = await firstError.getAttribute('aria-live');

            expect(role === 'alert' || ariaLive === 'polite').toBe(true);
          }
        }
      }
    }
  });

  test('should have proper fieldset and legend for related inputs', async ({ page }) => {
    const fieldsets = page.locator('fieldset');
    const count = await fieldsets.count();

    if (count > 0) {
      const firstFieldset = fieldsets.first();
      const legend = firstFieldset.locator('legend');
      const legendExists = await legend.count();

      expect(legendExists).toBeGreaterThan(0);
    }
  });

  test('should indicate required fields with aria-required', async ({ page }) => {
    const requiredInputs = page.locator('input[required], textarea[required]');
    const count = await requiredInputs.count();

    if (count > 0) {
      const firstRequired = requiredInputs.first();
      const ariaRequired = await firstRequired.getAttribute('aria-required');

      expect(ariaRequired).toBe('true');
    }
  });
});

