              <div className="p-4 border-b border-alden-border bg-alden-panel rounded-t-lg">
                <div className="flex items-center justify-between mb-2">
                  <h3 className="font-semibold text-alden-text-primary">{column.name}</h3>
                  <span className="text-sm text-gray-500">
                    {getColumnCards(column.columnId).length}
                    {column.wipLimit && ` / ${column.wipLimit}`}
                  </span>
                </div>
                {column.wipLimit && getColumnCards(column.columnId).length > column.wipLimit && (
                  <div className="text-xs text-red-600 flex items-center gap-1">
                    <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
                    </svg>
                    WIP limit exceeded
                  </div>
                )}
              </div>

              {/* Cards Container */}
              <Droppable droppableId={column.columnId}>
                {(provided, snapshot) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    className={`p-4 min-h-[200px] space-y-3 ${
                      snapshot.isDraggingOver ? 'bg-blue-50' : ''
                    }`}
                  >
                    {getColumnCards(column.columnId).map((card, index) => (
                      <Draggable
                        key={card.cardId}
                        draggableId={card.cardId}
                        index={index}
                      >
                        {(provided, snapshot) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            data-testid="kanban-card"
                            data-card-id={card.cardId}
                            className={`bg-alden-panel rounded-lg shadow-sm border border-alden-border p-3 cursor-move transition-shadow hover:shadow-md focus:ring-2 focus:ring-blue-500 outline-none ${
                              snapshot.isDragging ? 'shadow-lg ring-2 ring-blue-500' : ''
                            }`}
                            role="button"
                            tabIndex={0}
                            onKeyDown={(e) => handleKeyboardMove(e, card)}
                            aria-label={`Task: ${card.title}, Priority: ${card.priority}. Press Enter for options.`}
                          >
                            {/* Card Header */}
                            <div className="flex items-start justify-between gap-2 mb-2">
                              <h4 className="font-medium text-alden-text-primary text-sm flex-1">
                                {card.title}
