                              <div className="flex gap-1">
                                <button
                                  aria-label={`Edit card ${card.title}`}
                                  onClick={(e) => { e.stopPropagation(); openEditModal(card); }}
                                  className="p-1 text-gray-400 hover:text-blue-600 transition"
                                >
                                  <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
                                  </svg>
                                </button>
                                <button
                                  aria-label={`Delete card ${card.title}`}
                                  onClick={(e) => { e.stopPropagation(); confirmDeleteCard(card); }}
                                  className="p-1 text-gray-400 hover:text-red-600 transition"
                                >
                                  <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                                  </svg>
                                </button>
                              </div>
                            </div>

                            {/* Card Description */}
                            {card.description && (
                              <p className="text-xs text-alden-text-secondary mb-3 line-clamp-2">
                                {card.description}
                              </p>
                            )}

                            {/* Card Metadata */}
                            <div className="flex flex-wrap gap-2 items-center">
                              {/* Priority Badge */}
                              <span
                                className={`inline-flex items-center px-2 py-0.5 rounded text-xs font-medium border ${getPriorityColor(card.priority)}`}
                                data-testid="priority-badge"
                              >
                                {card.priority}
                              </span>

                              {/* Blocked Indicator */}
                              {card.blocked && (
                                <span
                                  className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-red-100 text-red-800 border border-red-300"
                                  title={card.blockerReason || 'Blocked'}
                                  data-testid="blocked-indicator"
                                >
                                  <svg className="w-3 h-3 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
                                  </svg>
                                  Blocked
                                </span>
                              )}

                              {/* Labels */}
                              {card.labels.map((label, idx) => (
                                <span
                                  key={idx}
                                  className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-purple-100 text-purple-800"
                                >
                                  {label}
                                </span>
                              ))}
                            </div>

                            {/* Card Footer */}
                            <div className="flex items-center justify-between mt-3 pt-3 border-t border-gray-100 text-xs text-gray-500">
