import React, { useState, useEffect, useCallback, useRef, memo } from 'react';
import * as d3 from 'd3';
import { CoreBridge } from '../../bridges/CoreBridge';
import type { Risk, RiskImpact, RiskProbability, RiskStatus, RiskCategory } from '../../types/agents';
import { PremiumModal } from '../shared/premium/PremiumModal';
import { PremiumTextarea } from '../shared/premium/PremiumTextarea';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';

interface RiskMatrixProps {
  projectId: string;
  userId: string;
}

interface RiskFormData {
  title: string;
  description: string;
  category: RiskCategory;
  impact: RiskImpact;
  probability: RiskProbability;
  ownerId: string;
}

type ModalMode = 'create' | 'edit' | null;

const RISK_IMPACTS: RiskImpact[] = ['very_low', 'low', 'medium', 'high', 'very_high'];
const RISK_PROBABILITIES: RiskProbability[] = ['very_low', 'low', 'medium', 'high', 'very_high'];
const RISK_CATEGORIES: RiskCategory[] = ['technical', 'schedule', 'budget', 'resource', 'external', 'quality', 'scope', 'communication'];

const RISK_STATUS_COLORS: Record<RiskStatus, string> = {
  identified: '#60a5fa', // blue
  analyzing: '#facc15', // yellow
  mitigating: '#fb923c', // orange
  monitoring: '#a78bfa', // purple
  closed: '#34d399', // green
  materialized: '#ef4444', // red
};

const RiskMatrixComponent: React.FC<RiskMatrixProps> = ({ projectId, userId }) => {
  const [risks, setRisks] = useState<Risk[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [successMessage, setSuccessMessage] = useState<string | null>(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [modalMode, setModalMode] = useState<ModalMode>(null);
  const [selectedRisk, setSelectedRisk] = useState<Risk | null>(null);
  const [formData, setFormData] = useState<RiskFormData>({
    title: '',
    description: '',
    category: 'technical',
    impact: 'medium',
    probability: 'medium',
    ownerId: userId,
  });
  const [announcement, setAnnouncement] = useState<string>('');

  const svgRef = useRef<SVGSVGElement>(null);
  const bridge = useRef(new CoreBridge({ userId }));
  const errorMessage = (err: unknown): string => (
    err instanceof Error ? err.message : String(err)
  );

  const loadRisks = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      await bridge.current.initialize();

      // Assuming CoreBridge has a method to list risks
      const result = await bridge.current.listRisks(projectId);
      if (result.success) {
        setRisks(result.data);
      } else {
        setError(result.error || 'Failed to load risks');
      }


    } catch (err: unknown) {
      console.error('[RiskMatrix] Failed to load risks:', err);
      setError(errorMessage(err) || 'Failed to load risks');
    } finally {
      setLoading(false);
    }
  }, [projectId]);

  useEffect(() => {
    loadRisks();
  }, [loadRisks]);

  useEffect(() => {
    if (successMessage) {
       setAnnouncement(successMessage);
       const timer = setTimeout(() => {
         setSuccessMessage(null);
         setAnnouncement(''); // Clear to ready for next
       }, 3000);
       return () => clearTimeout(timer);
    }
  }, [successMessage]);

  const drawMatrix = useCallback(() => {
    if (!svgRef.current || risks.length === 0) return;

    const margin = { top: 20, right: 20, bottom: 60, left: 100 };
    const width = 600 - margin.left - margin.right;
    const height = 500 - margin.top - margin.bottom;

    const svg = d3.select(svgRef.current)
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .attr('aria-label', 'Risk Matrix')
      .attr('role', 'grid')
      .attr('data-testid', 'risk-matrix')
      .html('') // Clear SVG content
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    const xScale = d3.scaleBand()
      .domain(RISK_PROBABILITIES)
      .range([0, width])
      .paddingInner(0.1)
      .paddingOuter(0.5);

    const yScale = d3.scaleBand()
      .domain(RISK_IMPACTS.reverse()) // Reverse to have 'very_low' at bottom
      .range([height, 0])
      .paddingInner(0.1)
      .paddingOuter(0.5);

    // X-axis (Probability)
    svg.append('g')
      .attr('transform', `translate(0,${height})`)
      .attr('aria-labelledby', 'risk-axis-x-label')
      .attr('data-testid', 'likelihood-axis')
      .call(d3.axisBottom(xScale).tickFormat(d => d.replace('_', ' ')))
      .selectAll('text')
      .attr('transform', 'rotate(-45)')
      .style('text-anchor', 'end')
      .style('fill', 'var(--muted-foreground)');

    svg.append('text')
      .attr('id', 'risk-axis-x-label')
      .attr('x', width / 2)
      .attr('y', height + margin.bottom - 10)
      .attr('text-anchor', 'middle')
      .style('fill', 'var(--foreground)')
      .text('Probability');

    // Y-axis (Impact)
    svg.append('g')
      .attr('aria-labelledby', 'risk-axis-y-label')
      .attr('data-testid', 'impact-axis')
      .call(d3.axisLeft(yScale).tickFormat(d => d.replace('_', ' ')))
      .selectAll('text')
      .style('fill', 'var(--muted-foreground)');

    svg.append('text')
      .attr('id', 'risk-axis-y-label')
      .attr('transform', 'rotate(-90)')
      .attr('y', -margin.left + 20)
      .attr('x', -height / 2)
      .attr('text-anchor', 'middle')
      .style('fill', 'var(--foreground)')
      .text('Impact');

    // Draw cells
    RISK_PROBABILITIES.forEach(prob => {
      RISK_IMPACTS.forEach(imp => {
        const x = xScale(prob) ?? 0;
        const y = yScale(imp) ?? 0;
        svg.append('rect')
          .attr('x', x)
          .attr('y', y)
          .attr('width', xScale.bandwidth())
          .attr('height', yScale.bandwidth())
          .attr('fill', 'rgba(255,255,255,0.05)')
          .attr('stroke', 'var(--border)')
          .attr('stroke-width', 0.5)
          .attr('role', 'gridcell')
          .attr('aria-label', `Risk Cell: Impact ${imp.replace('_',' ')}, Likelihood ${prob.replace('_',' ')}`)
          .attr('tabindex', '0')
          .attr('data-testid', `risk-cell-${prob}-${imp}`)
          .style('pointer-events', 'all') // Make sure it's interactive if needed, though no click handler here
          .append('title')
          .text(`Impact: ${imp.replace('_', ' ')}, Likelihood: ${prob.replace('_', ' ')}`);
      });
    });

    // Draw risks
    svg.append('g')
      .selectAll('circle')
      .data(risks)
      .join('circle')
      .attr('cx', d => (xScale(d.probability) ?? 0) + xScale.bandwidth() / 2)
      .attr('cy', d => (yScale(d.impact) ?? 0) + yScale.bandwidth() / 2)
      .attr('r', 10)
      .attr('fill', d => RISK_STATUS_COLORS[d.status])
      .attr('stroke', 'var(--background)')
      .attr('stroke-width', 2)
      .style('cursor', 'pointer')
      .attr('role', 'button')
      .attr('tabindex', 0) 
      .attr('aria-label', d => `Risk: ${d.title}, Status: ${d.status}`)
       // Basic keyboard support for risks
      .on('keydown', (event, d) => {
          if (event.key === 'Enter' || event.key === ' ') {
            setSelectedRisk(d);
            setFormData({
              title: d.title,
              description: d.description,
              category: d.category,
              impact: d.impact,
              probability: d.probability,
              ownerId: d.ownerId,
            });
            setModalMode('edit');
            setModalOpen(true);
          }
      })
      .on('click', (event, d) => {
        setSelectedRisk(d);
        setFormData({
          title: d.title,
          description: d.description,
          category: d.category,
          impact: d.impact,
          probability: d.probability,
          ownerId: d.ownerId,
        });
        setModalMode('edit');
        setModalOpen(true);
      })
      .append('title')
      .text(d => `${d.title} (Impact: ${d.impact.replace('_', ' ')}, Probability: ${d.probability.replace('_', ' ')}, Status: ${d.status})`);

    // Legend
    const legend = svg.append('g')
      .attr('transform', `translate(${width + margin.right - 100}, ${0})`);

    Object.entries(RISK_STATUS_COLORS).forEach(([status, color], i) => {
      const legendRow = legend.append('g')
        .attr('transform', `translate(0, ${i * 20})`);

      legendRow.append('rect')
        .attr('width', 10)
        .attr('height', 10)
        .attr('fill', color);

      legendRow.append('text')
        .attr('x', 15)
        .attr('y', 5)
        .attr('dy', '0.35em')
        .style('text-anchor', 'start')
        .style('font-size', '10px')
        .style('fill', 'var(--foreground)')
        .text(status.replace('_', ' '));
    });

  }, [risks]);

  useEffect(() => {
    drawMatrix();
  }, [risks, drawMatrix]);

  const openCreateModal = () => {
    setModalMode('create');
    setSelectedRisk(null);
    setFormData({
      title: '',
      description: '',
      category: 'technical',
      impact: 'medium',
      probability: 'medium',
      ownerId: userId,
    });
    setModalOpen(true);
  };

  const closeModal = useCallback(() => {
    setModalOpen(false);
    setModalMode(null);
    setSelectedRisk(null);
    setFormData({
      title: '',
      description: '',
      category: 'technical',
      impact: 'medium',
      probability: 'medium',
      ownerId: userId,
    });
  }, [userId]);

  const handleSubmit = async (event: React.FormEvent) => {
