/**
 * ProjectsDashboard
 * ---------------------------------------------------------------------------
 * Core project management hub that exposes:
 * - Project list + detail pane
 * - Modal-based creation/editing flow for manual operators
 * - Legacy prompt-based fallback so Alden/CLI retain authority
 * - Tabs for Kanban, Gantt, and Risk experiences (all with test IDs)
 */

import React, { useEffect, useMemo, useState, useCallback } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { CoreBridge } from '../../bridges/CoreBridge';
import type { Project, ProjectPriority, ProjectStatus } from '../../types/agents';
import { KanbanBoard } from './KanbanBoard';
import { GanttChart } from './GanttChart';
import { RiskMatrix } from './RiskMatrix';

import { ResourceManagement } from './ResourceManagement';
import { PremiumModal } from '../shared/premium/PremiumModal';
import { PremiumTextarea } from '../shared/premium/PremiumTextarea';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';

interface ProjectsDashboardProps {
  userId: string;
  embedded?: boolean;
}

type DashboardTab = 'projects' | 'kanban' | 'gantt' | 'risks' | 'resources'; // Added 'resources'

interface ProjectFormState {
  name: string;
  description: string;
  ownerId: string;
  status: ProjectStatus;
  priority: ProjectPriority;
}

const DASHBOARD_TABS: Array<{
  id: DashboardTab;
  label: string;
  description: string;
  testId: string;
}> = [
  { id: 'projects', label: 'Projects', description: 'Roadmap + detail view', testId: 'projects-tab-projects' },
  { id: 'kanban', label: 'Kanban', description: 'Flow of work', testId: 'projects-tab-kanban' },
  { id: 'gantt', label: 'Gantt Timeline', description: 'Schedule + dependencies', testId: 'projects-tab-gantt' },
  { id: 'risks', label: 'Risk Matrix', description: 'Impact vs probability', testId: 'projects-tab-risks' },
  { id: 'resources', label: 'Resources', description: 'Team & allocations', testId: 'projects-tab-resources' }, // New tab
];


const createDefaultFormState = (userId: string): ProjectFormState => ({
  name: '',
  description: '',
  ownerId: userId,
  status: 'planning',
  priority: 'medium',
});

export const ProjectsDashboard: React.FC<ProjectsDashboardProps> = ({ userId, embedded = false }) => {
  const bridge = useMemo(() => new CoreBridge({ userId }), [userId]);
  const [loading, setLoading] = useState(true);
  const [projects, setProjects] = useState<Project[]>([]);
  const [selectedProjectId, setSelectedProjectId] = useState<string | null>(null);
  const navigate = useNavigate();
  const location = useLocation();

  const activeTab: DashboardTab = useMemo(() => {
    if (location.pathname.includes('/kanban')) return 'kanban';
    if (location.pathname.includes('/gantt')) return 'gantt';
    if (location.pathname.includes('/risks')) return 'risks';
    if (location.pathname.includes('/resources')) return 'resources';
    return 'projects';
  }, [location.pathname]);
  const [error, setError] = useState<string | null>(null);
  const [successMessage, setSuccessMessage] = useState<string | null>(null);

  const [showProjectModal, setShowProjectModal] = useState(false);
  const [projectForm, setProjectForm] = useState<ProjectFormState>(createDefaultFormState(userId));
  const [modalMode, setModalMode] = useState<'create' | 'edit'>('create');
  const [formError, setFormError] = useState<string | null>(null);
  const [submitting, setSubmitting] = useState(false);

  const errorMessage = (err: unknown): string => (
    err instanceof Error ? err.message : String(err)
  );

  const selectedProject = projects.find((project) => project.projectId === selectedProjectId) || null;

  useEffect(() => {
    if (projects.length > 0 && !selectedProjectId) {
      setSelectedProjectId(projects[0].projectId);
    }
  }, [projects, selectedProjectId]);

  useEffect(() => {
    if (!successMessage) return;
    const timer = setTimeout(() => setSuccessMessage(null), 3000);
    return () => clearTimeout(timer);
  }, [successMessage]);

  const loadProjects = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      await bridge.initialize();

      const result = await bridge.listProjects(userId);
      if (result.success) {
        setProjects(result.data);
        if (result.data.length > 0) {
          setSelectedProjectId((prev) => prev ?? result.data[0].projectId);
        }
      } else {
        setError(result.error);
      }
    } catch (err: unknown) {
      console.error('[ProjectsDashboard] Failed to load projects', err);
      setError(errorMessage(err) || 'Unable to load projects');
    } finally {
      setLoading(false);
    }
  }, [bridge, userId]);

  useEffect(() => {
    loadProjects();
  }, [userId, loadProjects]);

  const handlePromptCreateProject = async () => {
    const name = window.prompt('Project name (legacy prompt flow):');
    if (!name) return;
    const description = window.prompt('Project description:');
    if (!description) return;

    try {
      setSubmitting(true);
      const result = await bridge.createProject({ userId, name, description });
      if (result.success) {
        setProjects((prev) => [...prev, result.data]);
        setSelectedProjectId(result.data.projectId);
        setSuccessMessage(`Project "${result.data.name}" created via prompt flow`);
      } else {
        setError(result.error);
      }
    } catch (err: unknown) {
      setError(errorMessage(err) || 'Failed to create project');
    } finally {
      setSubmitting(false);
    }
  };

  const openCreateModal = () => {
    setModalMode('create');
    setProjectForm(createDefaultFormState(userId));
    setFormError(null);
    setShowProjectModal(true);
  };

  const openEditModal = () => {
    if (!selectedProject) return;
    setModalMode('edit');
    setProjectForm({
      name: selectedProject.name,
      description: selectedProject.description,
      ownerId: selectedProject.ownerId,
      status: selectedProject.status,
      priority: selectedProject.priority,
    });
    setFormError(null);
    setShowProjectModal(true);
  };

  const closeModal = () => {
    setShowProjectModal(false);
    setFormError(null);
    setSubmitting(false);
  };

  const handleProjectFormSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    setFormError(null);

    if (!projectForm.name.trim() || !projectForm.description.trim()) {
      setFormError('Name and description are required.');
      return;
    }

    try {
      setSubmitting(true);
      if (modalMode === 'create') {
        const result = await bridge.createProject({
          userId,
          name: projectForm.name.trim(),
          description: projectForm.description.trim(),
        });

        if (result.success) {
          setProjects((prev) => [...prev, result.data]);
          setSelectedProjectId(result.data.projectId);
          setSuccessMessage(`Project "${result.data.name}" created`);
          closeModal();
        } else {
          setFormError(result.error);
        }
      } else if (selectedProject) {
        // Backend currently supports status updates; persist other edits locally
        if (projectForm.status !== selectedProject.status) {
          const updateResult = await bridge.updateProjectStatus(selectedProject.projectId, projectForm.status);
          if (!updateResult.success) {
            setFormError(updateResult.error);
            setSubmitting(false);
            return;
          }
        }

        setProjects((prev) =>
          prev.map((project) =>
            project.projectId === selectedProject.projectId
              ? {
                  ...project,
                  name: projectForm.name.trim(),
                  description: projectForm.description.trim(),
                  priority: projectForm.priority,
                  status: projectForm.status,
                }
              : project
          )
        );
        setSuccessMessage(`Project "${projectForm.name}" updated`);
        closeModal();
      }
    } catch (err: unknown) {
      console.error('[ProjectsDashboard] Project form submit failed', err);
      setFormError(errorMessage(err) || 'Unable to save project');
    } finally {
      setSubmitting(false);
    }
  };

  const handleArchiveProject = async () => {
    if (!selectedProject) return;
    const confirmed = window.confirm(
      `Archive ${selectedProject.name}? This hides the project from Kanban/Gantt views but preserves data.`
    );
    if (!confirmed) return;

    try {
      const result = await bridge.updateProjectStatus(selectedProject.projectId, 'archived');
      if (result.success) {
        setProjects((prev) =>
          prev.map((project) =>
            project.projectId === selectedProject.projectId ? { ...project, status: 'archived' } : project
          )
        );
        setSuccessMessage(`Project "${selectedProject.name}" archived`);
      } else {
        setError(result.error);
      }
    } catch (err: unknown) {
      setError(errorMessage(err) || 'Failed to archive project');
    }
  };

  const handleTabKeyDown = (event: React.KeyboardEvent<HTMLButtonElement>, index: number) => {
    if (event.key !== 'ArrowRight' && event.key !== 'ArrowLeft') {
      return;
    }
    event.preventDefault();
    const nextIndex =
      event.key === 'ArrowRight'
        ? (index + 1) % DASHBOARD_TABS.length
        : (index - 1 + DASHBOARD_TABS.length) % DASHBOARD_TABS.length;
    
    const nextTabId = DASHBOARD_TABS[nextIndex].id;
    if (nextTabId === 'projects') {
      navigate('/core/projects');
    } else {
      navigate(`/core/projects/${nextTabId}`);
    }
  };

  const activeProjects = projects.filter((project) => project.status !== 'archived');
  const atRiskProjects = projects.filter((project) => project.status === 'at_risk');

  return (
    <div className={embedded ? "h-full flex flex-col" : "min-h-screen bg-alden-bg"} data-testid="projects-dashboard">
      <div className={embedded ? "bg-transparent" : "bg-alden-panel shadow-lg"}>
        {!embedded && (
          <div className="px-8 py-6">
          <div className="flex flex-col gap-4 md:flex-row md:items-center md:justify-between">
            <div>
              <p className="text-sm uppercase tracking-wide text-alden-gold font-semibold">Core Projects</p>
              <h1 className="text-3xl font-bold text-alden-text-primary">Project Management Command Center</h1>
              <p className="mt-2 text-alden-text-secondary max-w-2xl">
                Monitor schedules, unblock Alden, and keep every stakeholder aligned. Alden retains full command
                authority through CoreBridge, while human operators gain a polished modal workflow.
              </p>
            </div>

            <div className="flex flex-wrap gap-3">
              <button
                onClick={openCreateModal}
                className="inline-flex items-center justify-center rounded-md bg-blue-600 px-4 py-2 text-sm font-semibold text-white shadow hover:bg-blue-700 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-500"
              >
                + Create Project
              </button>
              <button
                onClick={handlePromptCreateProject}
                className="inline-flex items-center justify-center rounded-md border border-alden-border px-4 py-2 text-sm font-semibold text-alden-text-primary hover:bg-alden-border/50"
              >
                Prompt Flow
              </button>
            </div>
          </div>

          <div className="mt-6 grid grid-cols-1 gap-4 md:grid-cols-3">
            <StatCard label="Active Projects" value={activeProjects.length} subtext="Visible across tabs" />
            <StatCard label="At Risk" value={atRiskProjects.length} subtext="Requires Alden escalation" />
            <StatCard label="Total Projects" value={projects.length} subtext="Includes archived work" />
          </div>
          </div>
        )}

        {embedded && (
           <div className="px-6 py-4 flex justify-between items-center border-b border-alden-border/50 shrink-0">
              <div className="flex gap-4">
                 <StatCard label="Active" value={activeProjects.length} />
                 <StatCard label="At Risk" value={atRiskProjects.length} />
              </div>
              <div className="flex gap-2">
                <button
                  onClick={openCreateModal}
                  className="px-3 py-1.5 text-xs font-semibold rounded-md bg-blue-600 text-white hover:bg-blue-700"
                >
                  + New Project
                </button>
              </div>
           </div>
        )}

        <div className="border-t border-alden-border">
          <nav className="-mb-px flex flex-wrap gap-4 px-6" aria-label="Project dashboard views">
            {DASHBOARD_TABS.map((tab, index) => (
              <button
                key={tab.id}
                id={`projects-tab-${tab.id}`}
                type="button"
                role="tab"
                data-testid={tab.testId}
                aria-selected={activeTab === tab.id}
                aria-controls={`projects-panel-${tab.id}`}
                onKeyDown={(event) => handleTabKeyDown(event, index)}
                onClick={() => {
                  if (tab.id === 'projects') {
                    navigate('/core/projects');
                  } else {
                    navigate(`/core/projects/${tab.id}`);
                  }
                }}
                className={`flex flex-col border-b-2 px-4 py-3 text-left transition ${
                  activeTab === tab.id
                    ? 'border-indigo-500 text-alden-text-primary'
                    : 'border-transparent text-alden-text-secondary hover:text-gray-700'
                }`}
              >
                <span className="text-sm font-semibold">{tab.label}</span>
                <span className="text-xs">{tab.description}</span>
              </button>
            ))}
          </nav>
        </div>
      </div>

      {successMessage && (
        <div
          className="mx-6 mt-4 rounded-md border border-emerald-200 bg-emerald-50 px-4 py-3 text-sm text-emerald-900"
          role="status"
          aria-live="polite"
        >
          {successMessage}
        </div>
      )}

      {error && (
        <div className="mx-6 mt-4 rounded-md border border-red-200 bg-red-50 px-4 py-3 text-sm text-red-900" role="alert">
          {error}
        </div>
      )}

      <div className="px-6 py-8">
        {loading ? (
          <div className="flex items-center justify-center py-16">
            <div className="text-center">
              <div className="mx-auto mb-4 h-12 w-12 animate-spin rounded-full border-b-2 border-indigo-500"></div>
              <p className="text-alden-text-secondary">Loading projectsÆ’?Ä°</p>
            </div>
          </div>
        ) : (
          <>
            {activeTab === 'projects' && (
              <div
                id="projects-panel-projects"
                role="tabpanel"
                aria-labelledby="projects-tab-projects"
