            </div>
          )}
          {project.targetEndDate && (
            <div>
              <dt className="text-sm font-medium text-alden-text-secondary">Target End Date</dt>
              <dd className="mt-1 text-sm text-alden-text-primary">
                {new Date(project.targetEndDate).toLocaleDateString()}
              </dd>
            </div>
          )}
          {project.budget && (
            <div>
              <dt className="text-sm font-medium text-alden-text-secondary">Budget</dt>
              <dd className="mt-1 text-sm text-alden-text-primary">
                ${project.budget.toLocaleString()} / ${project.budgetSpent.toLocaleString()} spent
              </dd>
            </div>
          )}
        </dl>
      </div>

      {project.teamMembers?.length ? (
        <div>
          <h4 className="text-sm font-medium text-alden-text-secondary mb-2">Team Members</h4>
          <div className="flex flex-wrap gap-2">
            {project.teamMembers.map((member) => (
              <span
                key={member}
                className="inline-flex items-center rounded-full bg-indigo-50 px-3 py-1 text-sm font-medium text-indigo-700"
              >
                {member}
              </span>
            ))}
          </div>
        </div>
      ) : null}

      {project.tags?.length ? (
        <div>
          <h4 className="text-sm font-medium text-alden-text-secondary mb-2">Tags</h4>
          <div className="flex flex-wrap gap-2">
            {project.tags.map((tag) => (
              <span
                key={tag}
                className="inline-flex items-center rounded-full bg-alden-border/30 px-3 py-1 text-sm font-medium text-gray-700"
              >
                {tag}
              </span>
            ))}
          </div>
        </div>
      ) : null}
    </div>
  );
};

