    content_type: ['conversation', 'task', 'knowledge'][i % 3] as 'conversation' | 'task' | 'knowledge',
    persona_id: ['alden', 'alice', 'mimic', 'sentry'][i % 4] as 'alden' | 'alice' | 'mimic' | 'sentry',
    created_at: Date.now() - i * 1000,
    access_count: Math.floor(Math.random() * 50),
    tags: ['test', `category_${i % 5}`],
    is_private: i % 10 === 0, // 10% are private
  }));

  // Generate 50 tasks
  const tasks = Array.from({ length: 50 }, (_, i) => ({
    id: `task_${i}`,
    title: `Task ${i} for performance testing`,
    description: `Description for task ${i}`,
    priority: ['low', 'medium', 'high', 'critical'][i % 4] as 'low' | 'medium' | 'high' | 'critical',
    status: ['pending', 'in_progress', 'completed', 'blocked'][i % 4] as 'pending' | 'in_progress' | 'completed' | 'blocked',
    projectId: `proj_${i % 5}`,
    createdAt: Date.now() - i * 2000,
  }));

  // Generate 10 projects
  const projects = Array.from({ length: 10 }, (_, i) => ({
    id: `proj_${i}`,
    name: `Project ${i} for performance testing`,
    description: `Description for project ${i}`,
    status: ['active', 'archived', 'completed'][i % 3] as 'active' | 'archived' | 'completed',
    startDate: new Date(Date.now() - (i + 1) * 10 * 86400000).toISOString().split('T')[0],
    endDate: new Date(Date.now() + (i + 1) * 20 * 86400000).toISOString().split('T')[0],
    createdAt: Date.now() - (i + 1) * 10 * 86400000,
  }));

  seedMockData({
    memories,
    tasks,
    projects,
  });
}

