  const contentTypes: Array<'conversation' | 'task' | 'knowledge' | 'note'> = [
    'conversation',
    'task',
    'knowledge',
    'note',
  ];
  const personaIds: Array<'alden' | 'alice' | 'mimic' | 'sentry'> = [
    'alden',
    'alice',
    'mimic',
    'sentry',
  ];
  const tags = ['test', 'e2e', 'automated', 'random'];

  return {
    id: `mem_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
    content: `Random memory ${Date.now()}`,
    content_type: contentTypes[Math.floor(Math.random() * contentTypes.length)],
    persona_id: personaIds[Math.floor(Math.random() * personaIds.length)],
    created_at: Date.now(),
    access_count: Math.floor(Math.random() * 10),
    tags: tags.slice(0, Math.floor(Math.random() * tags.length) + 1),
    is_private: Math.random() > 0.8,
  };
}

