/**
 * ProjectsDashboard
 * ---------------------------------------------------------------------------
 * Core project management hub that exposes:
 * - Project list + detail pane
 * - Modal-based creation/editing flow for manual operators
 * - Legacy prompt-based fallback so Alden/CLI retain authority
 * - Tabs for Kanban, Gantt, and Risk experiences (all with test IDs)
 */

import React, { useEffect, useMemo, useState, useCallback } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { CoreBridge } from '../../bridges/CoreBridge';
import type { Project, ProjectPriority, ProjectStatus } from '../../types/agents';
import { KanbanBoard } from './KanbanBoard';
import { GanttChart } from './GanttChart';
import { RiskMatrix } from './RiskMatrix';

import { ResourceManagement } from './ResourceManagement';
import { PremiumModal } from '../shared/premium/PremiumModal';
import { PremiumTextarea } from '../shared/premium/PremiumTextarea';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';

interface ProjectsDashboardProps {
  userId: string;
  embedded?: boolean;
}

type DashboardTab = 'projects' | 'kanban' | 'gantt' | 'risks' | 'resources'; // Added 'resources'

interface ProjectFormState {
  name: string;
  description: string;
  ownerId: string;
  status: ProjectStatus;
  priority: ProjectPriority;
}

const DASHBOARD_TABS: Array<{
  id: DashboardTab;
  label: string;
  description: string;
  testId: string;
}> = [
  { id: 'projects', label: 'Projects', description: 'Roadmap + detail view', testId: 'projects-tab-projects' },
  { id: 'kanban', label: 'Kanban', description: 'Flow of work', testId: 'projects-tab-kanban' },
  { id: 'gantt', label: 'Gantt Timeline', description: 'Schedule + dependencies', testId: 'projects-tab-gantt' },
  { id: 'risks', label: 'Risk Matrix', description: 'Impact vs probability', testId: 'projects-tab-risks' },
  { id: 'resources', label: 'Resources', description: 'Team & allocations', testId: 'projects-tab-resources' }, // New tab
];


const createDefaultFormState = (userId: string): ProjectFormState => ({
  name: '',
  description: '',
  ownerId: userId,
  status: 'planning',
  priority: 'medium',
});

export const ProjectsDashboard: React.FC<ProjectsDashboardProps> = ({ userId, embedded = false }) => {
  const bridge = useMemo(() => new CoreBridge({ userId }), [userId]);
  const [loading, setLoading] = useState(true);
  const [projects, setProjects] = useState<Project[]>([]);
  const [selectedProjectId, setSelectedProjectId] = useState<string | null>(null);
  const navigate = useNavigate();
  const location = useLocation();

  const activeTab: DashboardTab = useMemo(() => {
    if (location.pathname.includes('/kanban')) return 'kanban';
    if (location.pathname.includes('/gantt')) return 'gantt';
    if (location.pathname.includes('/risks')) return 'risks';
    if (location.pathname.includes('/resources')) return 'resources';
    return 'projects';
  }, [location.pathname]);
  const [error, setError] = useState<string | null>(null);
  const [successMessage, setSuccessMessage] = useState<string | null>(null);

  const [showProjectModal, setShowProjectModal] = useState(false);
  const [projectForm, setProjectForm] = useState<ProjectFormState>(createDefaultFormState(userId));
  const [modalMode, setModalMode] = useState<'create' | 'edit'>('create');
  const [formError, setFormError] = useState<string | null>(null);
  const [submitting, setSubmitting] = useState(false);

  const errorMessage = (err: unknown): string => (
    err instanceof Error ? err.message : String(err)
  );

  const selectedProject = projects.find((project) => project.projectId === selectedProjectId) || null;

  useEffect(() => {
    if (projects.length > 0 && !selectedProjectId) {
      setSelectedProjectId(projects[0].projectId);
    }
  }, [projects, selectedProjectId]);

  useEffect(() => {
    if (!successMessage) return;
    const timer = setTimeout(() => setSuccessMessage(null), 3000);
    return () => clearTimeout(timer);
  }, [successMessage]);

  const loadProjects = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      await bridge.initialize();

      const result = await bridge.listProjects(userId);
      if (result.success) {
        setProjects(result.data);
        if (result.data.length > 0) {
          setSelectedProjectId((prev) => prev ?? result.data[0].projectId);
        }
      } else {
        setError(result.error);
      }
    } catch (err: unknown) {
      console.error('[ProjectsDashboard] Failed to load projects', err);
      setError(errorMessage(err) || 'Unable to load projects');
    } finally {
      setLoading(false);
    }
  }, [bridge, userId]);

  useEffect(() => {
    loadProjects();
  }, [userId, loadProjects]);

  const handlePromptCreateProject = async () => {
    const name = window.prompt('Project name (legacy prompt flow):');
    if (!name) return;
    const description = window.prompt('Project description:');
    if (!description) return;

    try {
      setSubmitting(true);
      const result = await bridge.createProject({ userId, name, description });
      if (result.success) {
        setProjects((prev) => [...prev, result.data]);
        setSelectedProjectId(result.data.projectId);
        setSuccessMessage(`Project "${result.data.name}" created via prompt flow`);
      } else {
        setError(result.error);
      }
    } catch (err: unknown) {
      setError(errorMessage(err) || 'Failed to create project');
    } finally {
      setSubmitting(false);
    }
  };

  const openCreateModal = () => {
    setModalMode('create');
    setProjectForm(createDefaultFormState(userId));
    setFormError(null);
    setShowProjectModal(true);
  };

  const openEditModal = () => {
    if (!selectedProject) return;
    setModalMode('edit');
    setProjectForm({
      name: selectedProject.name,
      description: selectedProject.description,
      ownerId: selectedProject.ownerId,
      status: selectedProject.status,
      priority: selectedProject.priority,
    });
    setFormError(null);
    setShowProjectModal(true);
  };

  const closeModal = () => {
    setShowProjectModal(false);
    setFormError(null);
    setSubmitting(false);
  };

  const handleProjectFormSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    setFormError(null);

    if (!projectForm.name.trim() || !projectForm.description.trim()) {
      setFormError('Name and description are required.');
      return;
    }

    try {
      setSubmitting(true);
      if (modalMode === 'create') {
        const result = await bridge.createProject({
          userId,
          name: projectForm.name.trim(),
          description: projectForm.description.trim(),
        });

        if (result.success) {
          setProjects((prev) => [...prev, result.data]);
          setSelectedProjectId(result.data.projectId);
          setSuccessMessage(`Project "${result.data.name}" created`);
          closeModal();
        } else {
          setFormError(result.error);
        }
      } else if (selectedProject) {
        // Backend currently supports status updates; persist other edits locally
        if (projectForm.status !== selectedProject.status) {
          const updateResult = await bridge.updateProjectStatus(selectedProject.projectId, projectForm.status);
          if (!updateResult.success) {
            setFormError(updateResult.error);
            setSubmitting(false);
            return;
          }
        }

        setProjects((prev) =>
          prev.map((project) =>
            project.projectId === selectedProject.projectId
              ? {
                  ...project,
                  name: projectForm.name.trim(),
                  description: projectForm.description.trim(),
                  priority: projectForm.priority,
                  status: projectForm.status,
                }
              : project
          )
        );
        setSuccessMessage(`Project "${projectForm.name}" updated`);
        closeModal();
      }
    } catch (err: unknown) {
      console.error('[ProjectsDashboard] Project form submit failed', err);
      setFormError(errorMessage(err) || 'Unable to save project');
    } finally {
      setSubmitting(false);
    }
  };

  const handleArchiveProject = async () => {
    if (!selectedProject) return;
    const confirmed = window.confirm(
      `Archive ${selectedProject.name}? This hides the project from Kanban/Gantt views but preserves data.`
    );
    if (!confirmed) return;

    try {
      const result = await bridge.updateProjectStatus(selectedProject.projectId, 'archived');
      if (result.success) {
        setProjects((prev) =>
          prev.map((project) =>
            project.projectId === selectedProject.projectId ? { ...project, status: 'archived' } : project
          )
        );
        setSuccessMessage(`Project "${selectedProject.name}" archived`);
      } else {
        setError(result.error);
      }
    } catch (err: unknown) {
      setError(errorMessage(err) || 'Failed to archive project');
    }
  };

  const handleTabKeyDown = (event: React.KeyboardEvent<HTMLButtonElement>, index: number) => {
    if (event.key !== 'ArrowRight' && event.key !== 'ArrowLeft') {
      return;
    }
    event.preventDefault();
    const nextIndex =
      event.key === 'ArrowRight'
        ? (index + 1) % DASHBOARD_TABS.length
        : (index - 1 + DASHBOARD_TABS.length) % DASHBOARD_TABS.length;
    
    const nextTabId = DASHBOARD_TABS[nextIndex].id;
    if (nextTabId === 'projects') {
      navigate('/core/projects');
    } else {
      navigate(`/core/projects/${nextTabId}`);
    }
  };

  const activeProjects = projects.filter((project) => project.status !== 'archived');
  const atRiskProjects = projects.filter((project) => project.status === 'at_risk');

  return (
    <div className={embedded ? "h-full flex flex-col" : "min-h-screen bg-alden-bg"} data-testid="projects-dashboard">
      <div className={embedded ? "bg-transparent" : "bg-alden-panel shadow-lg"}>
        {!embedded && (
          <div className="px-8 py-6">
          <div className="flex flex-col gap-4 md:flex-row md:items-center md:justify-between">
            <div>
              <p className="text-sm uppercase tracking-wide text-alden-gold font-semibold">Core Projects</p>
              <h1 className="text-3xl font-bold text-alden-text-primary">Project Management Command Center</h1>
              <p className="mt-2 text-alden-text-secondary max-w-2xl">
                Monitor schedules, unblock Alden, and keep every stakeholder aligned. Alden retains full command
                authority through CoreBridge, while human operators gain a polished modal workflow.
              </p>
            </div>

            <div className="flex flex-wrap gap-3">
              <button
                onClick={openCreateModal}
                className="inline-flex items-center justify-center rounded-md bg-blue-600 px-4 py-2 text-sm font-semibold text-white shadow hover:bg-blue-700 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-500"
              >
                + Create Project
              </button>
              <button
                onClick={handlePromptCreateProject}
                className="inline-flex items-center justify-center rounded-md border border-alden-border px-4 py-2 text-sm font-semibold text-alden-text-primary hover:bg-alden-border/50"
              >
                Prompt Flow
              </button>
            </div>
          </div>

          <div className="mt-6 grid grid-cols-1 gap-4 md:grid-cols-3">
            <StatCard label="Active Projects" value={activeProjects.length} subtext="Visible across tabs" />
            <StatCard label="At Risk" value={atRiskProjects.length} subtext="Requires Alden escalation" />
            <StatCard label="Total Projects" value={projects.length} subtext="Includes archived work" />
          </div>
          </div>
        )}

        {embedded && (
           <div className="px-6 py-4 flex justify-between items-center border-b border-alden-border/50 shrink-0">
              <div className="flex gap-4">
                 <StatCard label="Active" value={activeProjects.length} />
                 <StatCard label="At Risk" value={atRiskProjects.length} />
              </div>
              <div className="flex gap-2">
                <button
                  onClick={openCreateModal}
                  className="px-3 py-1.5 text-xs font-semibold rounded-md bg-blue-600 text-white hover:bg-blue-700"
                >
                  + New Project
                </button>
              </div>
           </div>
        )}

        <div className="border-t border-alden-border">
          <nav className="-mb-px flex flex-wrap gap-4 px-6" aria-label="Project dashboard views">
            {DASHBOARD_TABS.map((tab, index) => (
              <button
                key={tab.id}
                id={`projects-tab-${tab.id}`}
                type="button"
                role="tab"
                data-testid={tab.testId}
                aria-selected={activeTab === tab.id}
                aria-controls={`projects-panel-${tab.id}`}
                onKeyDown={(event) => handleTabKeyDown(event, index)}
                onClick={() => {
                  if (tab.id === 'projects') {
                    navigate('/core/projects');
                  } else {
                    navigate(`/core/projects/${tab.id}`);
                  }
                }}
                className={`flex flex-col border-b-2 px-4 py-3 text-left transition ${
                  activeTab === tab.id
                    ? 'border-indigo-500 text-alden-text-primary'
                    : 'border-transparent text-alden-text-secondary hover:text-gray-700'
                }`}
              >
                <span className="text-sm font-semibold">{tab.label}</span>
                <span className="text-xs">{tab.description}</span>
              </button>
            ))}
          </nav>
        </div>
      </div>

      {successMessage && (
        <div
          className="mx-6 mt-4 rounded-md border border-emerald-200 bg-emerald-50 px-4 py-3 text-sm text-emerald-900"
          role="status"
          aria-live="polite"
        >
          {successMessage}
        </div>
      )}

      {error && (
        <div className="mx-6 mt-4 rounded-md border border-red-200 bg-red-50 px-4 py-3 text-sm text-red-900" role="alert">
          {error}
        </div>
      )}

      <div className="px-6 py-8">
        {loading ? (
          <div className="flex items-center justify-center py-16">
            <div className="text-center">
              <div className="mx-auto mb-4 h-12 w-12 animate-spin rounded-full border-b-2 border-indigo-500"></div>
              <p className="text-alden-text-secondary">Loading projectsÆ’?Ä°</p>
            </div>
          </div>
        ) : (
          <>
            {activeTab === 'projects' && (
              <div
                id="projects-panel-projects"
                role="tabpanel"
                aria-labelledby="projects-tab-projects"
                className="grid grid-cols-1 gap-8 lg:grid-cols-3"
              >
                <div className="rounded-lg border border-alden-border bg-alden-panel shadow-sm lg:col-span-1">
                  <div className="flex items-center justify-between border-b border-alden-border px-5 py-4">
                    <div>
                      <h2 className="text-lg font-semibold text-alden-text-primary">Projects</h2>
                      <p className="text-sm text-alden-text-secondary">Select a project to view details.</p>
                    </div>
                    <span className="rounded-full bg-alden-border/30 px-3 py-1 text-xs font-semibold text-alden-text-secondary">
                      {projects.length} total
                    </span>
                  </div>

                  <ul className="max-h-[32rem] divide-y divide-gray-100 overflow-auto" data-testid="project-list">
                    {projects.length === 0 && (
                      <li className="px-5 py-6 text-center text-sm text-alden-text-secondary">
                        No projects yet. Use the Create Project button to get started.
                      </li>
                    )}
                    {projects.map((project) => {
                      const isSelected = project.projectId === selectedProjectId;
                      return (
                        <li key={project.projectId}>
                          <button
                            type="button"
                            data-testid="project-item"
                            aria-current={isSelected}
                            onClick={() => setSelectedProjectId(project.projectId)}
                            className={`w-full px-5 py-4 text-left transition ${
                              isSelected ? 'bg-indigo-50' : 'hover:bg-alden-bg'
                            }`}
                          >
                            <div className="flex items-start justify-between">
                              <div>
                                <p className="text-sm font-semibold text-alden-text-primary">{project.name}</p>
                                <p className="mt-1 text-xs text-alden-text-secondary">{project.description}</p>
                              </div>
                              <span
                                className={`text-xs font-semibold ${
                                  project.status === 'archived'
                                    ? 'text-gray-400'
                                    : project.status === 'at_risk'
                                    ? 'text-red-500'
                                    : 'text-emerald-500'
                                }`}
                              >
                                {project.status.replace('_', ' ')}
                              </span>
                            </div>
                            <div className="mt-3 flex flex-wrap gap-2 text-xs text-alden-text-secondary">
                              <span className="rounded-full bg-alden-border/30 px-2 py-1 font-medium">
                                Owner: {project.ownerId}
                              </span>
                              <span className="rounded-full bg-alden-border/30 px-2 py-1 font-medium capitalize">
                                Priority: {project.priority}
                              </span>
                            </div>
                          </button>
                        </li>
                      );
                    })}
                  </ul>
                </div>

                <div className="rounded-lg border border-alden-border bg-alden-panel p-6 shadow-sm lg:col-span-2" data-testid="selected-project">
                  {selectedProject ? (
                    <>
                      <div className="flex flex-wrap items-start justify-between gap-3">
                        <div>
                          <p className="text-xs uppercase tracking-wide text-alden-gold">Selected Project</p>
                          <h2 className="text-2xl font-bold text-alden-text-primary">{selectedProject.name}</h2>
                          <p className="mt-2 text-sm text-alden-text-secondary">{selectedProject.description}</p>
                        </div>
                        <div className="flex flex-wrap gap-2">
                          <button
                            type="button"
                            onClick={openEditModal}
                            className="rounded-md border border-alden-border px-3 py-2 text-sm font-medium text-gray-700 hover:bg-alden-bg"
                          >
                            Edit
                          </button>
                          <button
                            type="button"
                            onClick={handleArchiveProject}
                            className="rounded-md border border-red-200 px-3 py-2 text-sm font-medium text-red-600 hover:bg-red-50"
                          >
                            Archive
                          </button>
                        </div>
                      </div>

                      <div className="mt-6">
                        <ProjectOverview project={selectedProject} />
                      </div>
                    </>
                  ) : (
                    <div className="text-center text-sm text-alden-text-secondary">Select or create a project to view details.</div>
                  )}
                </div>
              </div>
            )}

            {activeTab === 'kanban' && (
              <section
                id="projects-panel-kanban"
                role="tabpanel"
                aria-labelledby="projects-tab-kanban"
                data-testid="projects-kanban-panel"
                className="rounded-lg border border-alden-border bg-alden-panel p-4 shadow-sm"
              >
                {selectedProject ? (
                  <KanbanBoard projectId={selectedProject.projectId} userId={userId} />
                ) : (
                  <EmptyState message="Select a project to view its Kanban board." />
                )}
              </section>
            )}

            {activeTab === 'gantt' && (
              <section
                id="projects-panel-gantt"
                role="tabpanel"
                aria-labelledby="projects-tab-gantt"
                data-testid="projects-gantt-panel"
                className="rounded-lg border border-alden-border bg-alden-panel p-4 shadow-sm"
              >
                {selectedProject ? (
                  <GanttChart projectId={selectedProject.projectId} userId={userId} />
                ) : (
                  <EmptyState message="Select a project to render the Gantt schedule." />
                )}
              </section>
            )}

            {activeTab === 'risks' && (
              <section
                id="projects-panel-risks"
                role="tabpanel"
                aria-labelledby="projects-tab-risks"
                data-testid="projects-risks-panel"
                className="rounded-lg border border-alden-border bg-alden-panel p-4 shadow-sm"
              >
                {selectedProject ? (
                  <RiskMatrix projectId={selectedProject.projectId} userId={userId} />
                ) : (
                  <EmptyState message="Select a project to review its risk posture." />
                )}
              </section>
            )}

            {activeTab === 'resources' && (
              <section
                id="projects-panel-resources"
                role="tabpanel"
                aria-labelledby="projects-tab-resources"
                data-testid="projects-resources-panel"
                className="rounded-lg border border-alden-border bg-alden-panel p-4 shadow-sm"
              >
                {selectedProject ? (
                  <ResourceManagement projectId={selectedProject.projectId} userId={userId} />
                ) : (
                  <EmptyState message="Select a project to manage its resources." />
                )}
              </section>
            )}
          </>
        )}
      </div>

      {showProjectModal && (
        <PremiumModal
          isOpen={showProjectModal}
          onClose={closeModal}
          title={modalMode === 'create' ? 'Create Project' : 'Edit Project'}
          size="lg"
        >
          <div
            className="space-y-6 rounded-xl p-6"
            style={{
              background: 'linear-gradient(135deg, rgba(11,17,36,0.95), rgba(0,240,255,0.08))',
              border: '1px solid rgba(0,205,255,0.45)',
              boxShadow: 'var(--shadow-glow-cyan, 0 0 24px rgba(0,240,255,0.18)), inset 0 0 12px var(--shadow-glow-gold, rgba(197,160,89,0.12))',
              color: 'var(--color-slate-300, #c5d2e8)'
            }}
          >
            <p className="text-sm text-[#8fa7c7]">
              {modalMode === 'create'
                ? 'Manual creation flow Æ’?" Alden can still create projects via prompt command.'
                : 'Update metadata or change lifecycle status.'}
            </p>

            <form onSubmit={handleProjectFormSubmit} className="space-y-4">
              <div className="space-y-2">
                <label htmlFor="project-name" className="block text-sm font-medium text-muted-foreground">Name</label>
                <Input
                  id="project-name"
                  value={projectForm.name}
                  onChange={(event) => setProjectForm((prev) => ({ ...prev, name: event.target.value }))}
                  placeholder="Project Falcon Zero"
                  required
                />
              </div>

              <PremiumTextarea
                label="Description"
                value={projectForm.description}
                onChange={(event) => setProjectForm((prev) => ({ ...prev, description: event.target.value }))}
                rows={4}
                placeholder="Brief summary of scope, timeline, and dependencies"
                required
              />

              <div className="grid grid-cols-1 gap-4 md:grid-cols-2">
                <div>
                  <label htmlFor="project-status-select" className="block text-sm font-medium text-alden-text-primary mb-2">Status</label>
                  <select
                    id="project-status-select"
                    value={projectForm.status}
                    onChange={(event) =>
                      setProjectForm((prev) => ({ ...prev, status: event.target.value as ProjectStatus }))
                    }
                    className="flex h-10 w-full rounded-lg border border-alden-cyan/35 bg-alden-panel px-3 py-2 text-sm text-alden-text-primary focus:outline-none focus:ring-2 focus:ring-[#0ef]/40"
                  >
                    <option value="planning">Planning</option>
                    <option value="active">Active</option>
                    <option value="on_hold">On Hold</option>
                    <option value="at_risk">At Risk</option>
                    <option value="completed">Completed</option>
                    <option value="archived">Archived</option>
                  </select>
                </div>

                <div>
                  <label htmlFor="project-priority-select" className="block text-sm font-medium text-alden-text-primary mb-2">Priority</label>
                  <select
                    id="project-priority-select"
                    value={projectForm.priority}
                    onChange={(event) =>
                      setProjectForm((prev) => ({ ...prev, priority: event.target.value as ProjectPriority }))
                    }
                    className="flex h-10 w-full rounded-lg border border-alden-cyan/35 bg-alden-panel px-3 py-2 text-sm text-alden-text-primary focus:outline-none focus:ring-2 focus:ring-[#0ef]/40"
                  >
                    <option value="critical">Critical</option>
                    <option value="high">High</option>
                    <option value="medium">Medium</option>
                    <option value="low">Low</option>
                  </select>
                </div>
              </div>

              {formError && (
                <div className="rounded-md border border-red-500/20 bg-red-500/10 px-3 py-2 text-sm text-red-400" role="alert">
                  {formError}
                </div>
              )}

              <div className="flex items-center justify-end gap-3 border-t border-white/10 pt-4">
                <Button
                  type="button"
                  variant="ghost"
                  onClick={closeModal}
                  className="text-[#8fa7c7] border border-white/15 hover:bg-alden-panel/5"
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  variant="default"
                  disabled={submitting}
                  className="bg-alden-cyan/12 text-alden-cyan border border-alden-cyan/40 hover:bg-alden-cyan/20 shadow-lg"
                >
                  {submitting ? 'Saving...' : modalMode === 'create' ? 'Create Project' : 'Save Changes'}
                </Button>
              </div>
            </form>
          </div>
        </PremiumModal>
      )}
    </div>
  );
};

const StatCard: React.FC<{ label: string; value: number; subtext?: string }> = ({ label, value, subtext }) => (
  <div className="rounded-lg border border-gray-100 bg-alden-bg px-4 py-3">
    <p className="text-xs uppercase tracking-wide text-alden-text-secondary">{label}</p>
    <p className="text-2xl font-bold text-alden-text-primary">{value}</p>
    {subtext && <p className="text-xs text-alden-text-secondary">{subtext}</p>}
  </div>
);

const EmptyState: React.FC<{ message: string }> = ({ message }) => (
  <div className="flex min-h-[12rem] items-center justify-center rounded-md border border-dashed border-alden-border bg-alden-bg">
    <p className="text-sm text-alden-text-secondary">{message}</p>
  </div>
);

const ProjectOverview: React.FC<{ project: Project | null }> = ({ project }) => {
  if (!project) {
    return null;
  }

  return (
    <div className="space-y-6">
      <div>
        <h3 className="text-lg font-medium text-alden-text-primary">Project Information</h3>
        <dl className="mt-4 grid grid-cols-1 gap-x-4 gap-y-6 sm:grid-cols-2">
          <div className="sm:col-span-2">
            <dt className="text-sm font-medium text-alden-text-secondary">Description</dt>
            <dd className="mt-1 text-sm text-alden-text-primary">{project.description}</dd>
          </div>
          <div>
            <dt className="text-sm font-medium text-alden-text-secondary">Status</dt>
            <dd className="mt-1 text-sm text-alden-text-primary capitalize">{project.status.replace('_', ' ')}</dd>
          </div>
          <div>
            <dt className="text-sm font-medium text-alden-text-secondary">Priority</dt>
            <dd className="mt-1 text-sm text-alden-text-primary capitalize">{project.priority}</dd>
          </div>
          <div>
            <dt className="text-sm font-medium text-alden-text-secondary">Progress</dt>
            <dd className="mt-1">
              <div className="flex items-center gap-2">
                <div className="h-2 flex-1 rounded-full bg-gray-200">
                  <div
                    className="h-2 rounded-full bg-indigo-500 transition-all"
                    style={{ width: `${Math.min(project.progressPercentage ?? 0, 100)}%` }}
                  />
                </div>
                <span className="text-sm text-alden-text-primary">{Math.round(project.progressPercentage ?? 0)}%</span>
              </div>
            </dd>
          </div>
          <div>
            <dt className="text-sm font-medium text-alden-text-secondary">Owner</dt>
            <dd className="mt-1 text-sm text-alden-text-primary">{project.ownerId}</dd>
          </div>
          {project.startDate && (
            <div>
              <dt className="text-sm font-medium text-alden-text-secondary">Start Date</dt>
              <dd className="mt-1 text-sm text-alden-text-primary">
                {new Date(project.startDate).toLocaleDateString()}
              </dd>
            </div>
          )}
          {project.targetEndDate && (
            <div>
