/**
 * Unified Command Center - Canonical Implementation
 *
 * Consolidates 3 previous versions into a single, flexible component that supports:
 * - Lite mode (simple content display) - DEFAULT
 * - Full mode (draggable dashboard tiles with react-rnd)
 * - 56px header height (MythTech standard)
 * - Performance optimizations (memoized subcomponents)
 * - Customizable theming per agent persona
 *
 * @version 2.0.0 - Sprint 2 Consolidation
 */

import React, { useState, useEffect, useMemo, useCallback, memo, ReactNode } from 'react';
import { Rnd } from 'react-rnd';
import { Lock, Unlock } from '@/components/ui/HearthlinkIcons';
import { RadialNavigationModal } from '../mythtech/navigation/RadialNavigationModal';
import { GlobalCommsStream } from '../mythtech/tasks/GlobalCommsStream';
import { PremiumFrame } from './premium/PremiumPanel';
import { useADHDUI } from '../ui/ADHDOptimizedInterface';
import { resolveAsset } from '@/utils/assets';
import CommandCenterHeader from './command-center/CommandCenterHeader';
import { CommandCenterSidebar } from './command-center/CommandCenterSidebar';
import { AgentIdentity, ThemeConfig } from './command-center/types';

// Re-export types
export type { AgentIdentity, ThemeConfig } from './command-center/types';

// ============================================================================
// Types & Interfaces
// ============================================================================

// ============================================================================
// Types & Interfaces
// ============================================================================

// AgentIdentity and ThemeConfig imported from ./command-center/types

export interface DashboardTile {
  id: string;
  x: number;
  y: number;
  width: number;
  height: number;
  locked?: boolean;
  content: ReactNode;
}

export type ViewMode = 'overview' | 'tasks' | 'calendar' | 'email' | 'chat' | string;

export interface TabItem {
  id: string;
  label: string;
}

export interface UnifiedCommandCenterProps {
  // Agent Identity (new API)
  identity?: AgentIdentity;

  // Legacy API (deprecated but supported for backward compatibility)
  title?: string;
  subtitle?: string;
  iconPath?: string;
  titleColor?: string;
  subtitleColor?: string;
  dateColor?: string;
  timeColor?: string;
  accentColor?: string;
  themeColor?: string;

  // User context
  userId?: string;

  // Mode selection
  mode?: 'lite' | 'full';  // 'lite' = simple content, 'full' = dashboard with tiles

  // Dashboard configuration (for 'full' mode)
  dashboardLayout?: DashboardTile[];
  onDashboardLayoutChange?: (layout: DashboardTile[]) => void;

  // Tabs (for 'lite' mode)
  tabs?: TabItem[];
  activeTab?: string;
  onTabChange?: (tabId: string) => void;

  // Views (for 'full' mode)
  activeView?: ViewMode;
  onViewChange?: (view: ViewMode) => void;
  availableViews?: Array<{ id: ViewMode; label: string }>;

  // View content renderers (for 'full' mode)
  renderOverview?: () => ReactNode;
  renderTasksView?: () => ReactNode;
  renderCalendarView?: () => ReactNode;
  renderEmailView?: () => ReactNode;
  renderChatView?: () => ReactNode;
  renderCustomView?: (viewId: string) => ReactNode;

  // Content (for 'lite' mode)
  children: ReactNode;

  // Sidebar/Chat
  sidebar?: ReactNode;
  sidebarTitle?: string;
  sidebarCollapsed?: boolean;
  expandedChatContent?: ReactNode;

  // Navigation
  onBackToLauncher?: () => void;
  onNavigateToModule?: (moduleName: string) => void;
  onNavigate?: (moduleName: string) => boolean | void;

  // Theme
  theme?: ThemeConfig;
  withFrame?: boolean;

  // Connection status
  isConnected?: boolean;
  statusLabel?: string;
  statusTone?: 'success' | 'warning' | 'error';

  // Header customization
  headerStyle?: React.CSSProperties;
  headerClassName?: string;
  headerActions?: ReactNode;
  onFocusAssist?: () => void;
  isFocusAssistActive?: boolean;

  // Content layout
  fullWidthContent?: boolean;
}

// ============================================================================
// Constants
// ============================================================================

const FRAME_PADDING = 16;

// Stable empty arrays to prevent re-renders
const EMPTY_DASHBOARD_LAYOUT: DashboardTile[] = [];
const EMPTY_TABS: TabItem[] = [];
const EMPTY_VIEWS: Array<{ id: ViewMode; label: string }> = [];

// ============================================================================
// Memoized Sidebar Component
// ============================================================================

// ============================================================================
// Memoized Sidebar Component
// ============================================================================

// Component extracted to ./command-center/CommandCenterSidebar.tsx

// ============================================================================
// Main Component
// ============================================================================

export const UnifiedCommandCenter: React.FC<UnifiedCommandCenterProps> = (props) => {
  const {
    identity: identityProp,
    title: legacyTitle,
    subtitle: legacySubtitle,
    iconPath: legacyIconPath,
    titleColor: legacyTitleColor,
    subtitleColor: legacySubtitleColor,
    accentColor: legacyAccentColor = '#00e6ff',
    themeColor: legacyThemeColor = '#c5d2e8',
    userId,
    mode = 'lite',
    dashboardLayout: initialDashboardLayout = EMPTY_DASHBOARD_LAYOUT,
    onDashboardLayoutChange,
    tabs = EMPTY_TABS,
    activeTab,
    onTabChange,
    activeView = 'overview',
    onViewChange,
    availableViews = EMPTY_VIEWS,
    renderOverview,
    renderTasksView,
    renderCalendarView,
    renderEmailView,
    renderChatView,
    renderCustomView,
    children,
    sidebar,
    sidebarTitle,
    sidebarCollapsed = false,
    expandedChatContent,
    onBackToLauncher,
    onNavigateToModule,
    onNavigate,
    theme: themeProp = {},
    withFrame = false,
    isConnected = true,
    statusLabel = 'System Online',
    statusTone = 'success',
    headerStyle,
    headerClassName,
    headerActions,
    fullWidthContent = false,
    onFocusAssist,
    isFocusAssistActive,
  } = props;

  // Build identity from legacy props if not provided directly
  const identity: AgentIdentity = identityProp || {
    name: legacyTitle?.toLowerCase().replace(/\s+/g, '-') || 'agent',
    displayName: legacyTitle || 'Agent',
    subtitle: legacySubtitle || 'Intelligence',
    iconPath: legacyIconPath || resolveAsset('assets/Hearthlink.png'), // Fallback to Hearthlink logo
    primaryColor: legacyTitleColor || '#d6b369',
    secondaryColor: legacySubtitleColor || legacyAccentColor || '#00e6ff',
    accentColor: legacyAccentColor
  };
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isRadialOpen, setIsRadialOpen] = useState(false);
  
  // Resolve theme
  const theme = useMemo(() => ({
    overlay: themeProp.overlay || { pattern: 'none', opacity: 0 },
    glassmorphism: themeProp.glassmorphism ?? false,
    borderGlow: themeProp.borderGlow ?? false,
  }), [themeProp]);

  // Use a ref to track previous layout for comparison
  const prevLayoutRef = React.useRef<string>(JSON.stringify(initialDashboardLayout));
  const [dashboardLayout, setDashboardLayout] = useState<DashboardTile[]>(initialDashboardLayout);

  // Sync external layout changes - only when actually different
  useEffect(() => {
    const currentLayoutStr = JSON.stringify(initialDashboardLayout);
    if (prevLayoutRef.current !== currentLayoutStr) {
      prevLayoutRef.current = currentLayoutStr;
      setDashboardLayout(initialDashboardLayout);
    }
  }, [initialDashboardLayout]);

  // Calculate dashboard canvas height dynamically
  const dashboardCanvasHeight = useMemo(() => {
    if (dashboardLayout.length === 0) return 720;
    const maxY = Math.max(...dashboardLayout.map(item => item.y + item.height));
    return Math.max(720, maxY + FRAME_PADDING * 2);
  }, [dashboardLayout]);

  // Handle tile updates
  const updateTile = useCallback((id: string, updates: Partial<DashboardTile>) => {
    const newLayout = dashboardLayout.map(item =>
      item.id === id ? { ...item, ...updates } : item
    );
    setDashboardLayout(newLayout);
    onDashboardLayoutChange?.(newLayout);
  }, [dashboardLayout, onDashboardLayoutChange]);

  const toggleTileLock = useCallback((id: string) => {
    const tile = dashboardLayout.find(t => t.id === id);
    if (tile) {
      updateTile(id, { locked: !tile.locked });
    }
  }, [dashboardLayout, updateTile]);

  // Navigation handler
  const handleNavigate = useCallback((moduleName: string) => {
    setIsRadialOpen(false);

    if (onNavigate) {
      const handled = onNavigate(moduleName);
      if (handled) return;
    }

    if (onNavigateToModule) {
      onNavigateToModule(moduleName);
    } else if (onBackToLauncher) {
      onBackToLauncher();
    }
  }, [onNavigate, onNavigateToModule, onBackToLauncher]);

  // Background overlay pattern generator
  const getOverlayPattern = useCallback(() => {
    const { overlay } = theme;
    if (!overlay || overlay.pattern === 'none') return {};

    const { primaryColor, secondaryColor } = identity;
    const opacity = overlay.opacity || 0.03;
    const opacityHex = Math.floor(opacity * 255).toString(16).padStart(2, '0');

    switch (overlay.pattern) {
      case 'grid':
        return {
          backgroundImage: `repeating-linear-gradient(0deg, transparent, transparent 35px, ${primaryColor}${opacityHex} 35px, ${primaryColor}${opacityHex} 36px),
                            repeating-linear-gradient(90deg, transparent, transparent 35px, ${primaryColor}${opacityHex} 35px, ${primaryColor}${opacityHex} 36px)`,
        };
      case 'hex':
        return {
          backgroundImage: `radial-gradient(circle at center, transparent 1px, ${secondaryColor}${opacityHex} 1px, ${secondaryColor}${opacityHex} 2px, transparent 2px)`,
          backgroundSize: '30px 30px',
        };
      case 'circuit':
        return {
          backgroundImage: `linear-gradient(90deg, ${primaryColor}${opacityHex} 1px, transparent 1px),
                            linear-gradient(0deg, ${primaryColor}${opacityHex} 1px, transparent 1px)`,
          backgroundSize: '20px 20px',
        };
      default:
        return {};
    }
  }, [theme, identity]);

  // Frame style with agent colors
  const frameStyle = useMemo<React.CSSProperties>(() => ({
    border: `2px solid ${identity.primaryColor}bf`,
    boxShadow: `0 0 22px ${identity.secondaryColor}2e, inset 0 0 18px rgba(0,0,0,0.55)`,
    background: 'linear-gradient(135deg, rgba(4,10,20,0.92), rgba(6,14,28,0.82))'
  }), [identity.primaryColor, identity.secondaryColor]);

  // Render view content for 'full' mode
  const renderViewContent = useCallback(() => {
    if (activeView === 'chat' && expandedChatContent) {
      return expandedChatContent;
    }

    switch (activeView) {
      case 'overview':
        if (renderOverview) return renderOverview();
        // Default overview with dashboard
        return (
          <div className="space-y-3 mt-1">
            <div className="relative w-full p-1 rounded-xl" style={frameStyle}>
              <div
                className={`absolute -top-2 left-1/2 -translate-x-1/2 w-32 h-1.5 rounded-full`}
                style={{
                  backgroundColor: identity.secondaryColor,
                  boxShadow: `0 0 12px ${identity.secondaryColor}b3`
                }}
              />
              <div
                className={`relative w-full rounded-lg border`}
                style={{
                  height: dashboardCanvasHeight,
                  minHeight: dashboardCanvasHeight,
                  overflow: 'visible',
                  borderColor: `${identity.secondaryColor}33`
                }}
              >
                {dashboardLayout.map(item => {
                  const snapGrid: [number, number] = [10, 10];
                  return (
                    <Rnd
                      key={item.id}
                      bounds="parent"
                      size={{ width: item.width, height: item.height }}
                      position={{ x: item.x, y: item.y }}
                      disableDragging={item.locked}
                      enableResizing={!item.locked}
                      dragGrid={snapGrid}
                      resizeGrid={snapGrid}
                      onDragStop={(_, d) => {
                        updateTile(item.id, { x: d.x, y: d.y });
                      }}
                      onResizeStop={(_, __, ref, ___, position) => {
                        updateTile(item.id, {
                          width: ref.offsetWidth,
                          height: ref.offsetHeight,
                          x: position.x,
                          y: position.y
                        });
                      }}
                      className="transition-shadow"
                    >
                      <div className="relative w-full h-full p-4 box-border overflow-hidden">
                        <div className="absolute inset-0 flex items-end justify-end pr-2 pb-2 z-30 pointer-events-auto">
                          <button
                            onClick={() => toggleTileLock(item.id)}
                            className="p-1.5 rounded-md bg-black/70 border border-white/20 text-[#9fb4d6] hover:border-opacity-50 transition-colors"
                            style={{
                              color: item.locked ? identity.primaryColor : undefined,
                              borderColor: item.locked ? identity.primaryColor : undefined
                            }}
                            title={item.locked ? 'Unlock tile' : 'Lock tile'}
                          >
                            {item.locked ? <Lock size={14} /> : <Unlock size={14} />}
                          </button>
                        </div>
                        <div className="w-full h-full">
                          {item.content}
                        </div>
                      </div>
                    </Rnd>
                  );
                })}
              </div>
            </div>
          </div>
        );
      case 'tasks':
        return renderTasksView ? renderTasksView() : null;
      case 'calendar':
        return renderCalendarView ? renderCalendarView() : null;
      case 'email':
        return renderEmailView ? renderEmailView() : null;
      default:
        return renderCustomView ? renderCustomView(activeView) : null;
    }
  }, [
    activeView,
    expandedChatContent,
    renderOverview,
    renderTasksView,
    renderCalendarView,
    renderEmailView,
    renderCustomView,
    frameStyle,
    identity,
    dashboardCanvasHeight,
    dashboardLayout,
    updateTile,
    toggleTileLock
  ]);

  // Wrap content in frame if requested
  const content = useMemo(() => {
    if (mode === 'full') {
      return renderViewContent();
    }

    if (withFrame) {
      return (
        <PremiumFrame>
          <div
            className="absolute -top-2 left-1/2 -translate-x-1/2 w-32 h-1.5 rounded-full"
            style={{
              backgroundColor: identity.secondaryColor,
              boxShadow: `0 0 12px ${identity.secondaryColor}70`
            }}
          />
          <div className="relative w-full h-full min-h-[720px] p-4">
            {children}
          </div>
        </PremiumFrame>
      );
    }

    return children;
  }, [mode, withFrame, children, renderViewContent, identity.secondaryColor]);

  return (
    <div
      className="fixed inset-0 bg-[#020617] text-[#c5d2e8] flex flex-col overflow-hidden font-sans z-50"
    >
      {/* Background overlay */}
      {theme.overlay && theme.overlay.pattern !== 'none' && (
        <div
          className="absolute inset-0 opacity-[0.02] pointer-events-none"
          style={getOverlayPattern()}
        />
      )}

      {/* Glassmorphism overlay */}
      {theme.glassmorphism && (
        <div className="absolute inset-0 bg-gradient-to-br from-[#0b1628]/10 via-transparent to-[#0b1628]/10 pointer-events-none" />
      )}

      {/* Header */}
      <CommandCenterHeader
        identity={identity}
        tabs={tabs}
        activeTab={activeTab}
        onTabChange={onTabChange}
        availableViews={availableViews}
        activeView={activeView}
        onViewChange={onViewChange}
        statusLabel={statusLabel}
        statusTone={statusTone}
        isConnected={isConnected}
        onBackToLauncher={onBackToLauncher}
        onRadialOpen={() => setIsRadialOpen(true)}
        headerStyle={headerStyle}
