/**
 * ActiveAgentPanel - Displays the currently active AI agent with avatar and metrics
 * Shows real-time status, system metrics, and cognitive load
 * Supports both 2D (AgentAvatar) and 3D (Avatar3D) avatar rendering
 */
import React, { memo, useMemo } from 'react';
import { Activity, Zap, Cpu, HardDrive, Wifi, Brain, Layers, Users, User } from '@/components/ui/HearthlinkIcons';
import { AgentAvatar } from './AgentAvatar';
import { Avatar3D } from '../../avatar/Avatar3D';
import { useAvatarStore } from '../../avatar/useAvatarStore';
import type { Persona, SystemMetrics, PersonaStatus } from './types';
import type { AvatarAnimationState } from '../../avatar/types';

interface ActiveAgentPanelProps {
  persona: Persona;
  metrics: SystemMetrics;
  onAvatarClick?: () => void;
  className?: string;
  onBridge?: () => void;
  onMute?: () => void;
  isMuted?: boolean;
  isBridgeActive?: boolean;
  hideHeader?: boolean;
}

interface VerticalMetricBarProps {
  label: string;
  value: number;
  color: string;
  icon?: React.ReactNode;
}

const VerticalMetricBar = memo<VerticalMetricBarProps>(({ label, value, color, icon }) => {
  const isCritical = value > 90;
  const isHigh = value > 80;
  const isElevated = value > 60;
  
  // Color Logic: Normal (Base) -> Elevated (Yellow) -> High (Orange) -> Critical (Red)
  const barColor = isCritical ? '#ef4444' : isHigh ? '#f97316' : isElevated ? '#eab308' : color;
  
  return (
    <div className="flex flex-col items-center gap-2 h-24 w-full">
      <div className="flex-1 w-2 bg-[#0b1628] rounded-full overflow-hidden border border-white/5 relative flex flex-col justify-end">
        <div
          className="w-full rounded-full transition-all duration-500"
          style={{
            height: `${Math.min(100, Math.max(0, value))}%`,
            backgroundColor: barColor,
            boxShadow: isHigh ? `0 0 10px ${barColor}` : `0 0 5px ${barColor}40`,
          }}
        />
      </div>
      <div className="flex flex-col items-center gap-0.5">
        {icon && <span className="opacity-60 text-[#8fa7c7]">{icon}</span>}
        <span className="text-[9px] text-[#8fa7c7] font-bold tracking-wider">{label}</span>
      </div>
    </div>
  );
});

VerticalMetricBar.displayName = 'VerticalMetricBar';

const StatusBadge = memo<{ status: PersonaStatus }>(({ status }) => {
  const config = useMemo(() => {
    switch (status) {
      case 'speaking':
        return { bg: 'bg-[#d6b369]/20', border: 'border-[#d6b369]', text: 'text-[#d6b369]' };
      case 'thinking':
        return { bg: 'bg-[#00e6ff]/20', border: 'border-[#00e6ff]', text: 'text-[#00e6ff]' };
      case 'idle':
        return { bg: 'bg-[#22c55e]/20', border: 'border-[#22c55e]', text: 'text-[#22c55e]' };
      case 'offline':
        return { bg: 'bg-[#64748b]/20', border: 'border-[#64748b]', text: 'text-[#64748b]' };
      default:
        return { bg: 'bg-[#22c55e]/20', border: 'border-[#22c55e]', text: 'text-[#22c55e]' };
    }
  }, [status]);

  return (
    <div
      className={`px-3 py-1 rounded-full text-[9px] uppercase tracking-widest font-bold border backdrop-blur-md shadow-lg ${config.bg} ${config.border} ${config.text}`}
    >
      {status}
    </div>
  );
});

StatusBadge.displayName = 'StatusBadge';

// View Mode Toggle Component
const ViewToggle = memo<{ 
  isBridgeActive?: boolean;
  onToggle?: () => void; 
}>(({ isBridgeActive, onToggle }) => (
  <div className="flex items-center justify-center w-full px-8">
    <div className="relative flex items-center bg-[#0b1628] rounded-full p-1 border border-white/10 w-full max-w-[200px]">
      {/* Sliding Background */}
      <div 
        className={`absolute inset-y-1 w-[calc(50%-4px)] bg-[#d6b369]/20 border border-[#d6b369]/50 rounded-full shadow-[0_0_10px_rgba(214,179,105,0.2)] transition-all duration-300 ease-out ${
          !isBridgeActive ? 'translate-x-[100%] left-0' : 'translate-x-0 left-1'
        }`}
      />
      
      {/* Bridge Option (Left) */}
      <button
        onClick={() => !isBridgeActive && onToggle?.()}
        className={`flex-1 relative z-10 py-1.5 flex items-center justify-center gap-2 text-[10px] uppercase tracking-wider font-bold transition-colors ${
          isBridgeActive ? 'text-[#e5f7ff]' : 'text-[#8fa7c7] hover:text-white'
        }`}
      >
        <Users size={6} />
      </button>

      {/* Agent Option (Right) */}
      <button
        onClick={() => isBridgeActive && onToggle?.()}
        className={`flex-1 relative z-10 py-1.5 flex items-center justify-center gap-2 text-[10px] uppercase tracking-wider font-bold transition-colors ${
          !isBridgeActive ? 'text-[#d6b369]' : 'text-[#8fa7c7] hover:text-white'
        }`}
      >
        <User size={6} />
      </button>
    </div>
  </div>
));

ViewToggle.displayName = 'ViewToggle';

// Semi-Circle Gauge Component (SVG)
const SemiCircleGauge = memo<{ 
  value: number; 
  label: string; 
  color?: string;
  subLabel?: string;
}>(({ value, label, color = '#d6b369', subLabel }) => {
  const percentage = Math.min(100, Math.max(0, value));
  const radius = 36;
  const strokeWidth = 6;
  const normalizedRadius = radius - strokeWidth / 2;
  const circumference = normalizedRadius * 2 * Math.PI;
  // We only want 180 degrees (half circle)
  const arcLength = circumference / 2;
  const strokeDashoffset = arcLength - (percentage / 100) * arcLength;

  // Determine color based on threshold if not provided or generic
  const getThresholdColor = (val: number) => {
    // Override base color for high usage
    if (val >= 90) return '#ef4444'; // Critical (Red)
    if (val >= 80) return '#f97316'; // High (Orange)
    if (val >= 60) return '#eab308'; // Elevated (Yellow)
    
    // Normal state - use provided color or default
    return color !== '#d6b369' ? color : 'var(--chat-accent-color)'; // Default to Theme Color instead of Cyan/Gold hardcoded if passed #d6b369
  };

  const finalColor = getThresholdColor(percentage);

  return (
    <div className="flex flex-col items-center justify-center relative p-2 bg-[#0a0f1d]/40 rounded-xl border border-white/5">
      <div className="relative w-24 h-14 overflow-hidden flex justify-center items-end pb-0">
        <svg
          height="100%"
          width="100%"
          viewBox="0 0 84 44"
          className="overflow-visible"
        >
          {/* Background Arc */}
          <path
            d="M 6 40 A 36 36 0 0 1 78 40"
            fill="none"
            stroke="#1e293b"
            strokeWidth={strokeWidth}
            strokeLinecap="round"
          />
          {/* Value Arc */}
          <path
            d="M 6 40 A 36 36 0 0 1 78 40"
            fill="none"
            stroke={finalColor}
            strokeWidth={strokeWidth}
            strokeLinecap="round"
            strokeDasharray={`${arcLength} ${circumference}`}
            strokeDashoffset={strokeDashoffset}
            className="transition-all duration-700 ease-out"
            style={{ 
              filter: `drop-shadow(0 0 4px ${finalColor}60)`
            }}
          />
        </svg>
        {/* Value Text centered at bottom of arc */}
        <div className="absolute bottom-0 flex flex-col items-center">
           <span className="text-sm font-bold font-orbitron-force" style={{ color: finalColor }}>
            {percentage.toFixed(0)}%
          </span>
        </div>
      </div>
      
      {/* Label */}
      <div className="flex flex-col items-center mt-1">
        <span className="text-[9px] text-[#8fa7c7] uppercase tracking-widest font-bold text-center">
          {label}
        </span>
        {subLabel && (
          <span className="text-[8px] text-[#8fa7c7]/50 uppercase tracking-wider text-center">
            {subLabel}
          </span>
        )}
      </div>
    </div>
  );
});

SemiCircleGauge.displayName = 'SemiCircleGauge';

