  // Map persona status to avatar animation state
  const animationState = useMemo((): AvatarAnimationState => {
    switch (persona.status) {
      case 'speaking': return 'speaking';
      case 'thinking': return 'thinking';
      case 'idle': return 'idle';
      default: return 'idle';
    }
  }, [persona.status]);

  return (
    <div className={`flex flex-col h-full bg-[#06101f]/95 backdrop-blur-xl ${className}`}>
      {/* Header - Fixed h-14 to match neighbors */}
      {!hideHeader && (
        <div
          className="min-h-[56px] h-14 max-h-[56px] px-4 border-b flex items-center justify-between bg-[#06101f]/80 shrink-0 overflow-hidden mt-0"
          style={{ 
