            <h2 className="font-orbitron-force uppercase tracking-widest text-xs font-bold" style={{ color: 'var(--chat-accent-color)' }}>
              Active Agent
            </h2>
          </div>
          {/* Status Badge moved to header for compactness */}
           <StatusBadge status={persona.status} />
        </div>
      )}

      {/* Main Content */}
      <div className="flex-1 p-4 flex flex-col justify-between overflow-y-auto">
        {/* Top Section: Avatar & Controls */}
        <div className="flex flex-col items-center gap-4 w-full h-full min-h-[300px]">
           <div className="flex items-center justify-center w-full h-full gap-3">
              {/* Left Control: Dynamic/Bridge Mode */}
              <button
                onClick={onBridge}
                disabled={isBridgeActive}
                className={`flex flex-col items-center justify-center gap-1.5 p-2 rounded-lg border transition-all duration-300 w-1/4 flex-shrink-0 ${
                   isBridgeActive
                   ? 'bg-[rgba(var(--chat-accent-rgb),0.2)] border-[rgba(var(--chat-accent-rgb),0.6)] text-[rgb(var(--chat-accent-rgb))] shadow-[0_0_15px_rgba(var(--chat-accent-rgb),0.3)]'
                   : 'bg-[#0b1628]/50 border-white/10 text-[#8fa7c7] hover:bg-[#0b1628] hover:text-white hover:border-white/30'
                }`}
                title="Activate Agent Bridge (Dynamic Mode)"
                aria-label="Activate Agent Bridge (Dynamic Mode)"
              >
                 <Users size={14} />
              </button>

              {/* Center: Portrait Avatar */}
              <div 
                className="h-full relative rounded-lg border-2 bg-[#0a0f1d]/50 flex items-center justify-center overflow-hidden shadow-[0_0_30px_rgba(0,0,0,0.5)] group flex-1 min-w-[220px]"
                style={{ borderColor: 'rgba(var(--chat-accent-rgb), 0.3)' }}
              >
                {/* Decorative corners */}
                <div className="absolute top-0 left-0 w-3 h-3 border-t-2 border-l-2 rounded-tl" style={{ borderColor: 'rgba(var(--chat-accent-rgb), 0.5)' }} />
                <div className="absolute top-0 right-0 w-3 h-3 border-t-2 border-r-2 rounded-tr" style={{ borderColor: 'rgba(var(--chat-accent-rgb), 0.5)' }} />
                <div className="absolute bottom-0 left-0 w-3 h-3 border-b-2 border-l-2 rounded-bl" style={{ borderColor: 'rgba(var(--chat-accent-rgb), 0.5)' }} />
                <div className="absolute bottom-0 right-0 w-3 h-3 border-b-2 border-r-2 rounded-br" style={{ borderColor: 'rgba(var(--chat-accent-rgb), 0.5)' }} />
                
                {/* Avatar Content */}
                <div className="relative z-10 w-full h-full flex items-center justify-center">
                  {avatarConfig?.avatarUrl && isDynamic ? (
                    <Avatar3D
