/**
 * Login Screen Component
 *
 * Provides authentication UI for Hearthlink.
 * Connects to the SentryBridge auth system.
 *
 * @version 1.0.0
 * @date 2026-01-01
 */

import React, { useState, useCallback } from 'react';
import { motion } from 'framer-motion';
import { useAuth } from '../../contexts/AuthContext';
import { Eye, EyeOff, Lock, User, Shield } from '../ui/HearthlinkIcons';

interface LoginScreenProps {
  onLoginSuccess?: () => void;
  onSkipToDemo?: () => void;
}

export const LoginScreen: React.FC<LoginScreenProps> = ({
  onLoginSuccess,
  onSkipToDemo,
}) => {
  const { login, autoLogin, isLoading } = useAuth();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setIsSubmitting(true);

    try {
      const success = await login({ username, password });
      if (success) {
        onLoginSuccess?.();
      } else {
        setError('Invalid credentials. Please check your user ID and password.');
      }
    } catch (err) {
      setError('Authentication failed. Please try again.');
      console.error('[LoginScreen] Login error:', err);
    } finally {
      setIsSubmitting(false);
    }
  }, [username, password, login, onLoginSuccess]);

  const handleAutoLogin = useCallback(async () => {
    setError(null);
    setIsSubmitting(true);

    try {
      const success = await autoLogin();
      if (success) {
        onLoginSuccess?.();
      } else {
        setError('Auto-login not available. Please enter credentials.');
      }
    } catch (err) {
      setError('Auto-login failed. Please enter credentials manually.');
      console.error('[LoginScreen] Auto-login error:', err);
    } finally {
      setIsSubmitting(false);
    }
  }, [autoLogin, onLoginSuccess]);

  const handleDemoMode = useCallback(() => {
    onSkipToDemo?.();
  }, [onSkipToDemo]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-[#030810] flex items-center justify-center">
        <motion.div
